INSERT INTO `questions` (`ID`, `category`, `type`, `image`, `question`, `feedback`, `option1`, `option2`, `option3`) VALUES
	(1, 'J', 4, 'gvr_001a.jpg', 'Je rijdt 40 km/u. Wat doe je?', 'Het bord waarschuwt voor een oversteekplaats voor fietsers en bromfietsers. De bocht die je gaat nemen is redelijk overzichtelijk: je ziet het tegemoetkomende verkeer. De maximumsnelheid is 30 km/u terwijl je 40 km/u rijdt, daarom laat je het gas los.\r\n', '1', '', ''),
	(2, 'J', 4, 'gvr_002a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'De auto is de bocht aan het nemen en zal waarschijnlijk op de tegenliggende weghelft terechtkomen. Je laat het gas los; zo kan je rustig blijven doorrijden, maar voorbereid zijn op een volgende handeling. Je nadert namelijk ook een voetgangersoversteekplaats. ', '1', '', ''),
	(3, 'J', 4, 'gvr_003a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Er is geen direct gevaar, je kan de situatie overzien en deze is veilig.', '2', '', ''),
	(4, 'J', 4, 'gvr_004a.jpg', 'Je rijdt 30 km/u. Wat doe je?', '30 km/u is een veilige snelheid op deze weg. Er dreigt geen direct gevaar.', '2', '', ''),
	(5, 'J', 4, 'gvr_004b.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Jouw voorganger remt. Het is een onoverzichtelijk punt: de bomen blokkeren jouw zicht. Rem af om te voorkomen dat je te dicht op de auto voor je raakt. ', '0', '', ''),
	(6, 'J', 4, 'gvr_005a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt een duidelijk overzicht en jouw volgafstand is ruim voldoende.', '2', '', ''),
	(7, 'J', 4, 'gvr_007a.jpg', 'Je rijdt 120 km/u. Wat doe je?\r\n', 'Je rijdt op de autosnelweg met genoeg afstand tot je voorganger. Het is niet druk op de weg. \r\n', '2', '', ''),
	(8, 'J', 4, 'gvr_008a.jpg', 'Je rijdt 90 km/u. Wat doe je?', 'De vrachtwagen voor je mag maar 80 km/u rijden, waardoor er dus een snelheidsverschil is. Tevens eindigt de linkerrijstrook en blokkeert de vrachtwagen jouw zicht. Zo kun je niet zien wat er voor de vrachtwagen gebeurd. Pas op voor eventuele invoegende/ inhalende auto’s. \r\n', '1', '', ''),
	(9, 'J', 4, 'gvr_009a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je rijdt op het tramspoor en er nadert een tegenligger. \n', '1', '', ''),
	(10, 'J', 4, 'gvr_009b.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'De fietser voor je rijdt veel langzamer dan jij en er is geen mogelijkheid om uit te wijken. Verminder daarom jouw snelheid.', '0', '', ''),
	(11, 'J', 4, 'gvr_010a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je nadert een zebrapad en verschillende verkeersdeelnemers. Voorzichtigheid is geboden, laat daarom het gas los. ', '1', '', ''),
	(12, 'J', 4, 'gvr_011a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je nadert een fietser, pas op, omdat fietsers kwetsbaar zijn. Passeer deze met een lage snelheid. Verder heb je goed zicht op de weg, dus remmen is niet nodig.\n', '1', '', ''),
	(13, 'J', 4, 'gvr_011b.jpg', 'Je rijdt 30 km/u. Wat doe je?\r\n', 'Je rijdt erg dicht op de auto voor je, die een fietser gaat inhalen. Het tramspoor gaat ook grenzen aan jouw weghelft. Wees alert zodat je eventueel kunt remmen.\r\n', '1', '', ''),
	(14, 'J', 4, 'gvr_011c.jpg', 'Je rijdt 30 km/u. Wat doe je?\r\n', 'De auto voor je remt en er nadert een fietser. Je hebt geen overzicht op de weg voor je. Rem daarom af, om te voorkomen dat je op jouw voorganger botst of de fietser raakt. \r\n', '0', '', ''),
	(15, 'J', 4, 'gvr_012a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van ‘50 km/u’ aan: pas jouw snelheid aan, je rijdt te hard! Ook mag er niet meer op de linkerrijstrook gereden worden, pas dus op voor bestuurders die op jouw rijstrook willen gaan invoegen. ', '0', '', ''),
	(16, 'J', 4, 'gvr_012b.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van ‘50 km/u’ aan. Blijf daarom op deze snelheid rijden. Let wel op de linkerrijstrook die gesloten is en de auto rechts van je die remt. \r\n', '2', '', ''),
	(19, 'J', 4, 'gvr_012c.jpg', 'Je rijdt 130 km/u. Wat doe je?', 'Het is erg druk op de weg en rechts van je remt een auto. Daarom is het beter het gas los te laten, zodat je sneller kan handelen wanneer er een gevaarlijke situatie ontstaat.  ', '1', '', ''),
	(20, 'J', 4, 'gvr_013a.jpg', 'Je rijdt 30 km/u. Wat doe je?', ' Je rijdt op een gelijkwaardige kruising. Kijk goed om u heen en steek vlot over.', '2', '', ''),
	(22, 'J', 4, 'gvr_014a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je hebt goed zicht op de weg, waar 80 km/u gereden mag worden. \r\n', '2', '', ''),
	(23, 'J', 4, 'gvr_014b.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je rijdt erg dicht op jouw voorganger, laat daarom het gas los voor een veilige volgafstand. \r\n', '1', '', ''),
	(24, 'J', 4, 'gvr_014c.jpg', 'Je rijdt 80 km/u. Wat doe je?\r\n', 'De motorfiets voor je remt, rem daarom ook. Blijf erachter en bescherm de motorrijder door ook te gaan remmen.', '0', '', ''),
	(25, 'J', 4, 'gvr_015a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Er is iets gebeurd op de andere weghelft, want er staat een file. Het kruispunt voor je is geblokkeerd, let daarom goed op en wees zo nodig voorbereid om te remmen. \r\n', '1', '', ''),
	(26, 'J', 4, 'gvr_016a.jpg', 'Je rijdt 20 km/u. Wat doe je?', 'Er zijn voetgangers aan het oversteken op het zebrapad. Door de bus naast je kun je niet zien of er plots nog meer voetgangers willen oversteken. \r\n', '0', '', ''),
	(27, 'J', 4, 'gvr_017a.jpg', 'Je rijdt 40 km/u. Wat doe je?', 'Het verkeerslicht is groen, maar op het kruispunt bevinden zich auto’s en er staat een fietser op jouw weghelft. Rem daarom snel af voordat je deze aanrijdt.  \r\n', '0', '', ''),
	(28, 'J', 4, 'gvr_018a.jpg', 'Je rijdt 40 km/u. Wat doe je?', 'Je nadert een onoverzichtelijke bocht; 40 km/u is te hard om deze bocht te nemen. Je hebt ook weinig ruimte door de tegenliggende bestuurder die de weg oprijdt. \r\n', '0', '', ''),
	(29, 'J', 4, 'gvr_019a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Jouw voorganger haalt een geparkeerde auto in op een smalle, onoverzichtelijke weg. Er staat ook iemand te wachten aan de rechterkant, wees daarom extra voorzichtig. Je rijdt al 25 km/u dus gas loslaten is voldoende. \n', '1', '', ''),
	(30, 'J', 4, 'gvr_020a.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'Vanwege de auto voor je en de bomen langs de weg is jouw zicht beperkt. Wanneer je 45 km/u rijdt is dit te snel om de wegversmalling te passeren. \r\n', '0', '', ''),
	(31, 'J', 4, 'gvr_021a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'De auto die jou inhaalt, rijdt harder dan jij. Er is daardoor voldoende afstand. Je hebt tevens goed zicht op de weg.\n', '2', '', ''),
	(32, 'J', 4, 'gvr_022a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je rijdt op een voorrangsweg buiten de bebouwde kom. Je kan daarom 80 km/u blijven rijden; je kunt ver vooruit kijken. \r\n', '2', '', ''),
	(34, 'J', 4, 'gvr_022c.jpg', 'Je rijdt 50 km/u. Wat doe je?\r\n', 'De tegemoetkomende auto wilt afslaan, pas op dat je niet te hard rakelings voorbij deze auto rijdt. Er bevindt zich maar een kleine afstand tussen jullie. \r\n', '1', '', ''),
	(35, 'J', 4, 'gvr_023a.jpg', 'Je rijdt 130 km/u. Wat doe je?', 'Op de vluchtstrook staan een aantal voertuigen: er is iets gebeurd. Er is genoeg ruimte om uit te wijken wanneer nodig, maar wees voorbereid. Je rijdt op de middelste rijstrook. Gas loslaten kan betekenen dat je hier rechts ingehaald wordt. Kijk in de binnenspiegel. \n', '1', '', ''),
	(36, 'J', 4, 'gvr_024a.jpg', 'Je rijdt 40 km/u. Wat doe je?', 'Je nadert een gelijkwaardig kruispunt waar je voorrang moet verlenen aan bestuurders van rechts, dus rem voor de auto van rechts. ', '0', '', ''),
	(37, 'J', 4, 'gvr_025a.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'Het verkeerslicht is groen, maar er rijdt nog iemand op het kruispunt. Je wilt afslaan: verminder jouw snelheid voordat je de bocht neemt. Let ook op de bestuurder naast je. \r\n', '1', '', ''),
	(38, 'J', 4, 'gvr_025b.jpg', 'Je rijdt 55 km/u. Wat doe je?', 'Het verkeerslicht is groen, maar er rijdt nog iemand op het kruispunt. Je rijdt met een te hoge snelheid om af te slaan: rem voordat je de bocht neemt. Let ook op de bestuurder naast je. ', '0', '', ''),
	(40, 'J', 4, 'gvr_027a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Het verkeerslicht is geel. Je kunt nog veilig tot stilstand komen voor het verkeerslicht. Remmen.', '0', '', ''),
	(41, 'J', 4, 'gvr_028a.jpg', 'Je rijdt 25 km/u. Wat doe je?\r\n', 'Je bevindt je voor een druk kruispunt met tegenliggers. De bestuurder van rechts heeft voorrang, geef deze dat ook!\r\n', '0', '', ''),
	(42, 'J', 4, 'gvr_029a.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'Het verkeerslicht staat op rood, dit betekent stoppen. ', '0', '', ''),
	(43, 'J', 4, 'gvr_029b.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'Het verkeerslicht staat op geel. Met deze snelheid kun je nog op tijd remmen. Er rijden verschillende bestuurders voor je, waaronder een ambulance, dus wees alert. Probeer te voorkomen om midden op het kruispunt stil te komen staan.', '0', '', ''),
	(44, 'J', 4, 'gvr_031a.jpg', 'Je rijdt 20 km/u. Wat doe je?', 'De auto voor je remt; om een aanrijding te voorkomen rem je ook. Er steekt tevens een voetganger over, laat deze veilig oversteken. Afremmen moet je hierna ook: je nadert een rotonde met zebrapad. \r\n', '0', '', ''),
	(45, 'J', 4, 'gvr_032a.jpg', 'Je rijdt 20 km/u. Wat doe je?', 'Een voetganger steekt de weg over, laat deze veilig oversteken. Er nadert ook een rotonde, waar je voorrang moet verlenen.  \r\n', '1', '', ''),
	(46, 'J', 4, 'gvr_034a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Er staan geparkeerde auto’s. Dit maakt de situatie onoverzichtelijk. Pas op voor passagiers die plots willen uitstappen of wegrijden van de parkeerplaats.\r\n', '1', '', ''),
	(48, 'J', 4, 'gvr_036a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Een kat lijkt te willen oversteken.  ', '0', '', ''),
	(49, 'J', 4, 'gvr_037a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Remmen onder deze weersomstandigheden keen zeer gevaarlijk zijn. Dus het is belangrijk om het gas zo snel mogelijk los te laten. Houd de binnenspeigel goed in de gaten!', '1', '', ''),
	(51, 'J', 4, 'gvr_038b.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'De volgafstand is te klein: vergroot jouw volgafstand door kort het gas los te laten. \r\n', '1', '', ''),
	(52, 'J', 4, 'gvr_038c.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Met 80 km/u rijd je op jouw voorganger af, die remt. Rem daarom flink af om niet te dichtbij te komen en voldoende afstand te creëren. ', '0', '', ''),
	(53, 'J', 4, 'gvr_038d.jpg', 'Je rijdt 80 km/u. Wat doe je?\r\n', 'De auto voor je heeft zijn knipperlicht aan. Wees voorbereid dat de auto flink vaart zal minderen, omdat deze wil afslaan. Rem af en houd de binnenspiegel goed in de gaten. \n', '0', '', ''),
	(54, 'J', 4, 'gvr_039a.jpg', 'Je rijdt 80 km/u. Wat doe je?', ' Je hebt een duidelijk overzicht op deze weg, waar je 80 km/u mag rijden. Er is geen verkeer te bekennen. Blijf daarom op deze snelheid doorrijden. \r\n', '2', '', ''),
	(56, 'J', 4, 'gvr_041a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Het verkeerslicht springt op oranje en het kruispunt is niet vrij. Door te remmen voorkom je dat je midden op het drukke kruispunt moet wachten. \r\n', '0', '', ''),
	(57, 'J', 4, 'gvr_042a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Er staat een verkeersregelaar op de weg voor je en er zijn wegwerkzaamheden. Volg deze instructies op en rem. ', '0', '', ''),
	(58, 'J', 4, 'gvr_043a.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'De auto van links is aan het afslaan en zal jou kruisen. Rem daarom om een ongeval te voorkomen. Er is tevens geen ruimte op de weg voor jou. \r\n', '0', '', ''),
	(59, 'J', 4, 'gvr_044a.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'De verkeerslichten werken niet. Het kruispunt voor je staat vol. Wacht tot er ruimte is om veilig te passeren.\r\n', '0', '', ''),
	(60, 'J', 4, 'gvr_045b.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'Het verkeerslicht springt op geel en op het kruispunt bevinden zich nog bestuurders. Rem om een gevaarlijke situatie te voorkomen en wacht totdat er ruimte is om veilig te passeren. \r\n', '0', '', ''),
	(61, 'J', 4, 'gvr_047a.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'Een gevaarlijke situatie ontstaat voor je. De weg is geblokkeerd en er komen tegenliggers. Je moet remmen, want er is geen ruimte voor jou om uit te wijken.\n', '0', '', ''),
	(63, 'J', 4, 'gvr_049a.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'Er is niet meer genoeg ruimte om veilig tot stilstand te komen voor het gele verkeerslicht. Zorg er voor dat je het kruispunt niet blokkeert. Je doet hier niets en rijdt door.', '2', '', ''),
	(64, 'J', 4, 'gvr_049c.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'Het verkeerslicht is rood, dus je moet flink remmen. ', '0', '', ''),
	(65, 'J', 4, 'gvr_049d.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'Het verkeerslicht staat op groen. Steek het kruispunt over en let op het busje voor je dat net de bocht neemt. ', '2', '', ''),
	(66, 'J', 4, 'gvr_050a.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'De toegestane snelheid is 50 km/u. Let erop dat je een veilige volgafstand blijft houden.\r\n', '2', '', ''),
	(67, 'J', 4, 'gvr_050b.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'De auto voor je remt. Om deze niet aan te rijden, rem jij ook. ', '0', '', ''),
	(68, 'J', 4, 'gvr_050c.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'De auto voor je is van plan af te slaan en zal dus moeten remmen. Jij hebt geen ruimte om uit te wijken. Rem daarom af. \r\n', '0', '', ''),
	(69, 'J', 4, 'gvr_051a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je rijdt op de juiste snelheid, je hebt een goed zicht op de weg en houdt voldoende volgafstand. ', '2', '', ''),
	(70, 'J', 4, 'gvr_051b.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je mag niet meer op deze rijstrook rijden: deze is gesloten. Let op het snelheidsverschil tussen jou en de andere bestuurders. Laat je gas los en probeer zo snel mogelijk op te schuiven naar de linkerrijstrook. \r\n', '1', '', ''),
	(71, 'J', 4, 'gvr_053a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Er is een nauwe doorgang, je kunt niet veilig passeren.  De vrachtwagen voor je staat stil en er zijn voetgangers en fietsers. Rem, omdat je niet kunt zien wat er achter de vrachtwagen gebeurd.', '0', '', ''),
	(72, 'J', 4, 'gvr_054a.jpg', 'Je rijdt 20 km/u. Wat doe je?', 'Je nadert een wegversmalling in een straat met druk verkeer. Deze is vrij. Je rijdt al stapvoets dus kan zo doorrijden.', '2', '', ''),
	(74, 'J', 4, 'gvr_056a.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'Het verkeerslicht staat op groen, dat betekent doorrijden.\n', '2', '', ''),
	(75, 'J', 4, 'gvr_057a.jpg', 'Je rijdt 30 km/u. Wat doe je?\r\n', 'De auto’s voor je remmen en er bevindt zich een rood kruis boven deze rijstrook. Wees voorbereid op een opstopping en om te wisselen naar een andere rijstrook.\r\n', '0', '', ''),
	(77, 'J', 4, 'gvr_059a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'De auto voor je remt op een wegdek van klinkers. Deze kunnen glad zijn. Houdt daarom een veilige afstand.  ', '0', '', ''),
	(78, 'J', 4, 'gvr_060a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Je nadert een gevaarlijk kruispunt met een stopbord. Je moet dus remmen. Ook bevindt er zich verkeer op het kruispunt.  \r\n', '0', '', ''),
	(80, 'J', 4, 'gvr_062a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Jou voorligger remt niet, dus er is geen directe handeling. Doe niets en rij door.', '2', '', ''),
	(81, 'J', 4, 'gvr_063a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je rijdt dicht op jouw voorganger en er nadert een onoverzichtelijke bocht. Let ook op bestuurders die het tankstation willen verlaten, willen invoegen op de rijstrook waar jij rijdt.\r\n', '1', '', ''),
	(82, 'J', 4, 'gvr_066a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je rijdt op een 80km/u weg, maar de auto voor je wilt afslaan naar links. Deze blokkeert de weg. Rem om een aanrijding te voorkomen. Er naderen ook tegenliggers, dus je hebt weinig ruimte om uit te wijken. \r\n', '0', '', ''),
	(84, 'J', 4, 'gvr_069a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Er is voldoende overzicht en volgafstand, dus je hoeft niet te handelen. \r\n', '2', '', ''),
	(85, 'J', 4, 'gvr_070a.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'Je nadert een voorrangskruising, maar er rijdt een landbouwvoertuig voor je die niet sneller kan rijden dan 25 km/u. Rem flink af om een veilige volgafstand te behouden.', '0', '', ''),
	(86, 'J', 4, 'gvr_071a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De auto voor je wil uitvoegen, wees daarom voorbereid dat deze langzamer kan gaan rijden. Ook rijdt er nog een vrachtwagen voor die niet sneller dan 80 km/u rijdt. Wanneer het mogelijk is, dan kun je het beste van rijstrook wisselen.  \n', '1', '', ''),
	(87, 'J', 4, 'gvr_072a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt een duidelijk zicht op de weg, je houdt je aan de maximumsnelheid en aan voldoende volgafstand.', '2', '', ''),
	(88, 'J', 4, 'gvr_073a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Je nadert een rotonde waarbij de fietser voorrang heeft. Laat deze daarom veilig verder fietsen.', '0', '', ''),
	(90, 'J', 4, 'gvr_075a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Er naderen fietsers op jouw weghelft en er komen tegenliggers aan. Wees daarom voorzichtig en voorbereid om te remmen. ', '1', '', ''),
	(92, 'J', 4, 'gvr_077a.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'Er is geen plaats voor jou om veilig door te rijden, vanwege de bus die de bocht neemt. Verder wil je voorkomen om stil te staan op de oversteekplaats voor fietsers.', '0', '', ''),
	(93, 'J', 4, 'gvr_078a.jpg', 'Je rijdt 130 km/u. Wat doe je?', 'Laat op deze snelheid je gas even los om voldoende afstand tot jouw voorganger te behouden. Je hebt geen ruimte om uit te wijken naar de linkerrijstrook. ', '1', '', ''),
	(94, 'J', 4, 'gvr_079a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Wees alert om te kunnen handelen wanneer de voetganger plots besluit over te steken. Je hebt geen duidelijk zicht op de weg voor je. ', '1', '', ''),
	(95, 'J', 4, 'gvr_080a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je nadert een scherpe bocht met een adviessnelheid van 70 km/u. Een snelheid van 120km/u is te hard om deze bocht veilig te kunnen nemen. Rem af voordat je deze bocht ingaat.', '0', '', ''),
	(96, 'J', 4, 'gvr_080b.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Je nadert een scherpe bocht met een adviessnelheid van 70 km/u. Om veilig de bocht te kunnen nemen moet je dus gas loslaten.\n', '1', '', ''),
	(97, 'J', 4, 'gvr_081a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Rem om op tijd tot stilstand te komen voor het verkeerslicht. ', '0', '', ''),
	(98, 'J', 4, 'gvr_082a.jpg', 'Je rijdt 130 km/u. Wat doe je?', 'Je hebt duidelijk zicht op de weg. Er dreigt geen gevaar.\r\n', '2', '', ''),
	(99, 'J', 4, 'gvr_083a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'De voetgangers op het zebrapad moet je voor laten gaan: deze kunnen plots oversteken om de tram te willen halen. ', '0', '', ''),
	(100, 'J', 4, 'gvr_084a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je hebt een duidelijk overzicht op de weg en er rijdt niemand achter, rem en vogels zullen waarschijnlijk wegvliegen zodra de auto nadert. Gas loslaten geeft de vogels misschien te weinig tijd om weg te vliegen. ', '0', '', ''),
	(101, 'J', 4, 'gvr_085a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je rijdt op een lage snelheid en kunt tegenliggers zien naderen. Let wel op: geef je ogen de tijd om aan het verschil van licht naar donker te wennen.', '2', '', ''),
	(102, 'J', 4, 'gvr_086a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je houdt meer dan voldoende volgafstand en er dreigt geen gevaar. Daarom kun je blijven doorrijden op deze snelheid. ', '2', '', ''),
	(106, 'J', 4, 'gvr_090a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Op deze weg mag je 80 km/u en er is geen nabij verkeer. Je kan daarom 80 km/u blijven rijden. Na het bord ‘einde 80’ mag je 100 km/u gaan rijden.   ', '2', '', ''),
	(107, 'J', 4, 'gvr_090b.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Laat nog even het gas los, want je mag hier maar 80 km/u rijden. Na het bord ‘Einde 80’ mag je 100 km/u gaan rijden. ', '1', '', ''),
	(108, 'J', 4, 'gvr_091a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je hebt duidelijk zicht op de weg en je rijdt niet te hard. Je wordt wel gewaarschuwd voor laagvliegende vliegtuigen. ', '2', '', ''),
	(109, 'J', 4, 'gvr_092a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Wanneer de bestuurder voor je te langzaam zou rijden dan heb je eventueel ruimte om in te halen via de linkerrijstrook. ', '2', '', ''),
	(110, 'J', 4, 'gvr_092b.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'De auto voor je wilt gaan inhalen. Let op dat bestuurders op deze rijstrook langzamer kunnen rijden. Nu is er nog voldoende afstand tussen jou en de bestuurder voor je. ', '2', '', ''),
	(113, 'J', 4, 'gvr_095a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je rijdt met een matige snelheid en houdt voldoende afstand. ', '2', '', ''),
	(114, 'J', 4, 'gvr_095b.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je hebt geen volledig zicht op de weg en de volgafstand is onvoldoende op deze snelheid. Vergroot jouw volgafstand door het gas los te laten. ', '1', '', ''),
	(116, 'J', 4, 'gvr_097a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Rem af zodat je de bocht met een veilige snelheid kunt nemen.', '0', '', ''),
	(117, 'J', 4, 'gvr_098a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je hebt duidelijk zicht op de weg voor je en rijdt op een toegestane snelheid.', '2', '', ''),
	(118, 'J', 4, 'gvr_099a.jpg', 'Je rijdt 50 km/u. Wat doe je?', ' Het is een drukke situatie: let op de bus aan de overkant die eventueel wil gaan invoegen. Zorg dat er genoeg ruimte is. Let ook op de geparkeerde auto’s en het fietspad naast je. ', '1', '', ''),
	(119, 'J', 4, 'gvr_100a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je rijdt op de linkerrijstrook en bent aan het inhalen, blijf daarom doorrijden op deze snelheid. Houdt wel rekening met het snelheidsverschil tussen jou en de vrachtwagen.', '2', '', ''),
	(121, 'J', 4, 'gvr_102a.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'Je nadert een druk kruispunt. Verminder jouw snelheid om het kruispunt veilig over te steken, ook al is het verkeerslicht groen. ', '1', '', ''),
	(122, 'J', 4, 'gvr_103a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'De weg voor je is vrij. Je mag hier 80 km/u rijden. ', '2', '', ''),
	(123, 'J', 4, 'gvr_104a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je mag hier 80 km/u rijden. Er dreigt geen gevaar en de volgafstand is voldoende. Blijf daarom doorrijden op deze snelheid.', '2', '', ''),
	(125, 'J', 4, 'gvr_105a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je wordt ingehaald. Laat de auto zijn inhaalmanoeuvre afmaken en laat het gas los, zodat hij tijdig kan invoegen op jouw rijstrook, want er komen tegenliggers aan. ', '1', '', ''),
	(126, 'J', 4, 'gvr_106a.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'Je rijdt te hard binnen de bebouwde kom. Door het zonlicht tussen de bomen door kun je tevens weggebruikers op de kruising pas op het laatste moment zien. ', '1', '', ''),
	(127, 'J', 4, 'gvr_107a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Je nadert een wegversperring, een onoverzichtelijke, scherpe bocht en je wordt gewaarschuwd voor fietsers. Neem daarom deze bocht met zeer lage snelheid. \r\n', '0', '', ''),
	(128, 'J', 4, 'gvr_108a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Jouw doorgang wordt geblokkeerd: geef de bus voldoende ruimte om de bocht te kunnen nemen. Laat de bus daarom voor en wacht voor de bocht totdat de bus voorbij is.', '0', '', ''),
	(129, 'J', 4, 'gvr_109a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'De fietser rijdt de weg op zonder te kijken. Rem om een aanrijding te voorkomen. Daarnaast nader je een onoverzichtelijke bocht en zijn er wegwerkzaamheden. Je kunt niet zien of er tegenliggers naderen. ', '0', '', ''),
	(130, 'J', 4, 'gvr_110a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'De weg voor je is vrij. Je kunt daarom blijven doorrijden op dezelfde snelheid.  ', '2', '', ''),
	(131, 'J', 4, 'gvr_111a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Er is een drukke verkeerssituatie ontstaan: nader deze met lage snelheid. Er rijden fietsers voor je, er bevindt zich weinig ruimte tussen jou en de tegenliggers, en er is geen ruimte om veilig uit te wijken. ', '0', '', ''),
	(132, 'J', 4, 'gvr_112a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je rijdt op een vluchtheuvel en nadert een onoverzichtelijke bocht met tegenliggers. Je hebt voorrang op bestuurders van rechts. Verminder jouw snelheid om de bocht veilig te nemen.\n', '1', '', ''),
	(133, 'J', 4, 'gvr_113a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je nadert een rotonde waar je voorrang moet verlenen aan bestuurders, en voetgangers op het zebrapad. Er rijdt een vrachtwagen naast je die jouw zicht blokkeert: let op wanneer deze de bocht neemt dat je niet geraakt wordt. Rem om veilig de rotonde over te kunnen steken. \r\n', '0', '', ''),
	(134, 'J', 4, 'gvr_114a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je nadert een verkeerslicht en er staat een voetganger op het punt om over te steken. Passeer deze  en wees voorbereid om snel te kunnen handelen.', '2', '', ''),
	(135, 'J', 4, 'gvr_115a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'De volgafstand op deze snelheid is te klein. Er bevinden zich bestuurders en personen op de vluchtstrook. Passeer deze gevaarlijke situatie met een lage snelheid. Houd de binnenspiegel in de gaten. ', '0', '', ''),
	(136, 'J', 4, 'gvr_116a.jpg', 'Je rijdt 130 km/u. Wat doe je?', 'Je hebt voldoende overzicht op de weg, je houdt de juiste snelheid en volgafstand aan. ', '2', '', ''),
	(137, 'J', 4, 'gvr_117a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De weg voor je is vrij, blijf daarom deze snelheid aanhouden. \r\n', '2', '', ''),
	(138, 'J', 4, 'gvr_118a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je nadert een turborotonde, waar het druk is. De auto voor je remt en er rijdt net een bus de rotonde op. Wees dus voorbereid om te kunnen stoppen. \r\n', '0', '', ''),
	(139, 'J', 4, 'gvr_119a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je rijdt met matige snelheid en je hebt een goed overzicht op de weg: je kunt door blijven rijden. ', '2', '', ''),
	(140, 'J', 4, 'gvr_120a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je wordt ingehaald door een bestuurder. De inhalende auto rijd al hard genoeg, dus geen handeling nodig.', '2', '', ''),
	(141, 'J', 4, 'gvr_121a.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'Je passeert een kruising en er nadert een auto van rechts maar jij rijdt op een voorrangsweg. Wees voorbereid om te kunnen handelen. \n', '2', '', ''),
	(142, 'J', 4, 'gvr_122a.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'Er dreigt geen direct gevaar: het overige verkeer is nog op een veilige afstand, blijf daarom deze snelheid aanhouden. ', '2', '', ''),
	(143, 'J', 4, 'gvr_123a.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'Je nadert een drukke kruising. Er staat een fietser te wachten om over te steken, laat je gas los om de situatie met een veilige snelheid te kunnen benaderen. \n', '1', '', ''),
	(144, 'J', 4, 'gvr_124a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De weg splitst zich, maar je houdt voldoende afstand tot je voorgangers. Je kunt dus door blijven rijden. ', '2', '', ''),
	(145, 'J', 4, 'gvr_125a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je rijdt met een toegestane snelheid. Je kunt de weg overzien. ', '2', '', ''),
	(146, 'J', 4, 'gvr_125b.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Wanneer je niet remt, kom je te dichtbij op jouw voorganger. Houd een veilige afstand.', '0', '', ''),
	(147, 'J', 4, 'gvr_126a.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'Er steken voetgangers over, rem om deze niet aan te rijden.', '0', '', ''),
	(148, 'J', 4, 'gvr_127a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je rijdt op een invoegstrook waar je snelheid moet maken, maar de auto voor je rijdt dicht op zijn voorganger. Voorkom dat je te dichtbij komt. ', '1', '', ''),
	(149, 'J', 4, 'gvr_128a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt een goed zicht op de weg. Er dreigt geen direct gevaar.  ', '2', '', ''),
	(150, 'J', 4, 'gvr_129a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'De auto voor je voegt in op de rotonde, laat je gas los om voldoende afstand te creëren. ', '1', '', ''),
	(151, 'J', 4, 'gvr_130a.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'De weg voor je is vrij, maar er nadert een zebrapad met weinig zicht. Pas op, hier kunnen voetgangers oversteken vanuit de bus-/ of tramhalte of vanachter de fietsen. ', '1', '', ''),
	(152, 'J', 4, 'gvr_131a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'De ambulance heeft haaientanden en heeft geen zwaailichten en sirene aanstaan. Je kunt de rotonde blijven volgen, want je hebt voorrang. ', '2', '', ''),
	(153, 'J', 4, 'gvr_131b.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je rijdt op een rotonde en er nadert een voorrangsvoertuig: een ambulance met zwaailichten en sirene. Rem en laat deze voor gaan. ', '0', '', ''),
	(154, 'J', 4, 'gvr_132a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je nadert een spoorwegovergang en de ambulance blokkeert jouw overzicht op de weg. Voorkom dat je stil komt te staan op de spoorwegovergang.  ', '1', '', ''),
	(155, 'J', 4, 'gvr_133a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je hebt nog een ruime afstand tot de verkeerslichten en je hebt overzicht op de weg. Wees voorbereid dat de verkeerslichten op rood kunnen springen, maar het is niet nodig om nu al te gaan remmen. ', '2', '', ''),
	(157, 'J', 4, 'gvr_135a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je haalt een militaire kolonne in: passeer deze. Houd dezelfde snelheid aan om ze zo snel mogelijk te passeren. ', '2', '', ''),
	(158, 'J', 4, 'gvr_136a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Er zijn wegwerkzaamheden; de linkerrijstrook gaat eindigen. Er zijn echter geen snelheidsbeperkingen en er is geen direct gevaar, dus je kan dezelfde snelheid behouden. ', '2', '', ''),
	(159, 'J', 4, 'gvr_137a.jpg', 'Je rijdt 35 km/u. Wat doe je?', 'Aan jouw beide kanten bevinden zich obstakels: er rijdt een auto dicht naast je en er staat een vuilnisbak rechts. Er is geen ruimte om uit te wijken. Je nadert ook een splitsing: rem af voordat de bocht neemt.', '0', '', ''),
	(161, 'J', 4, 'gvr_139a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De bestuurder rechts van je wilt dicht achter de auto voor je invoegen. Gas loslaten creëert genoeg afstand in de eerste instantie.   ', '1', '', ''),
	(162, 'J', 4, 'gvr_140a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De weg voor je is vrij; je houdt een ruime volgafstand. Daarom kun je blijven doorrijden op deze snelheid. ', '2', '', ''),
	(163, 'J', 4, 'gvr_141a.jpg', 'Je rijdt 130 km/u. Wat doe je?', 'Laat jouw gas los om op deze snelheid een veilige volgafstand te creëren. \r\n', '1', '', ''),
	(164, 'J', 4, 'gvr_142a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De spitsstrook is geopend. Je houdt voldoende volgafstand, de juiste snelheid en je hebt een goed zicht op de weg. Er is geen reden tot ingrijpen. ', '2', '', ''),
	(166, 'J', 4, 'gvr_145a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Je gaat het kruispunt oversteken waar werkzaamheden zijn. Ook staat er een persoon rechts. Rijd de werkzaamheden met lage snelheid voorbij en wees voorbereid om te kunnen remmen.  ', '2', '', ''),
	(167, 'J', 4, 'gvr_146a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Je rijdt op een weg waar je 100 km/u mag. De weg voor je is helemaal vrij, je kunt dus door blijven rijden.', '2', '', ''),
	(168, 'J', 4, 'gvr_147a.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'Passeer de fietser met een lagere snelheid. Door de bocht en bomen ernaast kun je niet goed zien of er tegenliggers aankomen', '2', '', ''),
	(169, 'J', 4, 'gvr_148a.jpg', 'Je rijdt 45 km/u. Wat doe je?', 'Je nadert een afbuigende voorrangsweg: neem de bocht en let op eventuele fietsers. ', '2', '', ''),
	(170, 'J', 4, 'gvr_150a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Je hebt een goed zicht op de weg voor je en je houdt voldoende volgafstand.  ', '2', '', ''),
	(171, 'J', 4, 'gvr_151a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je houdt voldoende volgafstand en kunt de situatie goed overzien.', '2', '', ''),
	(172, 'J', 4, 'gvr_153a.jpg', 'Je rijdt 90 km/u. Wat doe je?', 'De bestuurders om je heen remmen en de matrixborden geven 50 km/u aan. Bestuurders van rechts gaan invoegen. De auto naast je neigt naar jouw rijstrook. Rem af om aanrijdingen te voorkomen en volg de snelheidslimiet van het matrixbord op. ', '0', '', ''),
	(173, 'J', 4, 'gvr_153b.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De bestuurders voor je remmen en het lijkt alsof de auto links naast je zich wilt verplaatsen naar jouw rijstrook. Rem af om een aanrijding te voorkomen. ', '0', '', ''),
	(174, 'J', 4, 'gvr_154a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De elektronische signaleringsborden geven een snelheid weer van 50 km/u: er is iets aan de hand. Blijf met het overige verkeer meerijden.', '2', '', ''),
	(175, 'J', 4, 'gvr_155a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Er rijdt een politiewagen voorbij en de weersomstandigheden zijn niet zo best. Je rijdt ook op een uitvoegstrook, maar er is geen reden om af te remmen.', '2', '', ''),
	(176, 'J', 4, 'gvr_156a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je houdt een ruime volgafstand en er dreigt geen gevaar, je kunt blijven doorrijden op deze snelheid. ', '2', '', ''),
	(177, 'J', 4, 'gvr_157a.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'Je houdt een ruime volgafstand en er dreigt geen gevaar, je kunt blijven doorrijden op deze snelheid. ', '2', '', ''),
	(180, 'J', 4, 'gvr_160a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt een goed zicht op de weg en je houdt een veilige afstand tot jouw voorganger. Er is geen reden om in te grijpen.  ', '2', '', ''),
	(181, 'J', 4, 'gvr_161a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je nadert een vrachtwagen die maximaal 80 km/u rijdt. Er is ruimte om je te verplaatsen naar de linkerrijstrook. Daarom kun je deze snelheid aanhouden.', '2', '', ''),
	(182, 'J', 4, 'gvr_162a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Vergroot de volgafstand op deze snelheid door het gas los te laten. ', '1', '', ''),
	(183, 'J', 4, 'gvr_163a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Deze snelheid mag je hier rijden. Er is genoeg afstand tot jouw voorganger, houdt er rekening mee dat de auto voor je zich kan gaan verplaatsen naar jouw rijstrook. ', '2', '', ''),
	(184, 'J', 4, 'gvr_164a.jpg', 'Je rijdt 55 km/u. Wat doe je?', 'Deze overzichtelijke bocht kun je nemen met deze snelheid. Er rijdt niemand voor je. Je moet dadelijk met voldoende snelheid invoegen.', '2', '', ''),
	(185, 'J', 4, 'gvr_165a.jpg', 'Je rijdt 100 km/u. Wat doe je?', ' Er is voldoende zicht op de weg en je houdt een ruime volgafstand, er is geen reden om in te grijpen. ', '2', '', ''),
	(186, 'J', 4, 'gvr_166a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De matrixborden lichten op met ‘50 km/u’, dus je houdt de juiste snelheid aan, maar de bestuurders voor je remmen. Rem daarom af om te voorkomen dat je op hen botst. Let ook op de auto’s rechts naast je die eventueel willen invoegen.  ', '0', '', ''),
	(187, 'J', 4, 'gvr_166b.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van 50 km/u aan. De bestuurders voor je remmen. Rem daarom af om te voorkomen dat je op hen botst. Let ook op de auto’s rechts naast je die eventueel willen invoegen. ', '0', '', ''),
	(188, 'J', 4, 'gvr_167a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De weg voor je is vrij, je kunt blijven doorrijden op deze snelheid. ', '2', '', ''),
	(189, 'J', 4, 'gvr_168a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Rem om te voorkomen dat de volgafstand te klein wordt en je op de bestuurders voor je botst. Let op de auto’s die zich willen verplaatsen naar de andere rijstroken.  ', '0', '', ''),
	(190, 'J', 4, 'gvr_169a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je rijdt op een hoge snelheid en de bestuurder voor je remt. Rem flink af om een aanrijding te voorkomen. ', '0', '', ''),
	(191, 'J', 4, 'gvr_170a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Het verkeerslicht springt op geel. Rem om op tijd tot stilstand te komen.  ', '0', '', ''),
	(192, 'J', 4, 'gvr_171a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De weg voor je is vrij. Je kunt daarom gewoon door blijven rijden. ', '2', '', ''),
	(194, 'J', 4, 'gvr_173a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je moet voorrang verlenen aan bestuurders, rijdt deze rotonde op met een lagere snelheid. ', '0', '', ''),
	(195, 'J', 4, 'gvr_174a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je hebt een goed overzicht op de weg en er zijn geen verkeersdeelnemers te bekennen. Je kunt op deze snelheid blijven rijden. ', '2', '', ''),
	(197, 'J', 4, 'gvr_175a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De zigzag-streep op de weg waarschuwt je: verminder je snelheid, er nadert een oversteekplaats voor fietsers.', '1', '', ''),
	(198, 'J', 4, 'gvr_175b.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Rem af om de fietser niet te raken die aan het oversteken is. De zigzag-streep waarschuwt je voor een oversteekplaats voor fietsers. ', '0', '', ''),
	(199, 'J', 4, 'gvr_176a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'De auto voor je remt, rem daarom ook af om een veilige afstand te behouden. Je kunt niet zien waarom hij remt. Houdt er rekening mee dat er iets is gebeurd en rem daarom ook.  ', '0', '', ''),
	(200, 'J', 4, 'gvr_177a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De auto voor je rijdt net jouw weghelft op. Laat je gas even los, om niet te dichtbij te raken.  ', '1', '', ''),
	(201, 'J', 4, 'gvr_178a.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'Het kruispunt voor je is geblokkeerd. Rem om te voorkomen dat je niet stil komt te staan op de spoorwegovergang.  ', '0', '', ''),
	(202, 'J', 4, 'gvr_179a.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'De felle zon blokkeert jouw zicht. Er is geen ruimte om uit te wijken. Laat het gas los om de situatie te kunnen inschatten en om een ruime volgafstand te houden.', '1', '', ''),
	(203, 'J', 4, 'gvr_180a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Er zijn wegwerkzaamheden. Verminder jouw snelheid en houdt voldoende afstand tot jouw voorgangers.', '1', '', ''),
	(204, 'J', 4, 'gvr_181a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Het is erg druk op de weg, zorg daarom voor voldoende volgafstand.  ', '1', '', ''),
	(206, 'J', 4, 'gvr_183a.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'Er bevinden zich voertuigen op de vluchtstrook, wees daarom extra voorzichtig. Bovendien rijd je door een scherpe bocht. ', '1', '', ''),
	(207, 'J', 4, 'gvr_184a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Een fietser steekt het kruispunt over en er bevinden zich voetgangers links naast je. Rem daarom af om niemand aan te rijden. ', '0', '', ''),
	(208, 'J', 4, 'gvr_185a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je kunt ver vooruit zien en er is geen dreigend gevaar. Om die reden hoef je hier niets te doen. ', '2', '', ''),
	(209, 'J', 4, 'gvr_186a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je houdt een veilige volgafstand tot de andere bestuurders. Let wel op: de vluchtstrook is geopend.', '2', '', ''),
	(210, 'J', 4, 'gvr_187a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je bent aan het uitvoegen en het regent licht. Verminder jouw snelheid om voldoende afstand te behouden tot jouw voorganger. ', '1', '', ''),
	(211, 'J', 4, 'gvr_188a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Rem flink af! Er loopt een voetganger richting jouw weghelft waarvan lijkt dat deze jou niet heeft gezien. Bovendien remt de auto voor je.', '0', '', ''),
	(214, 'J', 4, 'gvr_192a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Er is geen direct gevaar ', '2', '', ''),
	(215, 'J', 4, 'gvr_193a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'De bestuurder voor je remt. Rem om op een veilige afstand te blijven. \r\n', '0', '', ''),
	(216, 'J', 4, 'gvr_194a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Remmen, je nadert een rotonde en de bestuurder voor je moet wachten totdat de auto’s op de rotonde voorbij zijn. Let ook op de fietsers die willen oversteken.  ', '0', '', ''),
	(217, 'J', 4, 'gvr_195a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Laat je gas los om voldoende volgafstand te creëren. Houdt er ook rekening mee dat de weg zich in verschillende richtingen splitst. ', '1', '', ''),
	(218, 'J', 4, 'gvr_196a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De bestuurder voor je gaat zich verplaatsen naar de linkerrijstrook, omdat bestuurders van rechts gaan invoegen. Wees voorbereid om ook plaats te maken voor de invoegende bestuurders.  ', '2', '', ''),
	(219, 'J', 4, 'gvr_197a.jpg', 'Je rijdt 40 km/u. Wat doe je?', 'Passeer de blokkade/ wegwerkzaamheden met een lagere snelheid. Je nadert ook een kruispunt met verkeerslichten. Wees voorbereid om te kunnen stoppen. ', '1', '', ''),
	(220, 'J', 4, 'gvr_198a.jpg', 'Je rijdt 40 km/u. Wat doe je?', 'Let op, er loopt een persoon op het kruispunt! Rijdt voorbij de wegwerkzaamheden met een lage snelheid en wees voorbereid dat het verkeerslicht rood kan worden.  ', '0', '', ''),
	(221, 'J', 4, 'gvr_199a.jpg', 'Je rijdt 70 km/u. Wat doe je?', 'de max. snelheid is 90 km/u, en jij rijd 70 km/u, dus er is geen directe actie nodig . Behoud dezelfde snelheid om het kruispunt veilig en snel te passeren. ', '2', '', ''),
	(222, 'J', 4, 'gvr_200a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt duidelijk zicht op de weg voor je en er is genoeg ruimte om uit te wijken, wanneer er een gevaarlijke situatie ontstaat. ', '2', '', ''),
	(223, 'J', 4, 'gvr_201a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van ‘70 km/u’ aan, je rijdt dus veel te hard. De bestuurders voor je remmen ook.  ', '0', '', ''),
	(226, 'J', 4, 'gvr_203a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je rijdt met hoge snelheid, verminder daarom naar een veilige snelheid. De auto’s om je heen remmen, let op de in- en uitvoegende bestuurders. ', '0', '', ''),
	(227, 'J', 4, 'gvr_204a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je houdt voldoende volgafstand en kunt bij eventueel gevaar uitwijken naar de linkerrijstrook. ', '2', '', ''),
	(228, 'J', 4, 'gvr_205a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Er is geen duidelijk direct gevaar. ', '2', '', ''),
	(229, 'J', 4, 'gvr_206a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Je mag hier 100 km/h uur rijden, maar zorg ervoor dat je een veilige volgafstand behoud. ', '2', '', ''),
	(230, 'J', 4, 'gvr_207a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Op de linkerrijstrook rijdt een vrachtwagen die afremt. Deze mag maar maximaal 80 km/u rijden. Het snelheidsverschil tussen jullie is erg groot. Ook rijden er veel bestuurders voor je die afremmen. Bovendien mag je niet rechts inhalen. Remmen dus!', '0', '', ''),
	(231, 'J', 4, 'gvr_208a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt een goed zicht op de weg en je houdt voldoende volgafstand.', '2', '', ''),
	(233, 'J', 4, 'gvr_210a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je rijdt met hoge snelheid op de bestuurders voor je af, die aan het remmen zijn. Bovendien zijn er wegwerkzaamheden.', '0', '', ''),
	(234, 'J', 4, 'gvr_211a.jpg', 'Je rijdt 70 km/u. Wat doe je?', 'Zolang de verkeerslichten op groen staan is het niet nodig om gas terug te nemen. Echter moet je wel alert blijven!', '2', '', ''),
	(235, 'J', 4, 'gvr_212a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je nadert een bord met een aangegeven snelheidslimiet van 90 km/u. Onnodig remmen op de snelweg moet je vermijden. Gas loslaten dus! ', '1', '', ''),
	(236, 'J', 4, 'gvr_213a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Het is erg mistig, laat daarom je gas los om de situatie te blijven overzien en een veilige volgafstand te blijven behouden. Een snelheid van 120 km/u is te hard tijdens deze weersomstandigheden.   ', '1', '', ''),
	(237, 'J', 4, 'gvr_214a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je mag hier 120 km/u, maar door de weersomstandigheden is jouw zicht lichtelijk beperkt. ', '1', '', ''),
	(239, 'J', 4, 'gvr_215a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De bestuurder naast je wilt zich verplaatsen naar jouw rijstrook. Laat even het gas los om voldoende afstand te houden en de bestuurder deze manoeuvre veilig te laten uitvoeren.', '1', '', ''),
	(240, 'J', 4, 'gvr_216a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Het matrixbord rechts van je geeft een snelheidslimiet van 50 km/u aan maar er rijdt geen auto.  Als jij het gas terugneemt ben jij het gevaar op de weg.', '2', '', ''),
	(241, 'J', 4, 'gvr_217a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je houdt voldoende afstand tot de bestuurder voor je. Let wel op: het is druk op de weg.', '2', '', ''),
	(242, 'J', 4, 'gvr_218a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Jij gaat een afslag nemen waar een snelheidslimiet van 100 km/u geldt. Verminder je gas om op deze snelheid te komen. ', '1', '', ''),
	(243, 'J', 4, 'gvr_219a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De meest linkerrijstrook gaat eindigen en de weg splitst zich in meerdere richtingen: let dus goed op in- en uitvoegende bestuurders. Echter kun je gewoon met het verkeer voor je meerijden, indien de volgafstand verder verkleint moet je gas loslaten. ', '2', '', ''),
	(244, 'J', 4, 'gvr_220a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je nadert een turborotonde waar je voorrang moet verlenen en de bestuurder voor je remt. Let op de geleiders: hier kun je niet overheen rijden. Neem de rotonde met gematigde snelheid. ', '0', '', ''),
	(245, 'J', 4, 'gvr_221a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je houdt voldoende volgafstand. Houdt wel rekening met de felle zon: hierdoor kun je soms pas situaties op het laatste moment zien.  ', '2', '', ''),
	(246, 'J', 4, 'gvr_222a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Je hebt een duidelijk zicht op de weg en genoeg volgafstand. Er is geen reden tot handelen.  ', '2', '', ''),
	(248, 'J', 4, 'gvr_224a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je rijdt met hoge snelheid op de spitsstrook die geopend is. Je mag de maximale snelheid aanhouden.', '2', '', ''),
	(249, 'J', 4, 'gvr_224b.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je rijdt met hoge snelheid op de spitsstrook die geopend is. De bestuurder voor je remt. Je kunt niet zien wat er voor je gebeurd. Rem daarom af om aanrijdingen te voorkomen. ', '0', '', ''),
	(250, 'J', 4, 'gvr_225a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Houdt voldoende volgafstand! Het is erg druk op de weg en de spitsstrook is geopend. Er geldt een snelheidslimiet van 90 km/u en er wordt gewaarschuwd voor slipgevaar. Remmen om de situatie beter in te kunnen schatten.', '0', '', ''),
	(251, 'J', 4, 'gvr_226a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je houdt voldoende volgafstand om tijdig te kunnen reageren, Blijf op deze snelheid rijden voor de verkeersdoorstroming. ', '2', '', ''),
	(252, 'J', 4, 'gvr_227a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De bestuurder die jou aan het inhalen is houd voldoende afstand, er is dus geen handeling nodig', '2', '', ''),
	(253, 'J', 4, 'gvr_228a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Laat het gas los om meer volgafstand te creëren.  ', '1', '', ''),
	(254, 'J', 4, 'gvr_229a.jpg', 'Je rijdt 80 km/u. Wat doe je?', ' Er wilt een voorrangsvoertuig voor jou invoegen. Er is geen plek om uit te wijken: rem snel af, om een ongeval te voorkomen en de ambulance de ruimte te geven.', '0', '', ''),
	(255, 'J', 4, 'gvr_231a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je houdt voldoende afstand tot jouw voorganger. Let wel op de bestuurders op de linkerrijstrook die niet veel afstand tot elkaar houden. ', '2', '', ''),
	(256, 'J', 4, 'gvr_232a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De weg voor je is vrij: er is niemand te bekennen. Je kunt daarom op deze snelheid door blijven rijden.', '2', '', ''),
	(257, 'J', 4, 'gvr_233a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je rijdt op een brug, waar je niet mag inhalen. Je rijdt met een te hoge snelheid om deze situatie in te schatten. Rem daarom af.  ', '0', '', ''),
	(259, 'J', 4, 'gvr_235a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je nadert een brugdek, die je bij file moet vrijhouden. Je laat het gas los om niet midden op het brugdek te hoeven wachten. Je kunt tevens niet goed zien wat er voor je gebeurd. ', '1', '', ''),
	(262, 'J', 4, 'gvr_236b.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'De bestuurder voor je remt. Laat het gas los om de situatie te kunnen inschatten. Je hebt ruimte om je te verplaatsen naar de linkerrijstrook.  ', '1', '', ''),
	(263, 'J', 4, 'gvr_237a.jpg', 'Je rijdt 130 km/u. Wat doe je?', 'Zorg ervoor dat je voldoende volgafstand blijft houden op deze hoge snelheid.  ', '2', '', ''),
	(264, 'J', 4, 'gvr_238a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De weg staat vol met verkeer. Je rijdt met een hoge snelheid op je voorganger af, die aan het remmen is. Rem om aanrijdingen te voorkomen. ', '0', '', ''),
	(265, 'J', 4, 'gvr_239a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'De aangegeven snelheidslimiet is 70 km/ u, je rijdt dus te hard. De bestuurder voor je remt; voorkom een aanrijding en rem zo snel mogelijk. ', '0', '', ''),
	(266, 'J', 4, 'gvr_239b.jpg', 'Je rijdt 70 km/u. Wat doe je?', 'De bestuurder voor je remt. Om een aanrijding te voorkomen rem je ook, ook al is de aangegeven snelheidslimiet 70 km/ u. Je weet niet wat er verderop gebeurd is', '0', '', ''),
	(267, 'J', 4, 'gvr_240a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De bestuurder voor je remt, haal hem in op de linker rijbaan. ', '2', '', ''),
	(268, 'J', 4, 'gvr_241a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt voldoende zicht op de weg en je houdt een veilige volgafstand. Let wel op de invoegstrook waar bestuurders kunnen in- of uitvoegen of inhalen via rechts.  ', '2', '', ''),
	(269, 'J', 4, 'gvr_242b.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je nadert een brugovergang. De auto voor je remt: rem om een aanrijding te voorkomen. Inhalen mag hier niet: het is een gevaarlijk, onoverzichtelijk punt. Je kunt niet zien wat er voor je gebeurt en er is geen plek om uit te wijken naar de andere weghelft.', '0', '', ''),
	(270, 'J', 4, 'gvr_243a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je haalt een vrachtwagen in die maximaal 80 km/u rijdt. Let op het snelheidsverschil en eventueel in- en uitvoegende bestuurders. ', '2', '', ''),
	(271, 'J', 4, 'gvr_244a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'Je moet vaart maken want je gaat invoegen op een autoweg. De weg voor je is ook vrij, dus je hebt de ruimte om in te voegen. ', '2', '', ''),
	(274, 'J', 4, 'gvr_246b.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De bestuurder voor je remt. Door de winterse weersomstandigheden kan de remweg langer zijn. Het is verstandig om daarom alvast te remmen.', '0', '', ''),
	(275, 'J', 4, 'gvr_247a.jpg', 'Je rijdt 120 km/u. Wat doe je?', ' Je bent nog snelheid aan het maken op de invoegstrook en de weg voor je is niet vrij. Je rijdt erg hard in vergelijking tot de andere bestuurders. Let er op dat er genoeg ruimte is om veilig te kunnen invoegen. \r\n', '1', '', ''),
	(276, 'J', 4, 'gvr_248a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je passeert net een bord met de snelheidslimiet van 100 km/u. Je rijdt dus te hard. Verminder jouw snelheid, zodat je veilig kunt invoegen op dezelfde snelheid als het overige verkeer.', '1', '', ''),
	(277, 'J', 4, 'gvr_249a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De bestuurder naast je wilt plots invoegen op jouw rijstrook, maar deze houdt geen veilige afstand. Rem om een aanrijding te voorkomen. ', '0', '', ''),
	(278, 'J', 4, 'gvr_250a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Je rijdt 100 km/u op een weg waar maximaal 80 km/u gereden mag worden. Remmen dus! \r\n', '0', '', ''),
	(279, 'J', 4, 'gvr_251a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je nadert borden die een maximumsnelheidslimiet aangeven van 100 km/u. Gas loslaten om op deze snelheid te komen.  ', '1', '', ''),
	(280, 'J', 4, 'gvr_252a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Je rijdt op een spitsstrook. De vrachtwagen voor je rijdt maximaal 80 km/u. Laat het gas los en haal in wanneer dit mogelijk is.  ', '1', '', ''),
	(282, 'J', 4, 'gvr_253a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De bestuurder voor je remt. Er is iets gebeurd, rem dus af, want je nadert met een hoge snelheid.  ', '0', '', ''),
	(283, 'J', 4, 'gvr_254a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De bestuurders op de rijbaan zijn aan het remmen. Er is iets gebeurd: rem, want je nadert met een hoge snelheid.  ', '0', '', ''),
	(284, 'J', 4, 'gvr_255a.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'Het verkeerslicht staat op rood. Rem om tot stilstand te komen voor het verkeerslicht.  ', '0', '', ''),
	(285, 'J', 4, 'gvr_256a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De weg voor je is niet vrij, rijd daarom met gepaste snelheid. Er nadert tevens een kruising met een waarschuwingsbord voor een zebrapad maar je ziet geen hinder.', '1', '', ''),
	(286, 'J', 4, 'gvr_256b.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De bestuurder voor je remt. Een snelheid van 50 km/u is te snel in deze situatie, rem af om een aanrijding te voorkomen. Je nadert een kruispunt met een zebrapad, de auto remt wellicht om voetgangers over te laten steken. ', '0', '', ''),
	(287, 'J', 4, 'gvr_257a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je nadert een gewijzigde verkeerssituatie in verband met wegwerkzaamheden, een scherpe bocht en er komt een tegenligger aan. Passeer deze met een lage snelheid.  ', '0', '', ''),
	(288, 'J', 4, 'gvr_258a.jpg', 'Je rijdt 60 km/u. Wat doe je?', 'De weg voor je is vrij en er is nog genoeg afstand tot de verkeerslichten.  \r\n', '2', '', ''),
	(289, 'J', 4, 'gvr_259a.jpg', 'Je rijdt 80 km/u. Wat doe je?', 'De bestuurder voor je remt. Je nadert een sluis. Er is geen ruimte om uit te wijken en de borden waarschuwen je dat de vluchtstrook gaat eindigen of smal wordt.', '0', '', ''),
	(290, 'J', 4, 'gvr_259b.jpg', 'Je rijdt 70 km/u. Wat doe je?', 'Je nadert een sluis. Het matrixbord geeft een snelheidslimiet van 50 km/u aan. De bestuurder voor je remt: pas op! Er is geen ruimte om uit te wijken en de borden waarschuwen je dat de vluchtstrook gaat eindigen of smal wordt.', '0', '', ''),
	(291, 'J', 4, 'gvr_260a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Je rijdt recht op voetgangers af die oversteken. Rem om deze niet aan te rijden. ', '0', '', ''),
	(292, 'J', 4, 'gvr_261a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Het verkeerslicht staat op rood. Rem om tot stilstand te komen.  ', '0', '', ''),
	(293, 'J', 4, 'gvr_262a.jpg', 'Je rijdt 30 km/u. Wat doe je?', ' Je rijdt een rotonde op met bestuurders voor je. Rem totdat de weg vrij is om te kunnen doorrijden. ', '0', '', ''),
	(294, 'J', 4, 'gvr_262b.jpg', 'Je rijdt 15 km/u. Wat doe je?', 'Rem om te zorgen dat je op tijd stilstaat voor het rode verkeerslicht.', '0', '', ''),
	(295, 'J', 4, 'gvr_262c.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Het verkeerslicht staat op groen.  ', '2', '', ''),
	(296, 'J', 4, 'gvr_263a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Het is druk op de weg; de bestuurders voor je remmen. Zorg dat je voldoende volgafstand houdt, rem daarom. ', '0', '', ''),
	(297, 'J', 4, 'gvr_264a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je houdt voldoende volgafstand. Let wel op, in de verte zie je lichten branden. ', '2', '', ''),
	(298, 'J', 4, 'gvr_265a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Er is geen direct gevaar bij deze volgafstand. ', '2', '', ''),
	(299, 'J', 4, 'gvr_266a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De bestuurders op de weg voor je remmen. Rem om de situatie op een lagere snelheid veilig te kunnen overzien. ', '0', '', ''),
	(300, 'J', 4, 'gvr_267a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Jouw rijstrook is vrij, maar de linkerrijstrook is druk. Je kan de situatie maar deels overzien. Laat het gas even los om deze situatie met enige voorzichtigheid te benaderen.  \r\n', '1', '', ''),
	(301, 'J', 4, 'gvr_268a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van 50 km/u aan. Je houdt genoeg afstand tot jouw voorganger. ', '2', '', ''),
	(302, 'J', 4, 'gvr_268b.jpg', 'Je rijdt 70 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van 50 km/u aan. Pas daarom jouw snelheid aan en wees voorbereid op een file of andere gebeurtenis. ', '1', '', ''),
	(303, 'J', 4, 'gvr_269a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Het kruispunt is geblokkeerd. Rem om te voorkomen dat je de andere bestuurders raakt of blokkeert doordat je midden op het kruispunt stilstaat.  ', '0', '', ''),
	(304, 'J', 4, 'gvr_270a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je nadert een gevaarlijk kruispunt en rijdt dicht op je voorganger. Rem af om de situatie met een aangepaste snelheid te benaderen. ', '0', '', ''),
	(305, 'J', 4, 'gvr_271a.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'Je houdt voldoende volgafstand om tijdig te kunnen reageren. Houd de binnenspiegel in de gaten. ', '2', '', ''),
	(307, 'J', 4, 'gvr_273a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Benader deze situatie met een lage snelheid. Personen kunnen plots achter de auto’s vandaan komen of bestuurders die wegrijden vanuit hun parkeerplaats. Let ook op de auto voor je die remt.', '1', '', ''),
	(308, 'J', 4, 'gvr_274a.jpg', 'Je rijdt 130 km/u. Wat doe je?', 'Je nadert een splitsing van de autosnelweg. Wees voorbereid om te handelen, wanneer bv. bestuurders van rechts willen invoegen. Je kunt de situatie niet geheel overzien. ', '1', '', ''),
	(309, 'J', 4, 'gvr_275a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je rijdt erg dicht op de bestuurder voor je. Er is weinig ruimte om uit te wijken. Rem om de volgafstand flink te vergroten.  ', '1', '', ''),
	(310, 'J', 4, 'gvr_276a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je bent aan het invoegen, maar er zijn werkzaamheden. Passeer deze met een lagere snelheid en ga pas invoegen wanneer er ruimte voor is.  ', '0', '', ''),
	(311, 'J', 4, 'gvr_277a.jpg', 'Je rijdt 40 km/u. Wat doe je?', 'Een landbouwvoertuig heeft een maximumsnelheid van 25 km/u. Je mag hier niet inhalen, rem af om een aanrijding te voorkomen. ', '0', '', ''),
	(312, 'J', 4, 'gvr_278a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Er zijn wegwerkzaamheden. Let op bestuurders die jou kunnen afsnijden, terwijl ze zich verplaatsen naar jouw rijstrook.', '1', '', ''),
	(313, 'J', 4, 'gvr_279a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'Je nadert een autoweg, maar deze begint met een gevaarlijke scherpe bocht en de bestuurders voor je remmen af. Rem flink af om de bocht veilig te kunnen nemen. ', '0', '', ''),
	(315, 'J', 4, 'gvr_280a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'De weg is vol met bestuurders die remmen. Rem om een veilige volgafstand te creëren. ', '0', '', ''),
	(316, 'J', 4, 'gvr_281a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De weg voor je is niet vrij. Zorg dat je voldoende ruimte creëert en alert bent om tijdig te kunnen reageren wanneer bv. bestuurders naast je willen gaan invoegen. ', '1', '', ''),
	(317, 'J', 4, 'gvr_282a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De weg voor je is vrij. Je hebt geen reden om vaart te minderen.', '2', '', ''),
	(318, 'J', 4, 'gvr_282b.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Let op de bestuurder naast je die zich naar jouw rijstrook wil gaan verplaatsen.', '2', '', ''),
	(319, 'J', 4, 'gvr_283a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je houdt voldoende volgafstand en je hebt een goed zicht op de weg. \r\n', '2', '', ''),
	(321, 'J', 4, 'gvr_285a.jpg', 'Je rijdt 25 km/u. Wat doe je?', 'Je moet voorrang verlenen aan de bestuurder van rechts. Let op, er is geen ruimte om uit te wijken, omdat er bomen en geparkeerde auto’s zich naast jou bevinden. ', '0', '', ''),
	(322, 'J', 4, 'gvr_286a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'De bestuurder voor je rijdt jouw weghelft op. Je hebt geen ruimte om uit te wijken. Aan de andere kant staat een busje geparkeerd.  ', '0', '', ''),
	(323, 'J', 4, 'gvr_287a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van 70 km/u. Je rijdt erg hard: pas jouw snelheid aan om gevaarlijke situaties te voorkomen. Het is echter beter om verder van tevoren gas los te laten', '0', '', ''),
	(324, 'J', 4, 'gvr_287b.jpg', 'Je rijdt 90 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van 70 km/u. Pas daarom jouw snelheid aan: je rijdt te hard. ', '1', '', ''),
	(325, 'J', 4, 'gvr_287c.jpg', 'Je rijdt 70 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van 70 km/u. Je kunt de weg blijven volgen. Let wel op het snelheidsverschil tussen de andere rijstroken. \r\n', '2', '', ''),
	(326, 'J', 4, 'gvr_288a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Er is voldoende volgafstand met de overige bestuurders, er is geen directe handeling nodig. ', '2', '', ''),
	(327, 'J', 4, 'gvr_289a.jpg', 'Je rijdt 130 km/u. Wat doe je?', 'Je nadert een tunnel. Geef jouw ogen de tijd om op deze hoge snelheid aan het donker te wennen.', '1', '', ''),
	(328, 'J', 4, 'gvr_290a.jpg', 'Je rijdt 100 km/u. Wat doe je?', 'De auto voor je houdt niet genoeg afstand tijdens het invoegen. Vergroot daarom de volgafstand. Je rijdt in een tunnel: dit is erg gevaarlijk want je kunt niet uitwijken.  ', '1', '', ''),
	(329, 'J', 4, 'gvr_291a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt overzicht op de weg en houdt genoeg volgafstand.  ', '2', '', ''),
	(330, 'J', 4, 'gvr_292a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Er dreigt geen gevaar. Laat het verkeer doorstromen en ga niet onnodig vaart minderen. ', '2', '', ''),
	(331, 'J', 4, 'gvr_292b.jpg', 'Je rijdt 120 km/u. Het overige verkeer heeft dezelfde snelheid. Wat doe je?', 'Wanneer de bestuurder op de linkerrijstrook gaat invoegen naar jouw rijstrook, is er geen veilige afstand meer. Met 120 km/u is dit een zeer onveilige situatie. Rem om de volgafstand fiks te vergroten. \r\n', '1', '', ''),
	(332, 'J', 4, 'gvr_293a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'De bestuurder voor je remt, je hebt geen zicht op de weg voor je en er wil een minder valide voetganger oversteken: remmen dus!', '0', '', ''),
	(333, 'J', 4, 'gvr_294a.jpg', 'Je rijdt 30 km/u. Wat doe je?', 'Een voetganger die slecht ter been is, staat op het punt om over te steken. Je moet deze voor laten gaan.  \n', '0', '', ''),
	(334, 'J', 4, 'gvr_295a.jpg', 'Je rijdt 20 km/u. Wat doe je?', 'Iemand die met de fiets loopt is een voetganger, geef deze dus de gelegenheid om veilig het zebrapad over te steken.', '0', '', ''),
	(335, 'J', 4, 'gvr_296a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt een goed zicht op de weg en er is voldoende afstand tot het overige verkeer. ', '2', '', ''),
	(336, 'J', 4, 'gvr_297a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt geen ruimte om uit te wijken naar links of rechts. Je nadert een auto met trailer: deze mag maar maximaal 90 km/u rijden.  ', '0', '', ''),
	(337, 'J', 4, 'gvr_298a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je neemt een flauwe bocht en het is druk op de linkerrijstrook. Pas op voor bestuurders die naast je willen in- of uitvoegen. ', '1', '', ''),
	(338, 'J', 4, 'gvr_298b.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Zorg ervoor dat er voldoende afstand blijft tussen jou en de bestuurder die zich gaat verplaatsen naar jouw rijstrook.', '1', '', ''),
	(339, 'J', 4, 'gvr_299a.jpg', 'Je rijdt 90 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van 50 km/u aan. Pas jouw snelheid dus aan!', '0', '', ''),
	(340, 'J', 4, 'gvr_299b.jpg', 'Je rijdt 50 km/u. Wat doe je?', 'De matrixborden geven een snelheidslimiet van 50 km/u aan. Je houdt dus de correcte snelheid aan. Blijf wel op het drukke verkeer letten. \r\n', '2', '', ''),
	(341, 'J', 4, 'gvr_300a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je hebt een goed zicht op de weg, er is geen reden om te handelen.  ', '2', '', ''),
	(342, 'J', 4, 'gvr_300b.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Zorg ervoor dat er voldoende afstand blijft tussen jou en de bestuurder die zich gaat verplaatsen naar jouw rijstrook. ', '1', '', ''),
	(343, 'J', 4, 'gvr_301a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Dit is een gevaarlijke situatie: de bestuurder voor je remt, en de vrachtwagen blokkeert jouw zicht op de weg. Er is geen ruimte om uit te wijken.  ', '0', '', ''),
	(344, 'O', 4, 'gvr_302a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Een snelheidslimiet van 70km/u wordt aangegeven bij het nemen van deze scherpe bocht. Pas dus flink jouw snelheid aan. Er rijdt een vrachtwagen naast je dus je hebt geen ruimte om uit te wijken.  ', '0', '', ''),
	(345, 'J', 4, 'gvr_302b.jpg', 'Je rijdt 70 km/u. Wat doe je?', 'Een snelheidslimiet van 70km/u wordt aangegeven bij het nemen van deze scherpe bocht. Let op de vrachtwagen naast je en houdt genoeg afstand tot je voorganger. ', '2', '', ''),
	(347, 'J', 4, 'gvr_304a.jpg', 'Je rijdt 120 km/u. Wat doe je?', 'Je houdt voldoende afstand. Blijf wel opletten, want het wordt drukker op de weg.', '1', '', ''),
	(606, 'A', 1, '167b.jpg', 'Welk gedeelte wordt de rijbaan genoemd?', 'De rijstroken waar je op kunt rijden worden samen de rijbaan genoemd. \nB is de rijstrook. De berm en vluchtstrook horen bij de weg. \n', 'Gedeelte A', 'Gedeelte B', 'Gedeelte C'),
	(607, 'A', 2, 'ef_304.jpg', 'Kun je een brommobiel buiten de bebouwde kom verwachten?\r\n', 'Een brommobiel mag binnen en buiten de bebouwde kom rijden, deze is alleen niet toegestaan op de auto(snel)weg. \n', 'JA', 'NEE', ''),
	(608, 'A', 1, '185.png', 'Valt een gehandicaptenvoertuig in de categorie voetgangers of in de categorie bestuurders? ', 'Een gehandicaptenvoertuig mag zelf bepalen welk weggedeelte deze gebruikt. Wanneer een scootmobiel gebruik maakt van het trottoir/voetpad, moet deze de regels volgen van een voetganger, ook wanneer deze oversteekt van het ene naar het andere voetpad.', 'Het ligt aan de plek op de weg die het gehandicaptenvoertuig inneemt', 'Voetgangers', ' Bestuurders'),
	(609, 'A', 4, '113.jpg', 'Je gebruikt jouw auto niet meer en parkeert deze op de doorgaande weg voor je huis. Moet je nog steeds wegenbelasting betalen?', 'Wanneer de auto op de openbare weg staat of zichtbaar op de oprit van jouw huis, moet er altijd wegenbelasting betaald worden. Staat de auto in een afgesloten garage, dan kan je het voertuig schorsen en hoef je geen wegenbelasting betalen. Wel moet de kentekenplaat worden verwijderd.', 'JA', 'NEE', ''),
	(610, 'A', 1, '036.jpg', 'Wie wordt er verstaan onder het verkeer?', 'Weggebruikers zijn iedereen die deelneemt aan het verkeer. ', 'Alle weggebruikers', 'Alle motorvoertuigen', 'Voetgangers, fietsers en auto’s '),
	(611, 'A', 1, '171.jpg', 'Is iemand die naast zijn paard loopt een voetganger of bestuurder? ', 'Een ruiter die naast zijn paard loopt, blijft een bestuurder: Deze valt onder een geleider van een rij- of trekdier of vee. ', 'Bestuurder', 'Voetganger', ''),
	(612, 'A', 1, 'efvkr_232.jpg', 'Hoe hard mag je maximaal rijden op deze weg? ', 'dit bord betekent ‘Autoweg’: de maximumsnelheid bedraagt 100 km/u. \r\n', '100 km/u', '80 km/u', '130 km/u'),
	(613, 'G', 2, '028.png', 'Mag je een blaastest weigeren vanuit een geloofsovertuiging?', 'Ongeacht jouw geloof, moet je een blaastest doen wanneer de politie jou daartoe vordert. Je kunt wel vanwege bv. een kaakoperatie de blaastest weigeren omdat je niet kunt blazen. Dan moet je meewerken om je alsnog te laten testen (via het bloed of urine).\r\n', 'NEE', 'JA', ''),
	(614, 'A', 1, '268.jpg', 'Welke snelheid moet je op zijn minst kunnen rijden om gebruik te mogen maken van deze weg?', 'Alleen motorvoertuigen die minimaal 60 km/u kunnen en mogen rijden zijn toegestaan op de autosnelweg.', '60 km/u', '50 km/u', ' 80 km/u'),
	(615, 'A', 2, '2d_009.png', 'Moet je de groene kaart altijd bij je hebben?', 'In Nederland is het niet verplicht de groene kaart in de auto bij je te hebben. Zodra het voertuig zich buiten Nederland bevindt is het wel verplicht.', 'NEE', 'JA', ''),
	(616, 'A', 1, 'efvkr_085.jpg', 'Hoe hard mag je hier rijden?', 'Er bevindt zich een dubbele asstreep op de weg met onderbroken kantstrepen, dit betekent dat je hier maximaal 80 km/ u mag rijden. \r\n', '80 km/u', '60 km/u', '100 km/u'),
	(617, 'A', 2, '186.png', 'Mag een brommobiel op de autoweg rijden?', 'Een brommobiel mag niet op de autoweg en op de autosnelweg rijden, omdat een brommobiel niet harder mag en kan rijden dan 45 km/u.', 'NEE', 'JA', ''),
	(618, 'A', 1, 'G01.png', 'Hoe hard mag je maximaal rijden na dit bord?', 'Op de autosnelweg geldt een maximumsnelheid van 130 km/u, tenzij anders aangegeven.', '130 km/u', '120 km/u', '100 km/u'),
	(619, 'A', 2, '2d_007.png', 'Is het verstandig om de tenaamstellingscode in het dashboardkastje op te bergen?\r\n', 'Het is verstandiger de tenaamstellingscode niet in de auto te bewaren. Wanneer iemand deze code heeft is het mogelijk de auto op zijn eigen naam te laten zetten. \r\n', 'NEE', 'JA', ''),
	(621, 'B', 1, '2d_009.png', 'Hoe lang is de ‘groene’ verzekeringskaart geldig?', 'Een verzekering sluit je af voor 1 jaar, de groene kaart is daarom ook 1 jaar geldig. ', ' 1 jaar', '2 jaar', '5 jaar'),
	(622, 'A', 2, '157b.png', 'Is een sloot deel van de weg?', ' Een sloot behoort niet tot de weg. Een weg loopt van sloot tot sloot of berm tot berm.', 'NEE', 'JA', ''),
	(623, 'A', 1, '186.png', 'Welke regels volgt een brommobiel?\r\n', 'De bestuurders en passagiers van een brommobiel moeten de regels van een auto volgen tenzij dit anders bepaald is.', 'Die van een auto', 'Die van een bromfiets', ''),
	(624, 'B', 2, '186.png', 'Een gehandicapt persoon rijdt in een brommobiel. Mag deze nu op het trottoir parkeren?\r\n', 'Een brommobiel moet altijd de regels voor een brommobiel blijven volgen, welke de regels van de auto volgt, ongeacht wie er zich in de brommobiel bevindt. Ook wanneer de bestuurder gehandicapt is, dan wordt dit voertuig niet automatisch een gehandicaptenvoertuig. ', 'NEE', 'JA', ''),
	(625, 'A', 2, '299.jpg', 'Is iemand die naast zijn motorfiets loopt een bestuurder?\r\n', 'Wanneer je naast jouw fiets, bromfiets, snorfiets of motorfiets loopt, dan moet je de regels volgen van de voetganger. ', 'NEE', 'JA', ''),
	(626, 'B', 1, '185.png', 'Wanneer valt een gehandicaptenvoertuig onder de regels van een voetganger?', 'Wanneer een gehandicaptenvoertuig gebruik maakt van de stoep of het voetpad, dan moet deze de regels volgen van een voetganger. \r\n', 'Wanneer deze zich op de stoep of voetpad bevindt', 'Wanneer deze zich op de weg bevindt', ''),
	(628, 'B', 1, 'ef_317.jpg', 'Je wilt een aanhangwagen vervoeren die zwaarder is dan het lediggewicht van jouw auto. Welk rijbewijs heb je hiervoor nodig?', 'Om een aanhangwagen te mogen vervoeren die zwaarder is dan het trekkende voertuig, heb je rijbewijs BE of B+ nodig.', 'Rijbewijs BE of B+', 'Rijbewijs C1', 'Rijbewijs B'),
	(629, 'B', 1, '128.jpg', 'Vanaf welke leeftijd kun je je rijbewijs B behalen?', 'Je kunt je praktijkexamen behalen zodra je 17 jaar bent.', '17 jaar', '18 jaar', '16,5 jaar'),
	(630, 'B', 2, '2d_002.png', 'Je bent jonger dan 18 en je bent in het bezit van een to2drive rijbewijs. Je wilt gaan rijden onder begeleiding van iemand die toevallig een bevoegd rijinstructeur is. Moet diegene dan ook een begeleiderspas hebben?', 'Een begeleider van een jongere van onder de 18 jaar met een 2toDrive rijbewijs heeft een begeleiderspas nodig. Ook als de begeleider een bevoegd rijinstructeur is.', 'JA', 'NEE', ''),
	(631, 'B', 1, 'A01-80.png', 'Beginnende automobilisten kunnen het beginnersrijbewijs, naast  de bestaande straf en/of boete, kwijtraken als zij met meer dan ........ km per uur de toegestane maximumsnelheid overschrijden.', 'Als bestuurders met een beginnersrijbewijs meer dan 50 km/u te hard rijden, kunnen ze hun rijbewijs kwijtraken.', '50', '40', '60'),
	(632, 'B', 1, '2d_010a.png', 'Wat is de geldigheidsduur van het rijbewijs?', 'Wanneer je jonger dan 65 jaar bent, dan is het rijbewijs 10 jaar geldig.\r\n', '10 jaar', '5 jaar', '1 jaar'),
	(633, 'B', 2, '2d_007.png', 'Mag je aan de politie een kopie laten zien van jouw rijbewijs of kentekencard?\r\n', 'Kopieën van je rijbewijs of kentekencard zijn niet toegestaan. Je wordt geacht de originele papieren bij je te hebben. ', 'NEE', 'JA', ''),
	(634, 'B', 1, '204.png', 'Hoelang is je rijbewijs nog geldig als je 70 jaar bent?', 'Na je 75ste verjaardag heb je jaarlijks een medische keuring nodig om je rijbewijs te verlengen.', '5 jaar', '10 jaar', '1 jaar'),
	(635, 'B', 1, '2d_010a.png', 'Hoelang is het theoriecertificaat geldig?', 'Het theoriecertificaat is 1,5 jaar geldig. Binnen deze tijd moet je jouw praktijk examen gedaan hebben, anders vervalt het certificaat.', '1,5 jaar', '1 jaar', '2 jaar'),
	(636, 'B', 2, '002.png', 'Mag je al beginnen aan rijlessen zonder dat je het theoriecertificaat hebt?', 'Voor de auto mag je al beginnen met rijlessen zonder theoriecertificaat, deze heb je pas nodig bij de tussentijdse toets. Het is wel verstandig om al enige kennis van de theorie te hebben. ', 'JA', 'NEE', ''),
	(637, 'B', 1, '002.png', 'Vanaf welke leeftijd mag je het theorie-examen afleggen?', ' Je mag vanaf 16 jaar het theorie-examen doen voor het rijbewijs. ', '16 jaar', '15 jaar', '18 jaar'),
	(638, 'B', 2, '2d_002.png', 'Je hebt je rijbewijs gehaald op je 17e en wilt gaan rijden met een vriend die ook 17 is. Hij heeft ook al het rijbewijs B. Mag je zo gaan rijden?', 'Wanneer je jonger dan 18 jaar bent, mag je alleen rijden met een begeleider/ coach naast je. Deze moet o.a. ouder dan 27 jaar zijn. ', 'NEE', 'JA', ''),
	(640, 'B', 2, '2d_010b.png', 'Mag je met een rijbewijs voor een schakelauto ook in een automaat rijden?', 'Het rijbewijs voor de schakelauto is ook geldig voor de automaat. Andersom is dit niet het geval.', 'JA', 'NEE', ''),
	(641, 'B', 2, '131b.jpg', 'Je hebt je rijbewijs B. Mag je een aanhangwagen van 1750 kg vervoeren met een auto die 2000 kg weegt?', 'Met een rijbewijs B mag de combinatie van het trekkende voertuig met de aanhanger maximaal 3500 kg zijn. In het geval hierboven is het totaal 3750 kg, dat is dus te veel.\r\n', 'NEE', 'JA', ''),
	(642, 'B', 1, '7.JPG', 'Hoeveel is de toegestane maximum massa van een auto en aanhanger die je met het rijbewijs B mag besturen? ', 'Met het rijbewijs B is het toegestaan om een aanhanger te vervoeren die zwaarder is dan 750 kg, maar daarbij mag het leeggewicht en het laadvermogen van de aanhanger en de auto samen niet meer dan 3500 kg zijn. De aanhangwagen mag tevens niet zwaarder zijn dan het trekkende motorvoertuig.', '3500 kg', '1500 kg', '5000 kg'),
	(643, 'B', 1, '2d_003.png', 'Welke voertuigen hebben dit bord op de achterkant?', 'Land- en bosbouwtrekkers (inclusief hun aanhangwagens) moeten een  ‘rode retroreflector in de vorm van een driehoek’ op de achterzijde van het voertuig plaatsen.\n', 'Landbouwvoertuigen', 'Gehandicaptenvoertuigen', 'Bussen'),
	(644, 'B', 1, '119.png', 'Hoeveel personen mag je vervoeren met rijbewijs B?', 'Met het rijbewijs B mag je maximaal 8 personen vervoeren in een busje die hiervoor ingericht is (exclusief bestuurder). Is het busje ingericht voor het vervoer van meer dan 8 personen, dan moet je rijbewijs D hebben. ', '8 personen', '12 personen', '4 personen'),
	(645, 'B', 1, '186.png', 'Welk rijbewijs heb je minimaal nodig om een brommobiel te mogen besturen?', 'Rijbewijs AM heb je nodig om in een brommobiel te mogen rijden. Echter mag je je ook een brommobiel rijden met rijbewijs A en B.', 'Rijbewijs AM', 'Rijbewijs A', 'Rijbewijs B'),
	(646, 'B', 2, '2d_010b.png', 'Mag je met rijbewijs B een snorfiets, bromfiets of scootmobiel besturen?', 'Wanneer je in het bezit bent van rijbewijs B mag je een snorfiets en bromfiets besturen. Heb je deze niet, dan heb je het rijbewijs AM nodig. \r\n', 'JA', 'NEE', ''),
	(648, 'C', 1, '174g.png', 'Je wilt een lange ondeelbare lading bovenop jouw auto vervoeren, hoe ver mag die lading aan de voorkant uitsteken? \r\n', 'Alleen ondeelbare ladingen mogen aan de voorkant uitsteken, maar maximaal 1 meter.\r\n', '1 meter', '5 meter', '10 cm'),
	(650, 'C', 2, '254.png', 'De lading steekt 40cm uit aan beide zijkanten, mag je zo rijden?\r\n', 'Een lading van een auto mag nooit meer dan 20 cm aan de zijkanten uitsteken, daarbij moet de lading bij het uitsteken van 10 cm of meer, gemarkeerd worden.', 'NEE', 'JA', ''),
	(651, 'C', 1, '174h.png', 'Hoe ver mag ondeelbare lading uitsteken vanaf de achterste as van de auto?\r\n', 'Ondeelbare lading bv. een ladder, mag maximaal 5 meter uitsteken vanaf de achterste as van het voertuig. Lading die meer dan 1 meter uitsteekt moet voorzien zijn van een vierkant bord van 50 x 50 cm met rode en witte strepen.', '5 meter', '1 meter', '3 meter'),
	(653, 'C', 2, '151.jpg', 'Je rijdt met een aanhangwagen. Heeft dit effect op de zichtbaarheid van de lichten van jouw auto?\r\n', 'Een aanhangwagen kan het zicht op de achterlichten van jouw auto blokkeren. Daarom is het verplicht om ook licht te voeren op de aanhangwagen zelf.', 'JA', 'NEE', ''),
	(654, 'C', 2, '123.jpg', 'De lading steekt meer dan 1 meter uit en is gemarkeerd met rode vlaggetjes. Mag dit?\r\n', 'Wanneer er is gemarkeerd met iets anders dan het officiële markeringsbord, dan is het per definitie fout. \r\n', 'NEE', 'JA', ''),
	(655, 'C', 2, '243c.png', 'Deelbare lading steekt uit aan de voorkant van jouw auto, mag dat?\r\n', 'Deelbare lading mag nooit voor het voertuig uitsteken.\r\n', 'NEE', 'JA', ''),
	(656, 'C', 1, 'ef_301.png', 'Waar herken je het markeringsbord van de lading aan?\r\n', 'Het officiële markeringsbord heeft diagonale rode en witte strepen en is 42 bij 42 centimeter groot.\r\n', 'Rode strepen', 'Rode vlaggen', 'Zwarte strepen'),
	(657, 'C', 1, '131b.jpg', 'Wat bepaalt of je een aanhangwagen mag trekken?', 'De combinatie van het gewicht van de aanhangwagen en van de auto bepaalt welk rijbewijs je nodig hebt om ze te vervoeren. \r\n', 'Beide', 'Het gewicht van de auto', 'De lading van de aanhangwagen'),
	(658, 'C', 2, '174h.png', 'De lading steekt 75 cm uit aan de achterkant van jouw aanhangwagen. Moet je deze markeren?', 'Markeren hoeft pas wanneer de lading meer dan 1 meter uitsteekt aan de achterkant van de aanhangwagen, mits er geen scherpe delen uitsteken.\r\n', 'NEE', 'JA', ''),
	(659, 'C', 2, '184.jpg', 'Mag de auto met aanhanger hier rijden op de 3e rijstrook?', 'Wanneer je gaat voorsorteren mag je op de 3e rijstrook rijden. Zodra er meerdere rijstroken zijn, moet je weer zoveel mogelijk rechts houden.\r\n', 'JA', 'NEE', ''),
	(661, 'C', 2, '131.jpg', 'Mogen auto’s of auto’s met aanhanger die samen langer zijn dan 7 meter gebruik maken van de meest linkerrijstrook op de autosnelweg? ', 'Van de derde rijstrook mag geen gebruik gemaakt worden door vrachtauto’s en motorvoertuigen met een aanhangwagen die langer zijn dan 7 meter. Een uitzondering hierop is wanneer je gaat voorsorteren.', 'NEE', 'JA', ''),
	(662, 'D', 1, 'ef_284.JPG', 'Wat is de functie van de toerenteller?\r\n', 'De toerenteller informeert jou op hoeveel toeren de motor draait, je schakelt op naar de volgende versnelling wanneer de teller zich ongeveer tussen de 2000 en 3000 toeren bevindt. De snelheidsmeter geeft jouw snelheid aan. \r\n', 'Zo weet je wanneer je van versnelling moet wisselen', 'Zo weet je hoe hard je rijdt', ''),
	(664, 'C', 2, '122.jpg', 'De aanhanger is 3 meter lang. De ondeelbare lading is gemarkeerd met dit bord en steekt 1,20 meter uit. Mag dit?  \r\n', 'Je mag alleen dit officiële markeringsbord gebruiken wanneer je lading vervoert die meer dan 1 meter uitsteekt aan de achterkant van de auto of aanhanger.', 'JA', 'NEE', ''),
	(665, 'C', 1, '172.png', 'Vanaf hoeveel kilo moet een aanhanger een eigen kentekenplaat hebben?', 'Wanneer de aanhanger zwaarder is dan 750 kg, dient deze een eigen kentekenplaat te hebben, dus een andere kentekenplaat dan de auto. \r\n', 'Vanaf 750 kg', 'Vanaf 500 kg', 'Altijd'),
	(666, 'C', 2, '196a.png', 'Moet een fietsendrager een kentekenplaat hebben?\r\n', 'Een fietsendrager moet ook een (witte) kentekenplaat hebben (kopie van de auto). Het kenteken van de auto is anders niet goed zichtbaar.\r\n', 'JA', 'NEE', ''),
	(667, 'C', 1, 'ef_065.JPG', 'Welke aanhanger weegt minder dan 750 kg en heeft de kentekenplaat van de auto?\r\n', 'De aanhanger of met de witte kentekenplaat is een kopie van de gele kentekenplaat van de auto. De gele kentekenplaat op de aanhanger is een ander nummer dan de kentekenplaat van de auto, dit betekent dus dat deze meer dan 750 kilo weegt. \r\n', 'Witte kentekenplaat', 'Gele kentekenplaat', ''),
	(668, 'C', 1, '254.png', 'Lading mag niet meer dan …. cm uitsteken aan de zijkanten\r\n', 'Lading mag maximaal 20 cm aan beide kanten van de auto uitsteken. Wanneer deze meer dan 10 cm aan de zijkant uitsteekt, dan moet deze gemarkeerd worden met het markeringsbord, niet met vlaggetjes!', '20 cm', '10 cm', '40 cm'),
	(669, 'C', 2, 'ef_253.JPG', 'Je hebt een oude auto die oorspronkelijk geen rechterbuitenspiegel heeft. Je wilt zonder deze rechterbuitenspiegel rijden, mag dat?\r\n', 'De binnenspiegel en de linkerbuitenspiegel zijn verplicht. Zonder de rechterbuitenspiegel mag je wel rijden wanneer het een auto betreft met een bouwjaar van voor 2010.\r\n', 'JA', 'NEE', ''),
	(670, 'C', 2, '266.png', 'Je hebt een nieuwe auto gekocht. Je wilt zonder de rechterbuitenspiegel rijden, mag dat?', 'Auto’s vanaf 2010 moeten een rechterbuitenspiegel hebben.\n', 'NEE', 'JA', ''),
	(671, 'C', 2, '7.JPG', 'Je wilt gaan rijden zonder een kentekenplaat op jouw aanhanger, mag dat? \r\n', 'Het is wettelijk verplicht een kentekenplaat op de aanhangwagen te plaatsen. \r\n', 'NEE', 'JA', ''),
	(673, 'D', 2, 'ef_157.JPG', 'De kilometerteller is kapot. Mag je zo gaan rijden?', 'De kilometerteller hoeft niet perse te werken. De snelheidsmeter uiteraard wel. ', 'JA', 'NEE', ''),
	(675, 'C', 2, 'ef_317.jpg', 'Mag je met rijbewijs B een aanhanger trekken?\r\n', 'Met rijbewijs B mag je een aanhanger tot 750kg trekken. Wanneer de aanhanger zwaarder is dan 750kg, is het totale maximum toegestane gewicht van de combinatie auto en aanhanger 3500kg. ', 'JA', 'NEE', ''),
	(676, 'C', 1, 'ef_295.jpg', 'Welke aanhangers mag je trekken met het rijbewijs B?\r\n', 'Wanneer de combinatie van auto en aanhanger meer is dan 3500kg heb je rijbewijs B+ of BE nodig. ', 'Sommige aanhangers', 'Alle aanhangers', ''),
	(677, 'C', 2, '184.jpg', 'De auto en aanhanger zijn samen 6.90m lang. Mag je gebruik maken van de meest linkerrijstrook?', 'Is de combinatie van auto en aanhanger minder dan 7 meter, dan mag deze gebruik maken van alle rijstroken. ', 'JA', 'NEE', ''),
	(678, 'C', 2, '175.png', 'De rechterbuitenspiegel is beschadigd. Mag je zo gaan rijden? ', 'Je mag niet met een beschadigde spiegel rijden. \n', 'NEE', 'JA', ''),
	(679, 'C', 2, '151.jpg', 'Moet een aanhangwagen ook verlichting voeren?', 'Een aanhangwagen is verplicht verlichting te voeren. ', 'JA', 'NEE', ''),
	(680, 'D', 1, 's_002c.png', 'Wat doe je als het waarschuwingslampje van de motor rood oplicht?', 'Rood betekent stop, oranje betekend indien mogelijk stoppen(zo snel mogelijk naar de garage)', 'Direct stoppen', 'Doorrijden naar de garage', 'Naar de benzinepomp'),
	(689, 'D', 2, '2d_011.png', 'Moet een auto APK gekeurd zijn wanneer je ermee de weg op gaat?\n', 'Wanneer het tijd is om een auto APK te laten keuren, ben je verplicht dat te doen. Een auto moet altijd APK gekeurd zijn voordat je ermee op de openbare weg gaat rijden.', 'JA', 'NEE', ''),
	(690, 'D', 1, '2d_013c.png', 'Wanneer is het verstandig om je dimlicht aan te zetten?', 'Het is altijd verstandig om je dimlicht aan te hebben. Tussen zonsondergang en zonsopgang is het verplicht, maar het is verstandig om ook overdag de dimlichten aan te hebben.\r\n', 'Altijd', 'Met slecht weer', 'Wanneer er minder daglicht is vanwege bomen langs de weg'),
	(691, 'D', 1, '178.jpg', 'Welke kleur heeft het symbool voor het voeren van groot licht? ', 'Het symbool voor groot licht in het dashboard van een auto heeft een blauwe kleur.', 'Blauw', 'Groen', 'Wit'),
	(692, 'D', 1, 'ef_219.JPG', 'Wat is de minimale profieldiepte van een winterband?', 'De minimale profieldiepte van een winterband moet 4mm zijn. Deze is dieper dan een gewone band, omdat je met slecht weer extra grip nodig hebt op het wegdek.', '4 mm', '1,6 mm', '2 mm'),
	(693, 'D', 1, 'ef_217.JPG', 'Wat voor band is dit?', ' M&amp;S staat voor ‘Modder &amp; sneeuw’. Dit zegt niks over specifieke zomer- of wintereigenschappen van de band. De fabrikant mag bepalen op welke band hij het M&amp;S logo plaatst, dus het kan een winter-, zomer- of een all season band zijn. \r\n', 'Zomer en winter', 'Zomer band', 'Winter band'),
	(694, 'D', 2, 's_21.png', 'Zorgt ABS er in het algemeen voor dat de remafstand korter wordt?', 'ABS kan ervoor zorgen dat de remafstand korter wordt, doordat het systeem je helpt om het voertuig onder controle te houden, wanneer je bv. remt tijdens een bocht. \r\n', 'JA', 'NEE', ''),
	(695, 'D', 2, 'ef_283.JPG', 'Moet de snelheidsmeter verlicht zijn in het donker?\r\n', 'Je moet altijd de snelheidsmeter kunnen aflezen. ', 'JA', 'NEE', ''),
	(696, 'D', 2, '247.png', 'Zijn winterbanden verplicht in Nederland?', 'In Nederland ben je niet verplicht om tijdens de wintermaanden daadwerkelijk winterbanden onder jouw voertuig te hebben.', 'NEE', 'JA', ''),
	(697, 'D', 2, 's_019.png', 'Betekent dit lampje dat de airbag aanstaat?\r\n', 'Dit betekent dat er zich een storing bevindt in het airbagsysteem. Indien het lampje oranje is, staat de airbag uit', 'NEE', 'JA', ''),
	(698, 'D', 1, '2d_015.png', 'Je hebt een oude auto die op benzine rijdt. Hoe vaak moet je deze APK laten keuren?', 'Een oude benzineauto (ouder dan 8 jaar) moet je elk jaar APK laten keuren.\n', 'Elk jaar', 'Elke twee jaar ', 'Elke 5 jaar'),
	(700, 'D', 1, 's_017.png', 'Als je aan het rijden bent, verschijnt dit symbool op je dashboard. Wat betekent dit? ', 'Wanneer dit symbool rood is, dan geeft dit aan dat het oliepeil te laag is. Het is daarom raadzaam om de motorolie zo snel mogelijk bij te vullen, omdat dit anders schade kan veroorzaken. ', 'Olie bijvullen', 'Benzine bijvullen', 'Antivries bijvullen'),
	(701, 'D', 1, 's_001c.png', 'Als je aan het rijden bent, verschijnt dit symbool op je dashboard. Wat betekent dit? \r\n', 'Wanneer dit symbool rood brandt, kan er iets mis zijn met bv. de dynamo. Laat dit zo snel mogelijk controleren of koop vast startkabels, je gaat ze nodig hebben!', 'De accu laadt niet op', 'Elektrisch probleem in de motor van de auto', 'Probleem met de lichten'),
	(703, 'D', 1, 'ef_270.JPG', 'Na hoeveel jaar moet een hybride auto voor het eerst APK gekeurd worden?', 'Een hybride auto, moet net zoals een auto op benzine, APK gekeurd worden binnen de eerste 4 jaar. ', '4 jaar', '1 jaar', '3 jaar'),
	(704, 'D', 1, 'ef_202.JPG', 'Na hoeveel jaar moet een diesel of een auto op gas voor het eerst APK gekeurd worden?\r\n', 'Na 3 jaar moet een auto op diesel of gas voor het eerst worden gekeurd en daarna elk jaar.', '3 jaar', '1 jaar', '4 jaar'),
	(705, 'D', 1, 'ef_177.JPG', 'Na hoeveel jaar moet een elektrische auto voor het eerst APK gekeurd worden?\r\n', 'Na 4 jaar moet een elektrische auto voor het eerst worden gekeurd, daarna in jaar 6 en in jaar 8 jaar en als de auto 8 jaar of ouder is dan moet deze elk jaar gekeurd worden.', '4 jaar', '1 jaar', '3 jaar'),
	(706, 'D', 1, 's_21.png', 'Wat doet een ABS systeem?', 'ABS zorgt door middel van gefaseerd remmen dat het voertuig minder snel de grip met het wegdek verliest.\r\n', 'ABS zorgt door middel van gefaseerd remmen dat de wielen niet blokkeren en remmen in de bocht mogelijk is.', 'ABS zorgt voor een juiste verdeling van de rembalans over de twee wielen. Hierdoor kan er beter worden geremd in de bochten', ''),
	(707, 'D', 1, '247.png', 'Welk type band is dit? ', 'De winterband kun je herkennen aan het sneeuwvlok symbool. ', 'Winterband', 'Zomerband', '4 seizoenen band'),
	(708, 'D', 1, 'ef_214.JPG', 'Waartoe dienen de groene en blauwe strepen op een nieuwe band? ', 'Zodra er met de banden gereden wordt verdwijnen de kleurstrepen van de band en weet je dat de band eerder is gebruikt.', 'Om te laten zien dat de band nieuw is', 'Om de draairichting van de band aan te geven', 'Om de linker of rechterkant duidelijk te maken voor de garage'),
	(709, 'H', 1, 'ef_178.JPG', 'Welk type band is dit? ', 'Dit is een 4 seizoenen band/ all season band. Dat staat er op met grote letters.\r\n', '4 seizoenen band', 'Winterband', 'Zomerband'),
	(710, 'A', 1, 'ef_195.JPG', 'Wanneer kun je het beste het oliepeil controleren?', 'Het oliepeil kun je het beste controleren voordat je gaat rijden en/ of als de motor dus een tijdje uitgeschakeld is geweest en de olie in rust is, in plaats van dat het nog door de leidingen loopt.\r\n', 'Voordat je gaat rijden', 'Dit maakt niet uit wanneer', 'Na het rijden'),
	(711, 'D', 1, 's_029.png', 'Welk symbool betekent koelvloeistof?', 'Symbool B betekent dat er iets mis is met het koelsysteem van de auto, of de koelvloeistof bijgevuld moet worden. \r\n', 'Symbool B', 'Symbool A', 'Geen van beiden'),
	(712, 'D', 2, 'ef_216.JPG', 'Heeft de profieldiepte van een band effect op eventuele aquaplaning?\n', 'Hoe dieper het profiel van de banden hoe kleiner de kans op aquaplaning.', 'JA', 'NEE', ''),
	(721, 'D', 2, 'ef_066.JPG', 'Je wilt gaan rijden zonder een kentekenplaat op jouw aanhanger, mag dat? \r\n', 'Het is wettelijk verplicht een kentekenplaat op de aanhangwagen te plaatsen. \r\n', 'NEE', 'JA', ''),
	(722, 'E', 2, 'ef_181.JPG', 'Moet je altijd een gordel dragen wanneer je in een auto rijdt?', 'Het is te allen tijde verplicht om een gordel te dragen in een auto. \r\n', 'JA', 'NEE', ''),
	(723, 'E', 1, '188b.png', 'Welke hoofdsteun is juist afgesteld? ', 'De hoofdsteun moet zich bevinden op gelijke hoogte met jouw hoofd, om zo nodig bescherming te bieden, wanneer je bv. plots moet remmen. \r\n', 'Hoofdsteun B', 'Hoofdsteun A', 'Hoofdsteun C'),
	(724, 'E', 2, 'ef_232.JPG', 'Is het raadzaam om de airbag uit te schakelen wanneer je een kind onder de 1.35m voorin vervoert?', 'Het is niet aan te raden om de airbag uit te schakelen wanneer er kinderen op de passagiersplaats voorin zitten tenzij ze in een kinderzitje naar voren gericht zitten.', 'NEE', 'JA', ''),
	(725, 'E', 2, 'ef_001a.jpg', 'Een kind is 2 jaar oud. Heeft deze een kinderzitje nodig?', 'Wanneer een kind 2 jaar oud is, is deze kleiner dan 1.35 meter, daarom moet het kind in een goedgekeurd kinderzitje worden gezet. ', 'JA', 'NEE', ''),
	(726, 'E', 2, 'ef_001ai.jpg', 'Een kind is 1,50 meter en draagt een driepuntsgordel, mag dit?', 'Wanneer een kind groter is dan 1,35 meter dan heeft deze geen kinderzitje meer nodig en is de driepuntsgordel voldoende. \r\n', 'JA', 'NEE', ''),
	(727, 'E', 2, 'Frankvoorin.jpg', 'Mag je jouw baby zo vervoeren? ', 'Nee dit mag niet. Vervoer een baby nooit op schoot. Bij een eventuele botsing kun je de veiligheid van het kind namelijk niet garanderen. Vervoer een baby altijd in een daarvoor geschikt en goedgekeurd kinderzitje dat vast is gezet met een driepuntsgordel.  \n', 'NEE', 'JA', ''),
	(728, 'E', 2, '246.png', 'Moet je als passagier in een taxi een gordel dragen?', 'Als passagier in een taxi ben je altijd verplicht om een driepuntsgordel te dragen. Alleen voor de chauffeur van een taxi geldt een uitzondering als hij betalende passagiers vervoert.', 'JA', 'NEE', ''),
	(729, 'E', 2, 'ef_226.JPG', 'Mag je een driepuntsgordel als heupgordel gebruiken (door het schuine gedeelte achter de rug te dragen)?', 'Nee, het schuine gedeelte van de driepuntsgordel moet voor het lichaam langs en over de schouder lopen.\n', 'NEE', 'JA', ''),
	(731, 'E', 2, 'ef_001ai.jpg', 'Mag een kind een heupgordel dragen?', 'Een heupgordel biedt geen steun, wanneer er bv. plots geremd wordt. Een driepuntsgordel is verplicht. ', 'NEE', 'JA', ''),
	(732, 'E', 2, '264.png', 'Is een airbag verplicht?\r\n', 'De meeste nieuwe auto’s hebben ze echter wel. ', 'NEE', 'JA', ''),
	(733, 'E', 2, 'ef_181.JPG', 'Mag een volwassene een heupgordel dragen?', 'Een heupgordel biedt geen steun wanneer er bijvoorbeeld plots geremd wordt. Dit mag dus niet: een driepuntsgordel is verplicht.  ', 'NEE', 'JA', ''),
	(734, 'E', 2, '246.png', 'Moeten taxichauffeurs ook een autogordel dragen?\r\n', 'Alleen als zij betalende passagiers vervoeren, hebben taxichauffeurs deze verplichting niet. \n', 'JA', 'NEE', ''),
	(735, 'E', 2, 'ef_182.JPG', 'Is een gordel die pluist toegestaan?\r\n', 'Een gordel die pluist is officieel niet beschadigd.', 'JA', 'NEE', ''),
	(736, 'E', 2, 'ef_001e.jpg', 'Een kind is kleiner dan 1,35 meter en draagt een heupgordel, mag dit?\r\n', 'Een heupgordel is niet toegestaan en wanneer het kind kleiner is dan 1,35 meter. Deze moet dan van een goedgekeurd kinderstoeltje of zitje gebruikmaken. ', 'NEE', 'JA', ''),
	(737, 'E', 2, '030.jpg', 'Moet je in nieuwere touringcars een gordel dragen als die aanwezig zijn?\r\n', 'Als de gordels aanwezig zijn, ben je verplicht die te dragen', 'JA', 'NEE', ''),
	(738, 'E', 2, 'ef_182.JPG', 'Mag je zitten op een zitplaats zonder gordel?\r\n', 'Je bent te allen tijde verplicht een gordel te dragen wanneer je je in een voertuig bevindt. Een plek geldt alleen als officiële zitplaats als het ook ingericht is met een driepuntsgordel.', 'NEE', 'JA', ''),
	(739, 'E', 2, 'efvkr_031.jpg', 'Moet een bedrijfsauto ook zitplaatsen hebben met gordels?', 'Een plek geldt alleen als officiële zitplaats als het ook ingericht is met een driepuntsgordel.', 'JA', 'NEE', ''),
	(740, 'E', 2, '263b.jpg', 'Je vervoert je baby in een goedgekeurd autostoeltje, geplaatst in de tegengestelde richting van het verkeer en schakelt de airbag uit. Is dit veilig?\r\n', 'Wanneer je de airbag uitschakelt, mag je zo jouw baby vervoeren. \r\n', 'JA', 'NEE', ''),
	(741, 'E', 2, 'ef_001b.jpg', 'Je vervoert je baby in een goedgekeurd autostoeltje, geplaatst in de richting van het verkeer en zet de airbag aan. Is dit veilig?\r\n', 'Het is levensgevaarlijk om in deze situatie de airbag ingeschakeld te hebben. De baby zal de klap van de airbag vaak niet overleven. Het is veilig om de baby in deze positie te vervoeren, maar dan moet de airbag uit staan. ', 'NEE', 'JA', ''),
	(742, 'E', 2, 'ef_001ai.jpg', 'Is een kind onder de 12 jaar aansprakelijk voor zijn eigen veiligheid?\r\n', 'Bestuurders zijn aansprakelijk voor kinderen onder de 12 jaar om hen op een veilige manier te vervoeren.', 'NEE', 'JA', ''),
	(743, 'E', 2, 'ef_001f.jpg', 'Een kind is groter dan 1,35 meter en draagt een driepuntsgordel, mag dat?\r\n', 'Het is verplicht om een driepuntsgordel te dragen. Een kinderzitje is niet meer nodig, omdat het kind groter is dan 1,35 meter. ', 'JA', 'NEE', ''),
	(744, 'E', 2, 'ef_345.jpg', 'Een zwangere vrouw draagt een driepuntsgordel, is dit verstandig?', 'Ook als iemand zwanger is, is diegene verplicht een driepuntsgordel te dragen. Het is dan wel belangrijk om de gordel onder de buik langs te laten lopen', 'JA', 'NEE', ''),
	(745, 'E', 2, 'ef_182.JPG', 'Mag een kind zonder gordel op de achterbank zitten wanneer er 2 volwassenen voorin zitten?\r\n', 'Een kind moet altijd een gordel dragen ook al zitten er volwassenen bij. \r\n', 'NEE', 'JA', ''),
	(746, 'F', 2, 'efvkr_253.jpg', 'Moet je de motor uitschakelen als je weet dat je voor langere tijd stil zal staan (bv. bij een open brug)?\n', 'Het is beter voor het milieu en bespaart brandstof als je de motor uit zet bij een zichtbare stop die langer duurt dan 20 seconden. Bijvoorbeeld bij een open brug of spoorwegovergang.', 'JA', 'NEE', ''),
	(748, 'F', 2, 'ef_142.JPG', 'Je staat te wachten bij een spoorwegovergang. Schakel je de motor uit?', 'Het duurt vaak even voordat de trein voorbij is gekomen en het spoor weer wordt vrijgegeven. Bij een stop van 20 seconden of meer is het al zuiniger en dus beter voor het milieu om de motor uit te schakelen.', 'JA', 'NEE', ''),
	(749, 'F', 1, '104.jpg', 'Hoeveel seconde(n) is een veilige volgafstand?', 'Een veilige volgafstand is minimaal 2 seconden. Hoeveel dat in meters is, hangt af van de snelheid die je op dat moment hebt. De vuistregel is snelheid gedeeld door 2 plus 10%.', '2 seconden', '1 seconde', '5 seconden'),
	(750, 'F', 1, 'efvkr_057.jpg', 'Wanneer rijd je defensief?', 'Wanneer je defensief rijdt, houd je rekening met fouten die anderen zouden kunnen maken en je let op jouw eigen gedrag!', 'Als ik anticipeer op andermans fouten', 'Als ik gebruik maak van andermans fouten', ''),
	(751, 'F', 1, '244.png', 'Wat bespaart de meeste brandstof tijdens het rijden?', 'Door te rijden met de cruise control aan, blijf je op een constante snelheid rijden. Dit bespaart brandstof, omdat je niet remt of gas bijgeeft. ', 'Rijden met de cruise control aan', 'Rijden met de airconditioning aan', 'Rijden met de navigatie aan'),
	(753, 'F', 1, 'ef_170.JPG', 'Wat verbruikt de meeste brandstof tijdens het rijden?\r\n', 'Airconditioning is een grootverbruiker van brandstof. \r\n', 'Rijden met de airconditioning aan', 'Rijden met de cruise control aan', 'Rijden met de navigatie aan'),
	(754, 'F', 1, '160g.png', 'Je bandenspanning is te laag, wanneer verbruik je meer brandstof?\r\n', 'Wanneer je bandenspanning incorrect is, verbruik je altijd meer brandstof, omdat je wegligging niet optimaal is. ', 'In beide situaties', 'Wanneer je hard rijdt', 'Wanneer je zacht rijdt'),
	(755, 'F', 2, 'ef_260.JPG', 'Is het goed voor het milieu om de motor warm te laten draaien in de winter?', 'Het is niet goed voor het milieu om eerst de motor warm te laten draaien, je kunt beter de auto starten en rustig wegrijden, zo bespaar je ook brandstof.', 'NEE', 'JA', ''),
	(756, 'F', 1, 'ef_280.JPG', 'Hoe kun je brandstof besparen?', 'Door bij een laag toerental alvast op te schakelen, blijft het toerental constant laag waardoor je minder brandstof verbruikt in verhouding tot het rijden in een hoog toerental.\r\n', 'Bij een laag toerental opschakelen', ' Bij een hoog toerental opschakelen', ''),
	(757, 'F', 2, 'efvkr_159.jpg', 'Bespaar je brandstof wanneer je laat remt en snel accelereert?', 'Om brandstof te besparen kun je beter anticiperen op wat er komen gaat zodat je vroeg je gas los kunt laten en daarna weer rustig je snelheid op kunt voeren. ', 'NEE', 'JA', ''),
	(758, 'F', 1, '124.jpg', 'Hoe kun je filevorming voorkomen als gevolg van spookfiles?', 'Een spookfile ontstaat bijvoorbeeld doordat een persoon voor je remt, die daarachter remt nog harder etc. Het verkeer daarachter moet daardoor ook weer afremmen. Deze file kan zich verschuiven. Vaak onstaan deze in tunnels.', 'Afstand houden en geleidelijk remmen', 'Goed op de borden letten ', 'Je kunt niks doen om die te voorkomen'),
	(759, 'F', 2, 'ef_175.JPG', 'Als je meer dan een minuut moet wachten in de auto, schakel je dan de motor uit?\n', 'Wanneer je langdurig moet wachten, is het verstandiger de motor uit te schakelen. Zo bespaar je brandstof, tevens is dit beter voor het milieu. ', 'JA', 'NEE', ''),
	(760, 'F', 2, '292.jpg', 'Je hebt een skibox bovenop jouw auto, verbruik je nu meer brandstof?', 'Jouw auto is op deze manier zwaarder en minder aerodynamisch, waardoor die meer brandstof verbruikt dan zonder skibox.', 'JA', 'NEE', ''),
	(761, 'F', 1, 'efvkr_031.jpg', 'Wanneer bespaar je de minste brandstof?', 'Door hard te rijden en laat op te schakelen, rijdt je met een hoog toerental en verbruik je dus veel brandstof. Let op: als je het minst bespaart, verbruik je dus het meest.', 'Als je hard rijdt en laat opschakelt', 'Als je hard rijdt en vroeg schakelt', 'Wanneer je rustig rijdt en vroeg opschakelt'),
	(762, 'F', 1, 'efvkr_010.jpg', 'Wanneer bespaar je de meeste brandstof?', 'Door rustig te rijden en vroeg op te schakelen, houd je de toeren laag en verbruik je dus het minste brandstof. ', 'Wanneer je rustig rijdt en vroeg opschakelt', 'Als je hard rijdt en vroeg schakelt', 'Als je hard rijdt en laat opschakelt'),
	(763, 'F', 1, 'ef_187.JPG', 'Wanneer bespaar je de meeste brandstof?\r\n', 'Het bespaart geen brandstof om de motor eerst warm te laten draaien, voordat je wegrijdt, dit is alleen maar verspilling van brandstof.', 'Wanneer je meteen wegrijdt na het starten van de auto', 'Wanneer je de motor voordat je wegrijdt eerst enkele minuten warm laat draaien', ''),
	(764, 'F', 1, 'ef_180.JPG', 'Wat is het gevolg van een lage bandenspanning?', 'Door een te lage bandenspanning ontstaat er meer weerstand tussen de banden en het wegdek, waardoor het brandstofverbruik hoger ligt.', 'Meer brandstofverbruik', 'Minder brandstofverbruik', 'Geen gevolgen'),
	(765, 'F', 2, 'ef_208.JPG', 'Moeten nieuwe autobanden een EU-bandenlabel hebben?', 'Nieuwe autobanden moeten sinds november 2012 voorzien zijn van een bandenlabel, zodat er gemakkelijk gezien kan worden of de banden zuinig en veilig zijn. ', 'JA', 'NEE', ''),
	(766, 'F', 1, '2d_005.png', 'Wat betekent een rood energielabel?', 'Een energielabel geeft aan hoe zuinig een auto rijdt, waarbij rood niet zuinig is en groen wel zuinig. ', 'De auto rijdt niet zuinig', 'De auto is afgekeurd', ''),
	(767, 'F', 2, 'efvkr_039.jpg', 'Is anticiperen over het algemeen voordelig voor het brandstofverbruik?', 'Door te anticiperen kan je vaak eerder je gas loslaten en weer rustig optrekken in plaats van op het laatste moment flink te remmen en weer hard weg te rijden. Dat bespaart brandstof.', 'JA', 'NEE', ''),
	(768, 'G', 1, '2d_010a.png', 'Wanneer wordt het beginnersrijbewijs ongeldig verklaard?', 'Als je weigert mee te werken aan het alcoholonderzoek wordt je rijbewijs direct ongeldig verklaard. Bij antwoord B en C krijg je eerst een schorsing en moet je een proef dan bij het CBR. Als je voor de proef zakt dan wordt het rijbewijs ongeldig verklaard en moet het rijexamen opnieuw afgelegd worden.', 'Als je weigert om mee te werken aan het alcoholonderzoek', 'Als je binnen 5 jaar meer dan 3 maal door rood licht bent gereden', 'Als je meer dan 50 km te hard hebt gereden'),
	(769, 'G', 2, 'ef_340.jpg', 'Is het effect van medicijnen altijd merkbaar?', 'Als je last hebt van slaperigheid of moeite met concentreren, slecht ziet, of je duizelig voelt, kun je beter geen auto (gaan) rijden. Soms kan je door je medicijnen sneller geïrriteerd raken of onverschillig worden. Medicijnen kunnen daarnaast ervoor zorgen dat je minder controle hebt over je arm- en beenspieren. Als je twijfelt, overleg dan met je arts of apotheker. In sommige gevallen is het mogelijk een ander medicijn te gaan gebruiken dat minder invloed heeft op de rijvaardigheid.', 'NEE', 'JA', ''),
	(770, 'G', 1, '149.jpg', 'Wat is de gemiddelde reactietijd van een mens?', 'Als iemand niet moe of afgeleid is, is zijn of haar reactietijd gemiddeld 1 seconde.', '1 seconde', '2 secondes', 'Een halve seconde'),
	(771, 'G', 1, 'ef_343.jpg', 'Hoeveel alcohol is maximaal toegestaan bij een beginnend bestuurder?\r\n', 'Een beginnend bestuurder mag maximaal 0,2 promille alcohol in zijn of haar bloed hebben tijdens het rijden. Dit staat gelijk aan 88 µg/l in de adem. ', '0,2 promille', '0,1 promille', '0,5 promille'),
	(772, 'G', 1, '107.jpg', 'Welk van de onderstaande legt het CBR je op wanneer je teveel alcohol (ca. 0,8 promille) hebt gehad terwijl je reed?', 'Wordt je betrapt met 0,8 promille alcohol in jouw bloed terwijl je rijdt, dan moet je verplicht een cursus volgen. Voor ervaren bestuurders komt dit neer op een LEMA cursus en beginnende bestuurders kunnen in plaats van een LEMA ook een EMA opgelegd krijgen.', 'LEMA', 'Onderzoek', 'Alcoholslot'),
	(773, 'G', 1, '028.png', 'Hoelang kan de politie jou vasthouden wanneer je rijdt onder invloed van alcohol?\n', 'De politie kan je 6 uur lang vasthouden wanneer je wordt betrapt op het rijden onder invloed.', '6 uur', '24 uur', '2 uur'),
	(774, 'G', 1, 'ef_341.jpg', 'Wat kun je doen om alcohol sneller uit je bloed te krijgen?', 'Je kunt niks doen om alcohol sneller uit je bloed te krijgen. Let dus goed op de alcohollimiet. \r\n', 'Niks', 'Koffie drinken', 'Veel water drinken'),
	(777, 'G', 1, 'efvkr_247.jpg', 'Welke invloed heeft vermoeidheid op de concentratie van de bestuurder?', 'Vermoeidheid heeft een negatief effect op je wanneer je achter het stuur zit. Het is belangrijk om goed in de gaten te houden of je nog wel door kunt rijden.\r\n', 'De concentratie vermindert', 'De concentratie verbetert', 'Geen'),
	(778, 'G', 2, 'ef_341.jpg', 'Pleegt een beginnend bestuurder een misdrijf wanneer deze een motorvoertuig bestuurt, met een adem-alcoholgehalte hoger dan 0,2 promille?', 'Een adem-alcoholgehalte hoger dan 0,2 promille wordt bij een beginnend bestuurder als een misdrijf gezien. ', 'JA', 'NEE', ''),
	(779, 'G', 1, '2d_010a.png', 'Jouw rijbewijs is ongeldig verklaard, wat mag je doen?', 'Wanneer je rijbewijs ongeldig is verklaard, heb je geen rijbewijs meer dus je moet weer opnieuw je rijbewijs halen, dus lessen nemen.', 'Je mag rijlessen nemen ', 'Je mag geen rijlessen nemen en niet rijden', ''),
	(781, 'G', 2, '028.png', 'Kan de politie eisen dat je getest wordt als zij jou verdenken van drugs- en/of alcoholgebruik?', 'Wanneer de politie jou verdenkt van het gebruik van een verdachte ‘stof’, dan mogen ze jou staande houden, net zoals bij alcoholgebruik.', 'JA', 'NEE', ''),
	(782, 'G', 1, '2d_010a.png', 'Vanaf welk promillage kunnen ze jouw rijbewijs invorderen?\n', 'Vanaf 0,8 promille kan een beginnersrijbewijs ter plekke ingevorderd worden.', '0,8 promille', '0,5 promille', '0,2 promille'),
	(783, 'G', 1, 'ef_342.jpg', 'Wat is de maximale toegestane promillage voor een ervaren bestuurder om in zijn bloed te hebben?\r\n', 'De maximale toegestane promillage voor ervaren bestuurders is 0,5 promille. Bij een blaastest komt dit neer op 220 µg alcohol per liter uitgeblazen adem.', '0,5 promille', '0,2 promille', '0,8 promille'),
	(784, 'G', 1, 'ef_343.jpg', 'Wat is de maximale toegestane promillage voor een beginnend bestuurder om in zijn bloed te hebben?', 'De maximale toegestane promillage voor beginnende bestuurders is 0,2 promille. Bij een blaastest komt dit neer op 88 µg alcohol per liter uitgeblazen adem.', '0,2 promille', '0,5 promille', '0,1 promille'),
	(785, 'G', 1, '2d_010a.png', 'Wanneer ben je een ervaren bestuurder?', 'In principe ben je een ervaren bestuurder als je langer dan 5 jaar je rijbewijs bezit. Mocht je echter voor je 18e verjaardag jouw rijbewijs hebben gehaald, dan ben je pas na 7 jaar een ervaren bestuurder.', 'Wanneer je langer dan 5 jaar je rijbewijs hebt', 'Wanneer je langer dan 2 jaar je rijbewijs hebt', 'Wanneer je langer dan 1 jaar je rijbewijs hebt'),
	(786, 'G', 1, '325.jpg', 'Waarmee kun je het gevoel van extreme vermoeidheid vergelijken?', 'Wanneer je voor een lange tijd niet hebt geslapen, kan dit vergeleken worden met hetzelfde gevoel als alcohol. Jouw reactievermogen vermindert. ', 'Alcohol', 'Drugs', 'Medicijnen'),
	(788, 'G', 1, 'ef_341.jpg', 'Wat moet je volgen als je als ervaren bestuurder teveel alcohol (tussen 0,8 en 1,0 promille) hebt gedronken en de politie houdt je aan?', 'Als je als ervaren bestuurder bestrapt wordt op het rijden met tussen de 0,8 en 1,0 promille alcohol in je bloed dan wordt je verplicht een LEMA (Lichte Educatieve Maatregel Alcohol) te volgen.', 'LEMA', 'EMA', ''),
	(789, 'G', 2, '302.jpg', 'Kan een verzekeringsmaatschappij kosten op jou verhalen wanneer het blijkt dat je onder invloed een ongeluk hebt veroorzaakt?', 'Als je een ongeluk veroorzaakt terwijl je onder invloed bent, dan ben je daar in principe niet voor verzekerd.', 'JA', 'NEE', ''),
	(790, 'G', 2, 'ef_343.jpg', 'Ben je onder invloed na het drinken van een Bacardi Breezer?', 'Na een slokje alcohol ben je al onder invloed. Het maakt niet uit wat voor drankje dat dan is.', 'JA', 'NEE', ''),
	(791, 'G', 1, 'ef_339.jpg', 'Wat gebeurt er als je een joint rookt voor het rijden?', 'Het roken van een joint heeft een negatief effect op jouw reactievermogen. Ook al vinden sommigen misschien dat het geen effect of een kalmerend effect op hen heeft, het belangrijkste is dat je weet dat je reactievermogen hierdoor vermindert en dat dat gevaarlijk kan zijn.', 'Jouw reactievermogen vermindert net zoals bij alcohol', 'Je wordt kalmer en minder agressief', 'Een joint heeft geen effect op mijn rijgedrag'),
	(792, 'G', 2, 'ef_340.jpg', 'Bestaan er drugs of medicijnen die jouw rijvaardigheid beïnvloeden?\r\n', 'Er bestaan vele soorten drugs en medicijnen die jouw rijvaardigheid kunnen beïnvloeden.', 'JA', 'NEE', ''),
	(793, 'G', 2, '080.jpg', 'Omdat het rustig op de weg is, kun je flink doorrijden. Je rijd 3 uur achter elkaar zonder pauze. Is dit verstandig?', 'Het is verstandig om na elke 2 uur rijden ongeveer een kwartier pauze te nemen. Tijdens een rit van 3 uur, is het dus verstandig om minstens 1 keer pauze te nemen.', 'NEE', 'JA', ''),
	(796, 'G', 2, 'ef_343.jpg', 'Bevat een standaard glas bier evenveel alcohol als een standaard glas whisky?', 'De gedachte hierachter is dat een drank met een hoger percentage in een kleiner glas wordt geserveerd dan een drank met een lager percentage. Een glas bier is dus groter dan een glas whisky. Zo krijg je absoluut gezien evenveel alcohol binnen.', 'JA', 'NEE', ''),
	(797, 'G', 1, 'ef_341.jpg', 'Vanaf wanneer heeft alcohol invloed op jouw rijstijl?', 'Vanaf de eerste slok alcohol en dus vanaf het laagste promillage wordt jouw rijstijl al beïnvloed.', 'Vanaf 0,1 promille', 'Vanaf 0,2 promille', 'Vanaf 1 promille'),
	(800, 'D', 2, 'ef_280.JPG', 'Je snelheidsmeter is kapot. Mag je nu gaan rijden?', 'Het is verboden om met een niet werkende snelheidsmeter te gaan rijden. Het is ten allen tijde belangrijk om te kunnen zien hoe hard je rijdt. ', 'NEE', 'JA', ''),
	(802, 'H', 1, '168.jpg', 'Welke van de volgende items hebben effect op de stopafstand?', 'De stopafstand is de som van de reactietijd van de bestuurder en de remafstand.', 'Beide', 'Remafstand', 'Reactietijd van de bestuurder'),
	(804, 'H', 1, '168.jpg', 'Je rijdt 50 km/u. Hoeveel meter bedraagt de remafstand?\r\n', 'De remafstand bereken je door de snelheid te delen door tien (50/10=5), die uitkomst in het kwadraat te doen (5²=25) en die uitkomst delen door twee (25/2=12,5). In dit geval staat het precieze antwoord dus niet als optie aangegeven, dit kan ook voorkomen tijdens jouw examen. Kies dan voor het dichtstbijzijnde getal, in dit geval dus 14!\r\n', '14 meter', '5 meter', '25 meter'),
	(805, 'H', 1, '167c.jpg', 'Op welke rijstrook komt de meeste spoorvorming voor?', 'Op de meeste rechtse rijstrook komt de meeste spoorvorming voor, omdat daar de meeste voertuigen rijden. Bovendien rijden daar veruit de meeste vrachtwagens die het grootste aandeel hebben in het vormen van een spoor in het asfalt.', 'De meest rechtse rijstrook', 'De linkerrijstrook', 'De rijstrook in het midden'),
	(806, 'H', 1, '162b.png', 'Je rijdt in de regen. Met welke band is de kans op aquaplaning het kleinst?', 'Met winterbanden is de kans op aquaplaning het kleinst, omdat die banden het diepste profiel hebben waardoor het water gemakkelijker afgevoerd kan worden en er geen laagje water tussen de band en het wegdek blijf zitten.', 'Winterband', 'Brede band', 'Normale band'),
	(807, 'H', 1, 'ef_204.JPG', 'Wat heeft invloed op de remafstand?', 'Al deze factoren hebben invloed op de remafstand.', 'Wegdek, profiel van de banden en kwaliteit van de remmen', 'Het wegdek en het profiel van de banden', 'Het wegdek'),
	(808, 'H', 2, 'ef_203.JPG', 'Je bandenspanning is te laag. Zorgt dit ervoor dat de banden sneller slijten?', 'Op een band zit een speciaal gedeelte dat bestemd is om in aanraking te komen met de ondergrond. Bij te zachte banden komt niet het juiste gedeelte in aanraking met de ondergrond en slijt het snel af.', 'JA', 'NEE', ''),
	(809, 'H', 1, '192.jpg', 'Je rijdt 50 km/u en je bent 50 meter van een stoplicht verwijderd. Het stoplicht wordt plots geel. Wat doe je?', 'Als je 50 km/u rijdt, dan is de stopafstand 26,25 meter. Je hebt dus nog ruim voldoende afstand om rustig tot stilstand te komen.', 'Ik laat het gas los en rem rustig', 'Ik rem abrupt voor het gele licht', 'Ik geef extra gas'),
	(810, 'H', 1, '305.jpg', 'Je rijdt 50 km/u. Hoeveel meter bedraagt de reactieafstand?', 'Om de reactieafstand te berekenen kun je het volgende sommetje gebruiken: (snelheid/4)+10%. Dat is dus 50/4=12,5 → 12,5+10%= 13,75. Vervolgens kies je het antwoord dat er het dichtste bij zit en dat is 14 meter.', '14 meter', '25 meter', '5 meter'),
	(811, 'H', 1, 'efvkr_047.jpg', 'Je rijdt 50 km/u. Hoeveel meter bedraagt de remafstand?', 'Om de remafstand te berekenen kun je het volgende sommetje gebruiken: (snelheid/10)²/2. Dat is dus 50/10=5 →  5²=25 → 25/2=12,5. Vervolgens kies je het antwoord dat er het dichtste bij zit en dat is 14 meter.', '14 meter', '5 meter', '25 meter'),
	(812, 'H', 1, 'efvkr_005.jpg', 'Je rijdt 50 km/u. Hoeveel meter bedraagt de stopafstand?', 'Om de stopafstand te berekenen, tel je de reactieafstand en de remafstand bij elkaar op. De reactieafstand bij een snelheid van 50 km/u bedraagt 13,75 meter. De remafstand bij een snelheid van 50 km/u bedraagt 12,5 meter. 13,75+12,5= 26,25 meter.\r\n', 'ngeveer 25 tot 30  meter', 'ongeveer 35 tot 40 meter', 'ongeveer 45 tot 55 meter'),
	(813, 'H', 2, 'efvkr_068.jpg', 'Je rem werkt niet goed. Kan de remweg hierdoor langer worden?\r\n', 'Als de rem van een voertuig niet goed werkt, duurt het langer voordat je jouw voertuig compleet tot stilstand kunt brengen. ', 'JA', 'NEE', ''),
	(814, 'H', 2, '311.jpg', 'Je rem werkt niet goed. Heeft dat invloed op de reactieafstand?\r\n', 'Een niet goed werkende rem heeft geen invloed op jouw reactieafstand. Het heeft wel invloed op de remafstand en op de stopafstand.', 'NEE', 'JA', ''),
	(815, 'H', 2, 'ef_205.JPG', 'De spanning op de banden is niet correct. Is er nu een grotere kans op slipgevaar?\r\n', 'Wanneer de banden niet op de juiste spanning zijn, is er eerder kans op slipgevaar. ', 'JA', 'NEE', ''),
	(816, 'H', 1, 'ef_221.JPG', 'Welke banden verlagen het risico op aquaplaning?', 'Winterbanden hebben het diepste profiel waardoor er het meeste water weg gevoerd kan worden en de grip met het wegdek langer kan worden behouden.\r\n', 'Winterbanden ', '4 seizoenen banden', 'Zomerbanden'),
	(817, 'J', 1, '339.jpg', 'Tijdens een file rijdt iedereen dezelfde snelheid. Hoe kun je ervoor zorgen dat het verkeer blijft doorstromen?', 'Als je in een file rijdt, is het voor de doorstroming het beste om op dezelfde rijbaan te blijven. Op deze manier hoeven andere verkeersdeelnemers niet constant gas terug te nemen om ruimte te maken voor invoegend verkeer en kan er veelal op een gestaag tempo doorgereden worden.', 'Blijf op jouw eigen rijbaan', 'Hou meer volgafstand', 'Verander van rijbaan waar mogelijk'),
	(818, 'J', 2, '133.jpg', 'Is het verstandig om jouw waarschuwingslichten aan te zetten wanneer je een file nadert?\r\n', 'Ja, wanneer je flink moet afremmen, kun je dat door middel van waarschuwingslichten doorgeven aan het verkeer achter jou. Zo weten zij dat er voor hun hard geremd wordt en kunnen ze daar naar handelen.', 'JA', 'NEE', ''),
	(819, 'J', 2, '2d_010a.png', 'Is het waarschijnlijk dat je meer fouten maakt als beginnend bestuurder?', 'Ja, het is waarschijnlijk dat je meer fouten maakt als beginnend bestuurder. Naarmate je meer ervaring opdoet in het verkeer, kun je situaties beter inschatten en sneller reageren.', 'JA', 'NEE', ''),
	(820, 'J', 2, 'A03-70.png', 'Een matrixbord geeft een lagere snelheid weer. Kun je een file verwachten?', 'Wanneer je een elektronisch bord/ matrix bord nadert met een maximumsnelheid kun je ervan uitgaan dat er een gevaarlijke situatie nadert.  ', 'JA', 'NEE', ''),
	(821, 'J', 2, 'efvkr_112.jpg', 'Geef je een voorrangsvoertuig altijd voorrang?', 'Een voorrangsvoertuig moet je altijd voorrang geven. Let op! Een voertuig met bijvoorbeeld alleen zwaailichten aan is geen voorrangsvoertuig. Voorrangsvoertuigen hebben altijd een zwaailicht én een sirene.', 'JA', 'NEE', ''),
	(822, 'J', 1, '204.png', 'Bij welke personen moet je extra oplettend zijn?', 'Kinderen en oudere personen worden als meer kwetsbaar gezien in het verkeer, omdat ze onvoorspelbaar zijn. Let daarom extra goed op.', 'Kinderen', 'Fietsers', 'Baby’s'),
	(823, 'J', 2, 'efvkr_103.jpg', 'Is defensief rijgedrag gewenst?', 'Defensief rijgedrag betekent dat je rekening houdt met fouten die andere verkeersdeelnemers kunnen maken en daarop anticipeert.', 'JA', 'NEE', ''),
	(824, 'J', 1, 'efvkr_126.jpg', 'Hoeveel volgafstand houd je bij een snelheid van 60 km/u?', 'Met een snelheid van 60 km/u moet je minimaal 33 meter afstand houden tussen jou en je voorganger. Het sommetje gaat als volgt: (60/2)+10%=33.', '33 meter', '60 meter', '20 meter'),
	(825, 'J', 1, 'A03-50.png', 'De matrixborden lichten op en er staat ‘50’. Je rijdt op de autoweg waar je 100km/ u mag. Wat doe je?', 'De laagst aangegeven snelheid moet je opvolgen. Dit duidt meestal aan dat er iets verandert aan de verkeerssituatie. Het is niet verstandig om in zo’n situatie abrupt te gaan remmen, want daarmee kun je het overige verkeer verrassen.', 'Je vermindert je snelheid naar 50 km/u', 'Je blijft 100 km/u rijden, want er is geen gevaar te zien', 'Je remt abrupt'),
	(826, 'J', 1, '186.png', 'Waar kun je een brommobiel op de weg verwachten? ', 'Een brommobiel kun je binnen en buiten de bebouwde kom verwachten, maar deze mag niet op de auto(snel)weg. ', 'Beide', 'Buiten de bebouwde kom', 'Binnen de bebouwde kom'),
	(827, 'J', 1, '100.jpg', 'Hoeveel volgafstand houd je minimaal bij een snelheid van 90 km/u?', 'Om de minimale volgafstand te berekenen, kun je het volgende sommetje gebruiken: (snelheid/2)+10%. Dus bij een snelheid van 90 km/u is de minimale volgafstand 90/2=45 → 45+10%=49,5. Vervolgens kies je het antwoord dat het dichtste bij de uitkomst van het sommetje zit en dat is 50 meter.', '50 meter', '25 meter', '100 meter'),
	(828, 'J', 1, 'efvkr_225.jpg', 'Wat is de minimale volgafstand die je houdt bij een snelheid van 120 km/u?', 'Om de minimale volgafstand te berekenen, kun je het volgende sommetje gebruiken: (snelheid/2)+10%. Dus bij een snelheid van 120 km/u is de minimale volgafstand 120/2=60 → 60+10%=66 meter.\r\n', '66 meter', '120 meter', '40 meter'),
	(829, 'J', 1, 'efvkr_249.jpg', 'Hoeveel volgafstand houd je minimaal bij een snelheid van 30 km/u?', 'Om de minimale volgafstand te berekenen, kun je het volgende sommetje gebruiken: (snelheid/2)+10%. Dus bij een snelheid van 30 km/u is de minimale volgafstand 30/2=15 → 15+10%=16,5. Vervolgens kies je het antwoord dat het dichtste bij de uitkomst van zhet sommetje zit en dat is 15 meter.', '15 meter', '30 meter', '3  meter'),
	(831, 'J', 1, '304.jpg', 'Wat bepaalt de minimale volgafstand?', 'Het merk van de banden en de lengte van het voertuig hebben in principe niks met de minimale volgafstand te maken. De weersomstandigheden wel, die beïnvloeden bijvoorbeeld de grip op het wegdek en jouw zicht waardoor jouw stopafstand langer kan worden.', 'De weersomstandigheden', 'De lengte van het voertuig', 'Het merk van de banden'),
	(832, 'J', 1, '105.jpg', 'De bestuurder wil plots naar links, hoe reageer jij hierop?', 'Als je het kunt oplossen zonder abrupt te remmen, dan is dat een betere oplossing. Wanneer je abrupt remt, kan achterop komend verkeer jouw voertuig raken. ', 'Jij wijkt uit als dat nodig en mogelijk is en gebruikt de claxon om de bestuurder te waarschuwen.', 'Je remt abrupt om niet geraakt te worden door de bestuurder.', 'Je doet niks '),
	(833, 'J', 2, 'efvkr_099.jpg', 'Kun je de remafstand verkorten door te anticiperen?', 'Door te anticiperen kun je wel de reactieafstand verkorten, maar de remafstand hangt af van dingen als de weersomstandigheden en staat van het voertuig.', 'NEE', 'JA', ''),
	(834, 'J', 2, 'efvkr_174.jpg', 'Is risicocompensatie gevaarlijk?', 'Risicocompensatie houdt in dat mensen, naarmate ze weten dat er meer veiligheidsmaatregelen getroffen zijn, meer risico’s nemen. Een voorbeeld hiervan is het hard door de bocht rijden, omdat je toch wel een gordel om hebt. ', 'JA', 'NEE', ''),
	(835, 'K', 1, '159.jpg', 'Aan welke kant van de weg vind je dit paaltje?', 'De witte reflector vind je aan de linkerkant van de weg. De reflector paaltjes hebben altijd twee kanten. De regel is Rood = Rechts!', 'Aan de linkerkant van de weg', 'Aan de rechterkant van de weg', ''),
	(836, 'K', 2, '195.jpg', 'Is een negatieve wegverkanting voordelig voor het onder controle houden van jouw voertuig?', 'Een negatieve wegverkanting is niet voordelig voor jouw controle over het voertuig. Dit heeft te maken met de grip van het voertuig en hoe die over de ondergrond verdeeld is.', 'NEE', 'JA', ''),
	(837, 'K', 1, '129.jpg', 'Een auto wilt een uitrit uitrijden en naar links afslaan, jij komt voor je medeweggebruiker van links en wilt diezelfde uitrit inrijden. Wie heeft er voorrang?', 'De bestuurder die vanuit een uitrit komt moet voorrang verlenen, maar voor de doorstroming is het vaak wel weer handiger om eerst diegene eruit te laten. Communicatie onderling is dus heel belangrijk. ', 'Ik', 'Medeweggebruiker', ''),
	(838, 'K', 1, 'ef_256.JPG', 'Wanneer is het gedurende de dag gewenst om je dimlicht aan te zetten?', 'Het is altijd gewenst om met dimlichten aan te rijden, zelfs als de omstandigheden er niet per se om vragen.', 'Altijd', 'Met sneeuw of regen', 'Met bewolking of mist'),
	(839, 'K', 1, 'J31.png', 'Voor welke weggebruikers is dit bord bedoeld?', 'Er wordt gewaarschuwd voor zijwind. Dit kan effect hebben op iedereen die de weg gebruikt, ook voor voetgangers. ', 'Alle weggebruikers', 'Alleen bestuurders', 'Bestuurders van voertuigen op 2 wielen'),
	(840, 'K', 1, '228c.jpg', 'Op welke weg gebeuren de meeste ongelukken?', 'Op een 80km-weg gebeuren, in vergelijking met de auto(snel)weg en bebouwde kom, meer ongelukken. Dit is onder andere te wijten aan de smallere wegen, inhaalgevaar, en de verschillende onderlinge snelheden van voertuigen.', '80 km-weg', 'Autoweg', 'Autosnelweg'),
	(841, 'K', 1, 'ef_097.JPG', 'Je komt uit het bos en rijdt op een zandweg en wilt vervolgens de doorgaande rijbaan oprijden. Wie heeft er voorrang?', 'In het geval dat je op een onverharde weg rijdt en je wilt de verharde weg oprijden, hebben bestuurders op de verharde weg altijd voorrang ook al komen ze niet van rechts. Let op! Dit geldt dus niet voor voetgangers.', 'Bestuurders op de verharde weg', 'Degene die van rechts komt', 'Jij'),
	(842, 'K', 1, 'efvkr_016.jpg', 'Welke uitspraak is meer waar over begroeiing naast de weg?', 'Doordat er telkens zonlicht tussen de bomen doorkomt, moeten je ogen zich continu aanpassen aan de felheid van het licht. Dit is lastig en door de snelle veranderingen van schaduw naar zonlicht zie je slechter.', 'Zonlicht komt tussen de bomen door, hierdoor zie je slechter', 'Zonlicht komt tussen de bomen door, hierdoor zie je beter', 'Zonlicht komt tussen de bomen door, dit heeft geen invloed op je zicht'),
	(843, 'K', 1, 'efvkr_185.jpg', 'Je rijdt op een weg met veel bomen aan de zijkant van de weg. Waar moet je op letten?', 'Doordat er bomen langs de weg staan, heb je geen ruimte om uit te wijken naar de berm als dit nodig is.', 'Ik kan niet uitwijken voor het andere verkeer', 'Ik moet extra voorzichtig zijn, behalve op een kruising', 'Bestuurders op de zijwegen kunnen me niet zien'),
	(844, 'K', 1, 'efvkr_147.jpg', 'Wat kan er gebeuren nadat het opeens hard gaat regenen na een lange droge periode?', 'Op een droog wegdek hebben voertuigen rubber en olieresten gelekt. Wanneer het dan plots gaat regenen, wordt dit glad.', 'Rubber en olieresten maken het wegdek glad', 'De regen wordt sneller opgenomen in het wegdek', 'Door de regen op een droog wegdek zal ik sneller last hebben van aquaplaning'),
	(845, 'K', 1, 'efvkr_224.jpg', 'Waar komt aquaplaning vaker voor?', 'Op de rechterbaan zijn de meeste voertuigen en daarbij ook veel vrachtwagens. Deze zorgen voor meer spoorvorming, waardoor het risico op aquaplaning tijdens regen stijgt. ', 'Rechterbaan', 'Uitvoegstrook', 'Linkerbaan'),
	(846, 'K', 2, '309.jpg', 'Je hebt motorproblemen. Is dit een veilige plaats om als persoon te wachten?', 'Voor de veiligheid is het raadzaam om achter de vangrail te wachten, wanneer je je op de vluchtstrook bevindt. ', 'NEE', 'JA', ''),
	(847, 'K', 2, '310.jpg', 'Je hebt pech. Is dit een veilige plaats om als persoon te wachten?', 'De vluchtstrook is een gevaarlijke plek om te wachten. De vangrail kan je beschermen wanneer er bv. een andere bestuurder op de vluchtstrook terechtkomt. ', 'JA', 'NEE', ''),
	(848, 'K', 1, 'ef_269.JPG', 'Tijdens het rijden is het aan te raden je lichten aan te zetten. Welke lichten?', 'Dimlicht is altijd aan te raden tijdens het rijden om jouw zichtbaarheid te vergroten.', 'Dimlicht', 'Grootlicht', 'Stadslicht'),
	(849, 'K', 1, '167c.jpg', 'Op welke rijstrook is de kans op aquaplaning het grootst?', 'Vanwege de spoorvorming op rijstrook C, worden deze sporen gevuld met water. Dit maakt het risico op aquaplaning groter.', 'Rijstrook C', 'Rijstrook A', 'Rijstrook B'),
	(850, 'K', 2, 'C09.png', 'Mag een brommobiel hier inrijden? ', 'Het bord C9 is een geslotenverklaring voor alle motorvoertuigen. Gesloten voor ruiters, vee, wagens, motorvoertuigen die niet sneller kunnen of mogen rijden dan 25 km/h en brommobielen alsmede fietsen, bromfietsen en gehandicaptenvoertuigen.', 'NEE', 'JA', ''),
	(851, 'K', 2, 'C08.png', 'Mag een brommobiel hier inrijden?', 'Het bord c8 geeft een geslotenverklaring aan voor motor voertuigen die niet harder dan 25 km/u mogen of kunnen rijden. Een brommobiel mag 45 km/u en valt daar dus niet onder.', 'JA', 'NEE', ''),
	(852, 'K', 1, '312a.jpg', 'Je rijdt in een eenrichtingsweg. Aan beide kanten zijn auto’s geparkeerd. Waar moet je rekening mee houden?', 'Je zou er van uit moeten gaan dat voetgangers tussen de auto\'s door over kunnen steken zonder eerst naar links of naar rechts gekeken te hebben of er iets aan komt.', 'Voetgangers steken over zonder te kijken', 'Voetgangers kunnen me niet zien want ze kijken alleen naar links', 'Voetgangers kunnen me niet zien want ze kijken alleen naar rechts'),
	(853, 'K', 1, '058a.jpg', 'Aan welke kant van de weg vind je dit paaltje?  ', 'Rode reflectoren zijn te vinden aan de rechterkant van de weg en witte reflectoren staan aan de linkerkant van de weg. Ezelsbruggetje: Rood=Rechts.', 'Aan de rechterkant van de weg', 'Aan de linkerkant van de weg', ''),
	(854, 'K', 1, '037.jpg', 'Het begint te vriezen. Waar is er meer slipgevaar?', 'Bovenop viaducten en bruggen is het wegdek extra koud, omdat de kou van onder en boven komt. Hierdoor wordt het wegdek op die plekken sneller glad. Onder viaducten en bruggen is doorgaans minder ijsvorming. ', 'Bovenop viaducten en bruggen', 'Onder viaducten en bruggen', 'Op drukke wegen'),
	(868, 'K', 1, '169.jpg', 'Wanneer kan aquaplaning plaatsvinden?', 'Aquaplaning heeft alleen met regen te maken en komt dus niet voor als het sneeuwt.\n', 'Wanneer het heel hard regent', 'Wanneer het heel hard sneeuwt', 'Beide'),
	(870, 'L', 2, '316.jpg', 'Er is een ongeluk gebeurd en er zijn gewonden. Mag je het slachtoffer een glaasje water geven?\r\n', 'Je mag het slachtoffer niks laten drinken of eten. Dit kan verkeerd vallen bv. door interne verwondingen en de situatie verergeren. Wacht op de hulpdiensten, zij kunnen inschatten of het slachtoffer wat kan drinken.\r\n', 'NEE', 'JA', ''),
	(871, 'L', 1, '009a.png', 'Als het nodig is, hoe leg je het slachtoffer dan neer?', 'De stabiele zijligging is het meest veilig. Op deze manier zal het slachtoffer niet zijn tong kunnen inslikken of wanneer deze moet overgeven, dan wordt de luchtpijp niet geblokkeerd (stikgevaar).', 'Ligging A', 'Ligging C', 'Ligging B'),
	(872, 'L', 2, '303.jpg', 'De fietser bloedt hevig. Gebruik je jouw jas om de bloeding te stelpen?', 'Laat het slachtoffer liggen bij ernstig bloedverlies. \r\nBreng zo mogelijk het gewonde lichaamsdeel omhoog. \r\nDruk een bloedende wond onmiddellijk en minimaal tien minuten dicht. \r\nGeef druk rechtstreeks op de wond. \r\nUit de wond stekende voorwerpen niet verwijderen. \r\nDruk bij bloeding aan weerszijden van het voorwerp op de wond.\r\n', 'JA', 'NEE', ''),
	(873, 'L', 1, '325.jpg', 'Wiens identiteit moet bekend zijn, voordat deze de plaats van een ongeval mag verlaten?', 'De identiteit van iedereen die bij het ongeval betrokken is, moet bekend zijn. De afhandeling van het ongeval via verzekeringen en/of politie moet immers mogelijk zijn, wat niet kan zonder dat ieders identiteit bekend is.', 'Iedereen die te maken heeft met het ongeval', 'Diegene die het ongeval heeft veroorzaakt', 'Het slachtoffer'),
	(874, 'L', 2, '313.jpg', 'Moet je bij het slachtoffer blijven als er geen andere mensen zijn? ', 'Als jij de enige bij het slachtoffer bent dan ben je verplicht te blijven totdat de hulpdiensten er zijn. Je mag een slachtoffer niet zomaar aan zijn lot over laten.', 'JA', 'NEE', ''),
	(877, 'C', 2, '315.jpg', 'Je sleept een motorfiets, mag dat?', 'Dit mag niet. Om een voertuig te mogen slepen, moet het zichzelf op de weg in balans kunnen houden. Een motorfiets met zijspanwagen mag bijvoorbeeld wel, mits het wiel van het zijspan beremd is.', 'NEE', 'JA', ''),
	(878, 'L', 1, '319.jpg', 'Hoe hoog is het ongevalspercentage van menselijke fouten?', '92% van de ongelukken wordt veroorzaakt door menselijke fouten. Een moment van nonchalance of onkunde kan hiervan de aanleiding zijn. De overige 8% wordt veroorzaakt door bijvoorbeeld een technisch mankement of een boom die op de weg is gevallen.', '92%', '75%', '35%'),
	(879, 'L', 1, '302.jpg', 'Wat is de procedure na een ongeval?', 'Je zorgt eerst voor de veiligheid van jezelf en het slachtoffer, vervolgens bel je 112. Sleep het slachtoffer nooit naar een andere plek.', 'Je zorgt eerst voor de veiligheid van jezelf en het slachtoffer', 'Je sleept het slachtoffer naar de kant van de weg zodat deze veilig is', 'Je belt gelijk 112'),
	(881, 'L', 1, '050.jpg', 'Wanneer kun je (overstekende) dieren verwachten?\r\n', 'Na zonsondergang (‘s nachts) kun je dieren op de weg verwachten', 'Zonsondergang', 'Zonsopkomst', ''),
	(882, 'L', 2, '882.jpg', 'Je bent in het water beland met jouw auto. Is het verstandig om het contact uit te zetten?', 'Wanneer je het contact uitzet, gaat de verlichting ook uit. Door de verlichting kunnen hulpdiensten jouw juist makkelijker vinden. Ook zullen de elektrische ramen niet meer werken. ', 'NEE', 'JA', ''),
	(883, 'L', 1, '318.jpg', 'Wat kun je het beste als eerste doen als je onverhoopt in het water terechtkomt met de auto?', 'Je zet eerst jouw lichten aan zodat de plek van het voertuig duidelijk is en blijf voor hulpdiensten, daarna probeer je de ramen te openen of te breken.', 'De verlichting aanzetten', 'De ramen open draaien', ''),
	(884, 'L', 1, 'efvkr_183.jpg', 'Wanneer mag je van een vluchtstrook gebruikmaken?', 'Wanneer de spitsstrook geopend is, mag je gebruiken maken van de vluchtstrook, omdat deze dan als spitsstrook geldt. ', 'Als deze als spitsstrook gebruikt wordt', 'Als er een voorrangsvoertuig nadert', 'Als er een voertuig nadert'),
	(885, 'L', 2, '313.jpg', 'Na een ongeval is het been van de fietser ontwricht. Zet je het been recht? ', 'Het is het verstandigst het slachtoffer zo min mogelijk aan te raken om extra schade te voorkomen. Het rechtzetten van een been laat je dus over aan de hulpdiensten.', 'NEE', 'JA', ''),
	(886, 'L', 1, '322.jpg', 'Na een botsing heeft de bestuurder pijn aan zijn nek. Wat is in het algemeen het verstandigst om te doen?\r\n', 'Je weet niet wat er met de bestuurder gebeurd is. Daarom laat je de bestuurder zitten en wacht je tot de hulpdiensten arriveren. ', 'Je laat de bestuurder in de auto zitten', 'Je haalt de bestuurder uit de auto en legt hem plat neer', ''),
	(887, 'L', 2, '069.jpg', 'Je hebt motorproblemen en plaatst de gevarendriehoek op 5 meter afstand van de auto. Mag dit?', 'De gevarendriehoek moet je op minstens 30 meter afstand van de auto plaatsen. Het is overigens niet verplicht een gevarendriehoek bij je te hebben. Echter moet je hem wel gebruiken in het geval dat je hem wel bij je hebt.', 'NEE', 'JA', ''),
	(888, 'L', 2, '177.png', 'Je sleept een motorfiets met zijspan, mag dat?', 'Een voertuig met meer dan 2 wielen mag gesleept worden. ', 'JA', 'NEE', ''),
	(889, 'L', 1, '327.jpg', 'Hoeveel procent van de ongelukken wordt er veroorzaakt door vermoeidheid?', 'Ongeveer 15% van de verkeersongevallen wordt veroorzaakt doordat bestuurders erg moe zijn en daardoor niet tijdig genoeg kunnen reageren. ', '15%', '5%', '60%'),
	(890, 'L', 2, '303.jpg', 'Mag je wegrijden na een ongeluk?', 'Je mag niet zomaar wegrijden na een ongeval. De identiteit van het slachtoffer en de schuldige moet eerst bekend zijn. Je mag ook niemand alleen laten terwijl deze gewond is geraakt.  \r\n', 'NEE', 'JA', ''),
	(891, 'L', 2, '335.jpg', 'Het slachtoffer vraagt of je haar helm wilt afzetten, mag dat?', 'Je weet niet wat er bv. met de nek van het slachtoffer gebeurt. Daarom laat je de helm zitten: je wacht totdat er professionele hulp arriveert. ', 'NEE', 'JA', ''),
	(892, 'L', 1, '327.jpg', 'Jonge bestuurders zijn vaker betrokken bij een ongeval. Wat is de reden hiervoor?', 'Jonge bestuurders hebben nog niet genoeg ervaring in het verkeer en kunnen vaak situaties onderschatten. ', 'Ze onderschatten vaak de risico’s in het verkeer', 'Ze rijden vaak in oude auto’s', 'Ze draaien vaak harde muziek'),
	(893, 'L', 1, 'ef_340.jpg', 'Welke medicatie kan de rijvaardigheid beïnvloeden?', 'Medicijnen met een gele sticker kunnen de rijvaardigheid beïnvloeden, let daarom goed op wanneer je medicijnen neemt die een gele sticker op de verpakking hebben. ', 'Medicatie met een gele sticker', 'Medicatie die je alleen op recept krijgt', 'Alle medicijnen die te krijgen zijn in de apotheek'),
	(894, 'L', 1, '029.png', 'Wat is het telefoonnummer voor spoedeisende hulp in Nederland?', 'In een noodsituatie bel je 112. Echter krijg je ook de hulpdiensten aan de telefoon wanneer je 911 belt. ', '112', '911', '0900-8844'),
	(895, 'M', 1, '144.jpg', 'Je komt vanaf punt A. Wanneer geef je richting aan wanneer je rechtdoor wilt?', 'Bij punt C geef je richting aan. Wanneer je bij punt B richting aangeeft, denken mensen dat je de eerste afslag rechts wilt afslaan. ', 'Punt C', 'Punt B', 'Punt A'),
	(896, 'M', 2, '314.jpg', 'De bestuurder links verleent geen voorrang aan jou. Steek je nog steeds het kruispunt over?\r\n', 'Wanneer je wel voorrang hebt, maar dit niet krijgt, is het aan jou om een ongeluk te voorkomen. Dus ook al heb je eigenlijk voorrang, als er verkeer voordringt, betekent dat niet dat jij nu tegen iemand aan mag rijden. De andere bestuurder moet voorrang verlenen en jij mag geen voorrang nemen. ', 'NEE', 'JA', ''),
	(897, 'M', 1, 'ef_277.JPG', 'Je rijdt op een gelijkwaardig kruispunt. Wie heeft er voorrang?', 'De basisregel is dat bestuurders van rechts voorrang hebben. Voetgangers zijn geen bestuurders en zijn dus niet inbegrepen in deze basisregel.\r\n', 'Bestuurders die van rechts komen', 'Bestuurders die van links komen', 'Voetgangers die van rechts komen'),
	(898, 'M', 2, '125.jpg', 'Je wilt rechts afslaan op de rotonde. Moet je de voetganger die rechtdoor wilt, voor laten gaan?', 'De voetganger wilt in dit geval rechtdoor op dezelfde weg waarop jij rijdt. Je moet de voetganger dus voor laten gaan.\r\n', 'JA', 'NEE', ''),
	(899, 'M', 2, 'b05_d04.png', 'Mag je bij deze bordencombinatie rechts afslaan? ', 'Het onderste bord is een verplichte richting. Het bord daarboven geeft aan dat je voorrang hebt op de zijstraat van rechts. ', 'NEE', 'JA', ''),
	(900, 'M', 1, '116.jpg', 'Wat voor een soort rotonde nadert de auto hier? ', 'De rotonde is over meerdere rijstroken verdeeld. ', 'Met meerdere rijstroken', 'Met een rijstrook', ''),
	(901, 'M', 2, '317.jpg', 'Mag je parkeren op een voorrangsweg binnen de bebouwde kom?', 'Dit mag in principe wel. Let op! Als je gevaar of hinder veroorzaakt dan is het verboden. Op een voorrangsweg buiten de bebouwde kom is het verboden om te parkeren op de rijbaan, in de berm mag het echter wel, tenzij anders is aangegeven.', 'JA', 'NEE', ''),
	(902, 'M', 1, '145.jpg', 'Wie heeft er voorrang?', 'Een ruiter valt onder de categorie bestuurders en de regel luidt: een bestuurder op een verharde weg heeft voorrang op een bestuurder op een onverharde weg.', 'Ruiter', 'Auto', ''),
	(903, 'M', 2, 'B03.png', 'Betekent dit bord ’gevaarlijk kruispunt’?', 'Dit bord betekent voorrangskruispunt. ', 'NEE', 'JA', ''),
	(904, 'M', 2, '061.jpg', 'Je stopt hier om iemand in te laten stappen, mag dat? ', 'Nee, je mag niet stil staan vlak voor of na een kruising.', 'NEE', 'JA', ''),
	(905, 'M', 2, '419.jpg', 'Voordat je een rotonde oprijdt, mag je dan al richting aangeven als je naar links wilt afslaan?', 'Je mag voordat je de rotonde oprijdt al richting aangeven naar links, dit is extra duidelijk voor het andere verkeer. \r\n', 'JA', 'NEE', ''),
	(906, 'M', 1, '275.jpg', 'Waar bevind je je?', 'Wanneer het voorrangsbord voor het kruispunt is geplaatst, bevind je je binnen de bebouwde kom. ', 'Binnen de bebouwde kom', 'Buiten de bebouwde kom', ''),
	(907, 'M', 1, '276.jpg', 'Waar bevind je je?', 'Wanneer een voorrangsbord na het kruispunt is geplaatst, dan bevind je je buiten de bebouwde kom. ', 'Buiten de bebouwde kom', 'Binnen de bebouwde kom', ''),
	(908, 'M', 2, '293.jpg', 'Is een voorrangsweg altijd een weg die rechtdoor gaat?', 'Er zijn ook voorrangswegen die afbuigen.', 'NEE', 'JA', ''),
	(911, 'M', 2, '295.jpg', 'Je wilt rechtsaf. Moet je de fietser voor laten gaan?\r\n', 'De fietser gaat rechtdoor, jij slaat rechtsaf. De fietser heeft dus voorrang. ', 'JA', 'NEE', ''),
	(913, 'M', 2, '320.jpg', 'Je wilt de weg volgen naar links. Moet je het voertuig aan de rechterkant voor laten gaan?', 'Ook al buigt de weg af naar links blijft het een voorrangsweg. Je hebt dus voorrang boven de ander.', 'JA', 'NEE', ''),
	(914, 'M', 1, '330.jpg', 'Wat is de voornaamste reden van ongevallen op de weg?', 'Bestuurders zijn niet goed op de hoogte van de regels, daarom gebeuren er veel ongevallen. ', 'Bestuurders kennen de voorrangsregels niet goed', 'Technische problemen', 'Kinderen die niet opletten'),
	(915, 'M', 2, '296.jpg', 'Je wilt links afslaan. Moet je de andere auto voor laten gaan?', 'Ongeacht of het een douane auto is,  gaat hij rechtdoor. Je mag nooit linksaf slaan voor een tegenligger. ', 'JA', 'NEE', ''),
	(916, 'M', 1, '194.jpg', 'Waar vind je doorgaans een positieve wegverkanting?', 'Een rotonde heeft doorgaans een negatieve wegverkanting. ', 'Race circuit', 'Rotonde', 'Een rechtdoorgaande weg'),
	(917, 'M', 2, '419.jpg', 'Je nadert een rotonde en wordt ingehaald via rechts. Mag dit?', 'Vlak voor of op een rotonde mag je via rechts inhalen. ', 'JA', 'NEE', ''),
	(918, 'M', 2, '294.jpg', 'Mag je 2 meter voor een kruispunt parkeren?', 'Binnen 5 meter van het kruispunt mag je niet parkeren. ', 'NEE', 'JA', ''),
	(919, 'M', 2, '295.jpg', 'Je nadert een gelijkwaardig kruispunt en wilt afslaan. Je ziet een fietser naast jou rijden. Zij wilt rechtdoor. Moet je deze voor laten gaan?', 'Rechtdoor op dezelfde weg heeft voorrang, dus aan de fietser moet je voorrang verlenen. ', 'JA', 'NEE', ''),
	(920, 'M', 2, '291.jpg', 'Mag je parkeren op een voorrangsweg buiten de bebouwde kom?', 'Buiten de bebouwde kom mag je niet parkeren op een voorrangsweg. Je mag wel in de berm parkeren, tenzij anders aangegeven. ', 'NEE', 'JA', ''),
	(921, 'N', 2, '063.jpg', 'Je wilt naar links afslaan. Moet je voorrang verlenen aan de fietser? ', 'Je moet de fietser voor laten gaan. De fietser gaat immers rechtdoor op dezelfde weg als jij. Het valt alsnog onder dezelfde weg, ook al mag jij je niet op dat stuk weg bevinden, omdat het een fietspad is.', 'JA', 'NEE', ''),
	(922, 'N', 2, 'ef_156.JPG', 'Als mijn spiegel goed is afgesteld, dan is er geen dode hoek meer. Klopt dit?', 'Een spiegel kun je zo afstellen dat de dode hoek zo klein mogelijk is. Je kunt de dode hoek echter nooit helemaal laten verdwijnen.', 'NEE', 'JA', ''),
	(923, 'N', 1, '273a.jpg', 'Je wilt links afslaan. Wie gaat er voor? ', 'De tram en jij zijn in deze situatie gelijk wat betreft verkeerstekens. In zo’n geval gaat de tram altijd voor.', 'Tram', 'Jij', ''),
	(924, 'N', 2, '064.jpg', 'Je wilt naar rechts afslaan. Moet je voorrang verlenen aan de fietser?', 'Jullie willen allebei dezelfde weg inslaan. Jij hebt de kortste bocht en de fietser de lange bocht. Korte bocht gaat voor op de lange bocht, dus je hoeft geen voorrang te verlenen aan de fietser.\r\n', 'NEE', 'JA', ''),
	(925, 'N', 1, '166b.jpg', 'Wat is de meest ‘dode hoek’ bij een vrachtwagen?', 'Rechts en links van de vrachtwagen kan de chauffeur de dode hoek nog verkleinen door vanuit een andere hoek in zijn spiegels te kijken. Achter de vrachtwagen kan de chauffeur nooit zien. ', 'Punt B', 'Punt C', 'Punt A'),
	(926, 'N', 2, 'C04-L.png', 'Je wilt hier naar links afslaan, moet je richting aangeven? ', 'Je moet altijd richting aangeven als je af wilt slaan, om het overige verkeer te informeren. ', 'JA', 'NEE', ''),
	(927, 'N', 1, '272a.jpg', 'Je wilt op deze rijstrook blijven rijden. Wat is verstandig om te doen?', 'Je geeft richting aan, zodat het duidelijk is dat jij op de afslag blijft en niet de doorgaande rijbaan op wilt rijden. \r\n', 'Je geeft richting aan, zo weten de andere bestuurders wat je van plan bent', 'Je geeft geen richting aan, want je wilt niet van rijstrook wisselen', ''),
	(928, 'N', 1, '345.jpg', 'Je wilt de bocht nemen. Wanneer neem je gas terug?', 'Je neemt gas terug voordat je bocht inrijdt, deze moet je met aangepaste snelheid nemen. ', 'Voor de bocht', 'In de bocht', 'Na de bocht'),
	(929, 'N', 2, '346.jpg', 'Mag je in een bocht al beginnen met gas geven?', 'Je mag in de bocht al beginnen met gas geven, mits je de juiste snelheid aanhoudt.', 'JA', 'NEE', ''),
	(930, 'M', 1, '065.jpg', 'De fietser geeft aan naar links te willen. Wie heeft er voorrang?', 'Jij gaat rechtdoor op dezelfde weg en gaat daarom voor.', 'Jij', 'Fietser', ''),
	(932, 'N', 1, '193.jpg', 'Waar kun je het beste op letten voordat je een bocht neemt?', 'Let altijd op de scherpte en overzichtelijkheid van de bocht, dit is belangrijker dan de snelheid waar je de bocht mee kunt nemen. ', 'Scherpte en overzichtelijkheid van de bocht', 'De maximale snelheid waarmee je de bocht kunt nemen, zodat je flink kunt blijven doorrijden.', ''),
	(933, 'N', 1, '359.jpg', 'Je wilt afslaan. Wanneer schakel je terug?', 'Voor de bocht schakel je al terug. Dit helpt ook bij het verminderen van de snelheid om zo de bocht iets veiliger te nemen. ', 'Voor de bocht', 'In de bocht', 'Na de bocht'),
	(934, 'N', 2, '345.jpg', 'Zijn er bochten die je ook in de 4e versnelling kunt nemen?', 'Op de snelweg zijn er vaak bochten die heel erg ruim zijn. Deze kun je makkelijk in de 4e versnelling nemen. ', 'JA', 'NEE', ''),
	(936, 'N', 2, '347.jpg', 'Je schakelt terug terwijl je een bocht neemt. Is dit correct?', 'Je schakelt altijd terug voordat je een bocht neemt. ', 'NEE', 'JA', ''),
	(937, 'N', 1, '215a.jpg', 'Wie mag er eerst?', 'De blauwe auto heeft voorrang, de witte auto staat voor een witte pijl.\n\nDit zie je doordat de pijl die bij de witte auto hoort, rood is. ', 'Blauwe Auto', 'Witte Auto', ''),
	(938, 'N', 2, '295.jpg', 'Je wilt naar rechts afslaan. Moet je voorrang verlenen aan de fietser?', 'De fietser gaat rechtdoor op dezelfde weg, daarom heeft deze voorrang. ', 'JA', 'NEE', ''),
	(940, 'N', 2, '348.jpg', 'Je gaat rechtdoor. Moet je voorrang verlenen aan de fietser?\r\n', 'Je rijdt op een voorrangsweg, dit zie je aan het bord en doordat de fietser haaientanden voor zich heeft. Je hebt dus voorrang boven de fietser. ', 'NEE', 'JA', ''),
	(941, 'N', 2, '297.jpg', 'Je wilt rechtdoor. Moet je voorrang verlenen aan de bromfiets die van rechts komt?', 'De bromfiets komt van rechts, dus heeft zij voorrang. ', 'JA', 'NEE', ''),
	(942, 'N', 2, '166.jpg', 'Is er altijd een dode hoek?', 'Er is altijd een dode/ blinde hoek. ', 'JA', 'NEE', ''),
	(943, 'N', 1, 'efvkr_193.jpg', 'Je rijdt links naast een vrachtwagen. Wat kun je het beste doen?', 'Wanneer je naast een vrachtwagen rijdt, zorg voor de veiligheid dan zo snel mogelijk dat je uit zijn dode hoek verdwijnt.', 'Zorgen dat je uit zijn dode hoek verdwijnt', 'Abrupt remmen', 'Lichtsignalen geven zodat hij weet dat je naast hem rijdt'),
	(944, 'N', 2, 'geenplaatje.png', 'Je bevestigt nog 2 extra spiegels bovenop de normale spiegels. Is er nog een dode hoek aanwezig?', 'Er is altijd een dode/ blinde hoek. ', 'JA', 'NEE', ''),
	(946, 'O', 1, 'ef_289.JPG', 'Wie mag het hele gebied van een erf gebruiken?', 'Een erf is bedoeld voor spelende kinderen en voetgangers ', 'Spelende kinderen en voetgangers', 'Auto’s ', 'Fietsers &amp; voetgangers'),
	(947, 'O', 2, '051.jpg', 'Moet je voetgangers voor laten gaan wanneer ze willen uitstappen?', 'Je moet de voetgangers voor laten gaan. De voetgangers hebben namelijk geen platform om op te wachten. Als deze voetgangers wel een platform hadden gehad, dan hoefde je ze niet voor te laten gaan.', 'JA', 'NEE', ''),
	(949, 'O', 2, '204.png', 'Je ziet een voetganger die zich moeilijk voortbeweegt. Laat je deze voor gaan?', 'Je verleent altijd voorrang aan een voetganger die slecht ter been is.', 'JA', 'NEE', ''),
	(950, 'O', 1, '066.jpg', 'Wie gaat er voor?', 'Bij het einde van een erf kun je er vanuit gaan dat je te maken hebt met een in- uitrit constructie. In zo’n geval moet je voorrang geven aan al het verkeer en dus ook deze voetganger.', 'Voetganger', 'Auto', ''),
	(951, 'O', 2, '321.jpg', 'Is een scootmobiel die gebruikt maakt van het voetpad een voetganger?', 'Een scootmobiel die op het voetpad rijdt is een voetganger. Een scootmobiel die op het fietspad rijdt volgt de regels van een bestuurder.', 'JA', 'NEE', ''),
	(952, 'O', 1, '349.jpg', 'Er nadert een rouwstoet met herkenningstekens. Een voetganger wil oversteken op het zebrapad. Wie gaat er voor?', 'De militaire colonne en rouwstoet met uiterlijke kenmerken hoeven niet te stoppen voor een zebrapad! ', 'De rouwstoet', 'De voetganger op het zebrapad', ''),
	(953, 'O', 2, '298.jpg', 'Iemand loopt naast zijn fiets en wil oversteken op een zebrapad. Moet je haar voor laten gaan?', 'Ja, je moet haar voor laten gaan. Iemand die naast zijn fiets loopt is een voetganger en een voetganger moet voor gaan op een zebrapad.', 'JA', 'NEE', ''),
	(954, 'S', 2, '128.jpg', 'Mag hier worden ingehaald?', 'Bij nadering zebrapad mag je niet inhalen.', 'NEE', 'JA', ''),
	(955, 'O', 2, 'ef_303.jpg', 'Is een gehandicaptenvoertuig een moeilijk voortbewegend persoon?\r\n', 'Een gehandicaptenvoertuig zoals een scootmobiel valt bv. niet onder een moeilijk voortbewegend persoon. ', 'NEE', 'JA', ''),
	(956, 'O', 2, '321.jpg', 'Een scootmobiel rijdt op de rijbaan en nadert een zebrapad. Moet hij voetgangers voorrang geven die hier willen oversteken?', 'Een scootmobiel die de rijbaan volgt, heeft zich aan de regels van een bestuurder te houden. Daarom moet hij voetgangers op de zebra voor laten gaan. ', 'JA', 'NEE', ''),
	(957, 'O', 2, '271.jpg', 'Moet je hier voorrang verlenen?', 'Een scootmobiel moet de regels volgen van het deel van de weg waar deze zich bevindt, dus op de stoep is deze een voetganger. Deze krijgt daarom geen voorrang. Een scootmobiel valt onder een gehandicaptenvoertuig en niet als iemand die slecht ter been is. ', 'NEE', 'JA', ''),
	(958, 'O', 1, 'dsh.png', 'Waar hou je rekening mee als je één van deze borden ziet?', 'Dit bord is bedoeld voor personen die slechthorend zijn, iemand kan deze bordjes bv. achterop zijn bromfiets bevestigen. \r\n', 'Met slechthorenden', 'Met blinden', 'Met mensen die slecht ter been zijn '),
	(960, 'O', 2, '351.jpg', 'Mag je iemand even uit of af laten stappen op een voetgangersoversteekplaats?', '5 meter voor en na een voetgangersoversteekplaats mag je niet parkeren, inhalen of stoppen. ', 'NEE', 'JA', ''),
	(961, 'O', 2, 'b06_b03.png', 'Gelden deze verkeersborden ook voor voetgangers?', 'Deze verkeersborden gelden enkel voor bestuurders.', 'NEE', 'JA', ''),
	(962, 'K', 1, '215b.jpg', 'Wie mag er eerst?', 'Jij staat voor een rode pijl, de blauwe auto mag dus eerst. ', 'Blauwe Auto', 'Witte Auto', ''),
	(963, 'O', 1, '217.jpg', 'Wie gaat er voor?', 'Blinden met een witte stok met 1 of meer rode ringen die aangeven over te willen steken, moet je voor laten gaan. ', 'Voetganger', 'Auto', ''),
	(964, 'O', 1, '211.jpg', 'Wie moet er voor gaan? ', 'Voetgangers op dezelfde weg die rechtdoor gaan, moet je voor laten gaan.', 'Voetganger', 'Auto', ''),
	(965, 'O', 1, '225.jpg', 'Wie heeft er voorrang?', 'De auto heeft de voorrang, de voetganger kruist hem. De regel \'bestuurders gaan voor op de verharde weg\', geldt niet voor voetgangers. ', 'Auto', 'Voetganger', ''),
	(966, 'O', 1, '323.jpg', 'De voetganger loopt rechtdoor. Wie heeft er voorrang? ', 'De borden gelden voor bestuurders onderling. De auto moet wel stoppen voor het stopbord, dan kijken of er geen bestuurders naderen op het kruispunt, daarna kan hij verder rijden. ', 'Auto', 'Voetganger', ''),
	(967, 'O', 1, '329.jpg', 'Moet je de voetgangers voor laten gaan?', 'Wanneer ze de tram uitstappen kunnen de voetgangers nog veilig op het platform wachten, daarom hoef je ze geen voorrang te geven.', 'Nee, ze kunnen veilig op het platform wachten.', 'Ja', ''),
	(969, 'P', 1, '308.jpg', 'Je ziet de politie met alleen zwaailicht aan. Moet je voorrang verlenen?', 'Nee, je bent niet verplicht om voorrang te verlenen aan de politie als ze enkel lichtsignalen voeren. Alleen als ze zowel licht- als geluidsignalen voeren, zijn het voorrangsvoertuigen.\r\n', 'Nee', 'Ja, je moet altijd voorrang verlenen aan de politie. ', ''),
	(972, 'P', 1, '350.jpg', 'Het eerste voertuig van de rouwstoet nadert jou van links. Wie gaat er voor?', 'Het eerste voertuig heeft zich aan de gewone verkeersregels te houden. Aangezien jij in deze situatie van rechts komt, ga jij dus voor.', 'Ik ', 'Rouwstoet', ''),
	(975, 'P', 2, '415.jpg', 'Je wilt rechtdoor, moet je voorrang geven aan de militaire colonne?', 'Jij rijdt op een voorrangsweg, dus je hoeft geen voorrang te verlenen.', 'NEE', 'JA', ''),
	(1012, 'P', 2, '205a.jpg', 'Mag je hier stilstaan om te laden en lossen? ', 'Op een tramspoor dat deel is van dezelfde weg waar andere bestuurders ook mogen rijden, mag je stilstaan om te laden of lossen. Op een apart weggedeelte voor de tram mag je niet stilstaan! ', 'JA', 'NEE', ''),
	(1013, 'P', 2, '326.jpg', 'Deze tram rijdt 40 km/ u. Is het verstandig deze in te halen via rechts?', 'Wanneer een tram 40 km/u rijdt, is het niet verstandig deze in te halen via rechts, ook al mag je een tram via rechts inhalen. Het snelheidsverschil is te groot. Ruimte naast tram is te klein-tram kan gaan stoppen en passagiers moeten kunnen in-uitstappen.', 'NEE', 'JA', ''),
	(1015, 'P', 2, '182.png', 'Moet je stoppen voor de douane of de militaire politie als zij dit van jou vragen? ', 'De douane of Militaire Politie heeft altijd het recht om jou te laten stoppen. ', 'JA', 'NEE', ''),
	(1017, 'P', 1, '109.jpg', 'Welke voertuigen mogen een begrafenisstoet doorkruisen? ', 'De wetgeving geeft aan dat bestuurders van wat voor voertuig dan ook een rouwstoet nooit mag doorkruisen wanneer het eerste voertuig een kruising is overgestoken. ', 'Geen voertuigen ', 'Fietsers', 'Motorvoertuigen '),
	(1018, 'P', 2, '416.jpg', 'Een bus wilt wegrijden binnen de bebouwde kom. Moet een rouwstoet de bus voor laten gaan?', 'Een rouwstoet heeft voorrang t.o.v. een autobus die van een bushalte binnen de bebouwde wil wegrijden.', 'NEE', 'JA', ''),
	(1019, 'P', 1, '230c.jpg', 'Aan welke kant zit het groene licht (groene doorschijnende kap over koplamp) van het laatste voertuig van een militaire kolonne?', 'De laatste auto van een militaire colonne heeft 1 groen licht aan de rechterkant. ', '1 groen licht aan de rechterkant', '1 groen licht aan de linkerkant', ''),
	(1021, 'P', 1, '353.jpg', 'Wie gaat er voor?', 'De begrafenisstoet komt van rechts op een gelijkwaardige kruising en heeft dus voorrang. ', 'De begrafenisstoet', 'De auto', ''),
	(1022, 'P', 1, '230c.jpg', 'Welke kleur vlag(gen) heeft het eerste voertuig van een militaire colonne?', '', 'Blauw', 'Groen', 'Zwart'),
	(1026, 'P', 1, 'ef_310.jpg', 'Waar herken je voorrangsvoertuigen aan?', 'Voorrangsvoertuigen hebben altijd zwaailichten en sirenes. ', 'Sirene en blauw zwaailicht', 'Herkenningstekens zoals vlaggetjes', ''),
	(1029, 'P', 2, '1029.jpg', 'De politie nadert jou van links op een gelijkwaardig kruispunt zonder zwaailichten of sirene. Gaat deze voor?', 'Nee, de politie heeft hier geen sirenes en zwaailicht aan. Tenzij dit specifiek is aangegeven heeft de politie geen voorrang buiten de gewone verkeersregels van een gelijkwaardig kruispunt. ', 'JA', 'NEE', ''),
	(1033, 'P', 2, '331.jpg', 'Moet de auto de tram voor laten gaan?', 'De tram heeft haaientanden en heeft daardoor geen voorrang.', 'NEE', 'JA', ''),
	(1034, 'P', 1, '333.jpg', 'Wie gaat er eerst?', 'Je rijd op een voorrangsweg, daarom mag jij als eerste.', 'Witte auto', 'Blauwe Auto &amp; Fietser', 'Tram'),
	(1038, 'P', 2, '223.jpg', 'Mag je zo links voorsorteren?', 'Ja, een auto mag op de tramrails voorsorteren, tenzij een doorgetrokken streep dat verbiedt.', 'JA', 'NEE', ''),
	(1039, 'P', 2, '205b.jpg', 'Mag je hier stilstaan om te laden en lossen?', 'Op een tramspoor dat deel is van dezelfde weg waar andere bestuurders ook mogen rijden, mag je stilstaan om te laden of lossen. De gele streep op de stoep is onderbroken, hier mag je dus stoppen. \r\n\r\nOp een apart weggedeelte voor de tram mag je niet stilstaan! ', 'JA', 'NEE', ''),
	(1041, 'Q', 2, '361.jpg', 'De bestuurder wilt achteruit rijden. Moet de bestuurder de voetgangers voor laten gaan?', 'Wegrijden valt onder de bijzondere manoeuvres en als je een van die manoeuvres uitvoert, ben je verplicht al het andere verkeer voor te laten gaan.', 'JA', 'NEE', ''),
	(1047, 'Q', 2, '108.jpg', 'Je wilt naar links afslaan. Moet je voorrang verlenen aan de fietser die naar rechts wilt afslaan? ', 'Je begeeft je op een uitrit, dus je moet al het overige verkeer voor laten gaan.', 'JA', 'NEE', ''),
	(1048, 'Q', 1, '221.jpg', 'Je wilt de autosnelweg verlaten. Waar moet je te allen tijde rekening mee houden?', 'De snelheid minder je zoveel mogelijk op de uitvoegstrook en niet op de doorgaande weg. Let op! Niet elke uitvoegstrook is hetzelfde en je hebt daarom niet altijd even lang de tijd om snelheid te minderen.', 'Je moet zoveel mogelijk snelheid minderen op de uitvoegstrook', 'Je moet zoveel mogelijk snelheid minderen op de doorgaande weg', 'Elke uitvoegstrook heeft dezelfde lengte'),
	(1049, 'Q', 2, 'ef_289.JPG', 'Je rijdt een inrit in. Is dit een bijzondere manoeuvre?', 'Het inrijden van een inrit valt onder de bijzondere manoeuvres, je moet dan al het andere verkeer voor laten gaan.\r\n', 'JA', 'NEE', ''),
	(1054, 'Q', 2, '364.jpg', 'Je wilt wegrijden. Moet je richting aangeven?', 'Je moet altijd richting aangeven om de andere weggebruikers te laten weten wat je van plan bent. ', 'JA', 'NEE', ''),
	(1055, 'Q', 2, '360.jpg', 'Je wilt keren op de eenrichtingsweg. Mag dat?', 'Als je in een eenrichtingsweg keert dan rij je tegen het verkeer in. Dit mag dus niet. ', 'NEE', 'JA', ''),
	(1057, 'Q', 2, '354.jpg', 'Je wilt keren op een kruising, mag dat?', 'Op een gelijkvloerse kruising met een middenberm- mag je (180°) keren zolang er geen belemmering voor het overig verkeer plaats vindt en je het kruispunt niet blokkeert.', 'JA', 'NEE', ''),
	(1061, 'Q', 1, '030.jpg', 'Welke bus moet je voor laten gaan als deze binnen de bebouwde kom aangeeft weg te willen rijden van een bushalte?\n', 'Binnen de bebouwde kom moeten bestuurders aan bestuurders van een autobus de gelegenheid geven van een bushalte weg te rijden,wanneer de bestuurder van die autobus door het geven van een teken met zijn richtingaanwijzer zijn voornemen om weg te  rijden kenbaar maakt.Dit geldt niet voor bestuurders van een motorvoertuig dat behoort tot een militaire colonne of een uitvaartstoet van motorvoertuigen.', 'Alleen lijnbussen', 'Alleen touringcars', 'Alle autobussen'),
	(1062, 'Q', 2, '218.jpg', 'Geef je de bus voorrang? ', 'Wanneer een bus aangeeft te willen wegrijden van een bushalte binnen de bebouwde kom dan moet je deze voorrang verlenen. ', 'JA', 'NEE', ''),
	(1063, 'Q', 2, '219.jpg', 'Geef je de bus voorrang?', 'Wanneer een bus aangeeft te willen wegrijden van een bushalte buiten de bebouwde kom, dan hoef je deze geen voorrang te verlenen. ', 'NEE', 'JA', ''),
	(1064, 'Q', 2, '220.jpg', 'Mag je hier iemand uit of af laten stappen? ', 'Bij een bushalte ter hoogte van de blokmarkering mag je alleen iemand snel in- of uit laten stappen wanneer je geen bus hindert: je mag hier niet laden, lossen of parkeren. \r\n', 'JA', 'NEE', ''),
	(1065, 'Q', 2, 'ef_329.jpg', 'Mag je hier iemand uit of af laten stappen? ', 'Bij een bushalte zonder blokmarkering mag je alleen iemand snel in- of uit laten stappen wanneer je geen bus hindert.', 'JA', 'NEE', ''),
	(1066, 'Q', 2, '417a.jpg', 'Mag je hier stilstaan?', 'Als er geen blokmarkering aanwezig is, dan mag je 12 meter voor en na het bushaltebord niet stoppen.', 'NEE', 'JA', ''),
	(1067, 'Q', 2, '418.jpg', 'Mag je hier uitladen? ', 'Bij een bushalte mag je niet laden of lossen. Je mag alleen een passagier in- of uit laten stappen. ', 'NEE', 'JA', ''),
	(1068, 'Q', 2, '129.jpg', 'Je wilt rechtdoor. Moet je de andere auto voor laten gaan?\r\n', 'De andere auto moet wachten, omdat deze zich op een uitritconstructie bevindt. Jij gaat rechtdoor op de doorgaande weg. ', 'NEE', 'JA', ''),
	(1069, 'Q', 1, 'BM10.png', 'Je ziet dit bord. Wat kun je het beste doen als je op de 2e rijstrook rijdt? ', 'Als je op dezelfde rijstrook blijft rijden dan bots je in het verkeer dat op de doorgaande rijstrook rijdt. Hierdoor kun je beter de rechterrijstrook nemen waardoor het veiliger is voor het andere verkeer om in te voegen. ', 'Wissel naar de meest rechterrijstrook', 'Je blijft hier rijden', 'Wissel naar de linkerrijstrook om plaats te maken voor het andere verkeer'),
	(1071, 'R', 2, '226.jpg', 'Je wilt rechts afslaan. Sorteer je zo correct voor?', 'Je mag in dit geval niet voorsorteren op het fietspad, omdat er een doorgetrokken streep langs loopt. Waar je nu bent voorgesorteerd is dus de juiste plek.', 'JA', 'NEE', ''),
	(1073, 'R', 2, '371.jpg', 'Mag je op deze strook blijven rijden als er geen verkeer op de twee rechter rijstroken naast jou zijn en je ook niet aan het inhalen of voorsorteren bent?', 'Je mag niet onnodig links blijven rijden, daarom moet je opschuiven naar de rechter rijstrook.', 'NEE', 'JA', ''),
	(1076, 'R', 1, 'J10+BB23-2L.png', 'Hoeveel meter is het tot de spoorwegovergang?', 'Voor elk rood streepje op deze paaltjes staat een afstand tot de spoorwegovergang van 80 meter. Op dit paaltje staan twee streepjes, dus is de afstand twee keer 80 = 160 meter.', '160 meter', '80 meter', '100 meter'),
	(1077, 'R', 1, '171.jpg', 'Er is geen ruiterpad. Waar moet een ruiter rijden?', 'Als de berm er geschikt voor is, dan rijdt een ruiter in de berm. Op de rijbaan mag ook, een ruiter valt immers onder de bestuurders.', 'Op de rijbaan of in de berm', 'Op het fiets/ bromfietspad', 'Op het trottoir of voetpad'),
	(1079, 'R', 2, 'mtr_020.png', 'Mag je stilstaan op een fietssuggestiestrook?', 'Een suggestiestrook is een deel van de rijbaan dat ook bedoeld is voor motorvoertuigen. Je mag hier dus gewoon op stil staan, parkeren en rijden.', 'JA', 'NEE', ''),
	(1081, 'R', 1, 'ef_023.JPG', 'Wat mag je niet doen in een eenrichtingsweg?', 'Als je keert op een eenrichtingsweg, dan rij je tegen het verkeer in. Dit is uiterst gevaarlijk!', 'Keren', 'Achteruit parkeren', 'Stilstaan aan de linkerkant van de weg'),
	(1087, 'R', 2, '368.jpg', 'Mogen bromfietsers met zijn tweeën naast elkaar rijden?', 'Bromfietsers mogen nooit naast elkaar rijden, tenzij het is om in te halen. Ook niet naast een fietser.', 'NEE', 'JA', ''),
	(1089, 'J', 2, '312b.jpg', 'Mag je hier inrijden?', 'Dit mag, hou er wel rekening mee dat er (brom)fietsers van de andere kant naar je toe kunnen komen. ', 'JA', 'NEE', ''),
	(1093, 'R', 2, '223.jpg', 'Mag je zo voorsorteren wanneer je naar links wilt afslaan?', 'Om voor te sorteren mag je voor een tram staan. ', 'JA', 'NEE', ''),
	(1095, 'R', 2, '373.jpg', 'Je wilt hier links afslaan. Sorteer je zo correct voor?', 'Dit is correct, zo kunnen fietsers en overig verkeer er nog langs via de rechterkant. ', 'JA', 'NEE', ''),
	(1096, 'R', 2, '221.jpg', 'Je wilt rechtsaf. Is dit de juiste rijstrook?', 'Aan de pijlen op de weg kan je zien dat je correct rijdt om rechtsaf te gaan. ', 'JA', 'NEE', ''),
	(1098, 'R', 2, '272a.jpg', 'Mag je wisselen naar de linkerrijstrook?', 'Er is nog voldoende ruimte na de blokmarkering dus dit mag. ', 'JA', 'NEE', ''),
	(1099, 'R', 2, '336.jpg', 'Je wilt nog rechts afslaan, mag dat?', 'Het is te laat, er is geen blokmarkering meer te zien. Hier mag je dus niet meer rechts afslaan!', 'NEE', 'JA', ''),
	(1101, 'R', 1, 'ef_338.jpg', 'Waar rijdt een brommobiel in een normale situatie?', 'Als er een rijbaan aanwezig is, dan moet een brommobiel daar op rijden. In  ', 'Op de rijbaan', 'Op het fiets/ bromfietspad', 'Op het trottoir of voetpad'),
	(1103, 'R', 2, '376.jpg', 'Mag je voorsorteren op een fietssuggestiestrook?', 'Is er rechts van de rijbaan een fietsstrook met onderbroken streep, dan mag je daarop voorsorteren, op voorwaarde dat je fietsers en bestuurders van bromfietsen niet hindert.', 'JA', 'NEE', ''),
	(1104, 'R', 1, 'd02_c04_d_04.png', 'Welk bord geeft een eenrichtingsweg aan?', 'Borden A en C zijn verplichte richtingen. Bord B is een verboden weg, dit betekend dat het hier gaat om een eenrichtingsweg. ', 'Bord B', 'Bord A', 'Bord C'),
	(1107, 'S', 2, 'F01+OB101.png', 'Je haalt hier een tractor in. Mag dat?\r\n', 'Alleen wanneer er een onderbord is geplaatst om tractoren in te halen, mag je deze inhalen. ', 'JA', 'NEE', ''),
	(1111, 'S', 1, '131b.jpg', 'Jouw auto en aanhanger zijn samen 7.20m lang. Mag je inhalen via de meest linkerrijstrook?   ', 'Alleen om voor te sorteren mag je gebruik maken van de meest linkerrijstrook. Deze weg leidt bv. naar een andere stad. Wanneer er weer meerdere rijstroken zijn, verplaats je je uiteraard weer naar de meest rechterrijstrook. ', 'Alleen wanneer je gaat voorsorteren', 'Ja, dat mag', 'Nee, dat mag niet'),
	(1114, 'S', 1, '305.jpg', 'Welke volgafstand houd je met inhalen?', 'Houd altijd 2 seconden afstand. ', '2 seconden', '1 seconde', '3 seconden'),
	(1119, 'S', 1, '378.jpg', 'Wat weet je over dit voertuig wanneer je deze wilt inhalen?', 'Dit zie je aan de markeringen achter op de vrachtwagen. ', 'Dit voertuig is erg lang, daarom moet ik de afstand om in te halen goed inschatten ', 'Dit voertuig trekt veel langzamer op dan mijn voertuig, dit maakt inhalen gemakkelijker', ''),
	(1120, 'S', 1, '307.jpg', 'Je staat in de file. Wat verwachten motorfietsen van jou?', 'Door naar rechts uit te wijken maak je ruimte voor de motorfiets. ', 'Dat je uitwijkt naar rechts', 'Dat je uitwijkt naar links', 'Niks'),
	(1121, 'S', 2, '379.jpg', 'Je haalt hier in, mag dat?', 'Bij een onderbroken streep mag je elk voertuig inhalen. ', 'JA', 'NEE', ''),
	(1122, 'S', 2, '380.jpg', 'Je wilt naar de rechter rijstrook, mag dat?', 'Dit mag niet, dit zie je door de doorgetrokken streep. Dit is vaak zo bij een invoegstrook. ', 'NEE', 'JA', ''),
	(1124, 'S', 2, '382.jpg', 'De motorfiets haalt hier in. Is dat veilig?', 'Het is een onoverzichtelijk kruispunt met meer verkeer. Dit is dus niet veilig. ', 'NEE', 'JA', ''),
	(1125, 'S', 2, 'F01.png', 'Mag je bij dit bord een brommobiel inhalen?', 'Ook een brommobiel mag je niet inhalen bij bord F1. ', 'NEE', 'JA', ''),
	(1137, 'S', 2, '384.jpg', 'Je wilt wisselen naar de linker rijstrook. Mag dat?', 'Ook al zijn de strepen vervaagt of versleten, zijn ze nog steeds geldig. Je mag hier dus niet wisselen van baan. ', 'NEE', 'JA', ''),
	(1139, 'S', 2, 'F01.png', 'Je haalt hier een tractor in. Mag dat?', 'Nee, bij dit bord is het verboden om auto\'s, motorfietsen en landbouwvoertuigen in te halen. ', 'NEE', 'JA', ''),
	(1140, 'S', 2, '385.jpg', 'Je haalt zo in. Houd je genoeg afstand?', 'Er zit ongeveer een meter afstand tussen jou en de auto, dit mag dus. ', 'JA', 'NEE', ''),
	(1144, 'S', 1, '171.jpg', 'Je wilt een ruiter inhalen. Hoe ga je te werk?', 'Het is gevaarlijk om je claxon te gebruiken bij het inhalen van een ruiter. Het paard kan zo schrikken en uitwijken waardoor er ongelukken kunnen ontstaan.', 'Je wijkt uit en past jouw snelheid aan', 'Je gebruikt de claxon en wijkt uit', 'Je gebruikt de claxon en past jouw snelheid aan'),
	(1148, 'S', 2, '326.jpg', 'Mag je een tram via de rechterkant inhalen? ', 'Een tram die in het midden van de rijbaan rijdt, mag je langs rechts inhalen.\r\n\r\nLet wel goed op voor fietsers en bromfietsers.', 'JA', 'NEE', ''),
	(1149, 'T', 1, '033.png', 'Welke informatie kun je vinden op een hectometerpaaltje?\r\n', 'Op een hectometerpaaltje kun je het routenummer, het deel van de weg, de kilometeraanduiding en de maximumsnelheid vinden. De maximumsnelheid wordt soms echter achterwege gelaten.\r\n', 'Routenummer, wegdeel, kilometeraanduiding, maximumsnelheid.', 'Routenummer, kilometeraanduiding, maximumsnelheid.', 'Routenummer, maximumsnelheid.'),
	(1151, 'T', 1, '301.jpg', 'Hoe hard mag een bestelauto op de snelweg rijden met een aanhangwagen (totale gewicht 3000kg)?', 'De maximumsnelheid van een auto met aanhangwagen op de snelweg is 90km/u. ', '90 km/u', '80 km/u', '50 km/u'),
	(1152, 'T', 1, 'a01-50_h1.png', 'Welk bord geeft de bebouwde kom aan? ', 'Bord B: ‘Amsterdam’ geeft aan dat je de bebouwde kom inrijdt. Bord A geeft een maximumsnelheid aan. ', 'Bord B', 'Bord A', ''),
	(1155, 'T', 2, '030.jpg', 'Moet je een gordel dragen in een T100 bus?', 'In een T100 bus is het verplicht dat er gordels aanwezig zijn op elke zitplaats. Als deze aanwezig zijn, ben je ook verplicht deze om te doen. ', 'JA', 'NEE', ''),
	(1159, 'T', 1, 'B01.png', 'Dit bord is geplaatst na de kruising. Waar ben je nu?\r\n', 'Is het bord ‘voorrangsweg’ geplaatst na een kruising, dan bevind je je buiten de bebouwde kom. ', 'Buiten de bebouwde kom', 'Binnen de bebouwde kom', ''),
	(1163, 'T', 1, '043b.jpg', 'De maximumsnelheid op deze weg is op dit moment...?', 'Bij een aangepaste snelheid op een matrixbord moet je altijd de aangegeven snelheid aanhouden. ', '70km/u', '120km/u', '100km/u'),
	(1164, 'T', 1, 'A02-80.png', 'Wat betekent dit bord? ', 'Dit bord betekend einde maximumsnelheid. ', 'Einde maximumsnelheid', 'Einde alle verboden', 'Einde van adviessnelheid'),
	(1165, 'T', 1, '388.jpg', 'De maximumsnelheid op deze weg is…..?', 'Dit bord geeft aan dat je de bebouwde kom ingaat. Hier mag je dus 50km/u.', '50 km/u', '60 km/u', '80 km/u'),
	(1167, 'T', 1, '389a.jpg', 'De maximumsnelheid op deze weg is…..? ', 'Ondanks dat dit een voorrangsweg is, rijd je binnen de bebouwde kom. Hier mag je dus maar 50km/u. ', '50 km/u', '60 km/u', '80 km/u'),
	(1178, 'T', 1, '395.jpg', 'Je rijdt 80 km/u. Wat is het meest verstandig om te doen?', 'Je rijd te dicht op de vrachtwagen. Hierdoor moet je je afstand tussen jou en de vrachtwagen vergroten. ', 'Vergroot de afstand tussen jou en de vrachtwagen', 'Verklein de afstand tussen jou en de vrachtwagen', ''),
	(1182, 'T', 1, 'BB07R-A16-52,8.png', 'Wat geeft dit bord aan?', 'Door het ronde bordje met de rode rand boven het hectometer bord zie je dat dit de maximumsnelheid aangeeft voor deze weg. ', 'Maximumsnelheid', 'Minimumsnelheid', 'Adviessnelheid'),
	(1187, 'T', 1, 'a03-80_A04-80.png', 'Welk bord geeft een maximumsnelheid aan?', 'Het matrixbord geeft de maximumsnelheid aan. Het andere bord is een adviessnelheid. ', 'Bord A', 'Bord B', ''),
	(1200, 'T', 1, '390.jpg', 'Wat is de maximumsnelheid voor een auto met een aanhanger op de autoweg? \r\n', 'De maximumsnelheid voor een auto met een aanhanger op de autoweg is 90 km/u.', '90 km/u', '100 km/u', '120 km/u'),
	(1201, 'T', 1, '181.png', 'Wat gebeurt er met de middelpuntvliedende kracht (centrifugale kracht) wanneer je jouw snelheid verdubbelt?\r\n', 'De centrifugale kracht verviervoudigt wanneer je je snelheid verdubbelt. ', 'De kracht verviervoudigt', 'De kracht verdubbelt', 'De kracht verdriedubbelt'),
	(1206, 'T', 1, '185.png', 'Hoe hard mag een gehandicaptenvoertuig (met motor) rijden op het voetpad?\r\n', 'Met een gehandicaptenvoertuig, met of zonder motor, op een voetpad mag u maximaal 6 km/u.', '6 km/u', '10 km/u', '25 km/u'),
	(1213, 'U', 2, '317.jpg', 'Mag je parkeren op de rijbaan/wegkant van een voorrangsweg binnen de bebouwde kom?', 'Als er niet expliciet een verbod is aangegeven of als je geen gevaar of hinder veroorzaakt, mag je daar gewoon parkeren.', 'JA', 'NEE', ''),
	(1215, 'U', 2, '017.jpg', 'Moet je altijd in de parkeervakken parkeren binnen een erf?', 'Binnen een erf moet je altijd binnen de vakken parkeren, anders zou je wel eens flinke hinder kunnen veroorzaken.', 'JA', 'NEE', ''),
	(1216, 'U', 2, '085.jpg', 'Je wilt iemand uit of af te laten stappen en doet dit op een fietsstrook met een onderbroken streep. Mag dit?\n', 'Nee, je mag niet stil staan op of naast weggedeelten die bestemd zijn voor andere weggebruikers (zoals fietsers).', 'NEE', 'JA', ''),
	(1224, 'U', 2, '278a.jpg', 'Mag je hier parkeren?', 'Het is verboden om op de berm van een autoweg te parkeren. ', 'NEE', 'JA', ''),
	(1225, 'U', 2, '402a.png', 'Mag je hier parkeren?', 'Parkeren in de berm binnen de bebouwde kom mag. Het bord geld pas als je er voorbij bent.  ', 'JA', 'NEE', ''),
	(1226, 'U', 2, '406.png', 'Mag je hier parkeren? ', 'Parkeren in de berm buiten de bebouwde kom mag, echter staat hier een bord dat aangeeft dat dit niet mag. ', 'NEE', 'JA', ''),
	(1229, 'U', 2, '403a.jpg', 'Mag je hier stoppen?', 'De gele doorgetrokken streep geldt voor de andere kant van de weg, dit mag dus. Tegen het verkeer in stoppen aan de andere kant van de weg mag ook.', 'JA', 'NEE', ''),
	(1237, 'U', 2, '400g.png', 'Mag je parkeren naast een gele onderbroken streep?\r\n', 'Naast een gele onderbroken streep mag je niet parkeren. ', 'NEE', 'JA', ''),
	(1276, 'U', 2, '403b.jpg', 'Mag de auto op deze manier parkeren?', 'In Nederland mag je tegen het verkeer in parkeren, dit mag dus. De gele streep geld alleen voor de andere kant. ', 'JA', 'NEE', ''),
	(1283, 'U', 2, 'efvkr_264.jpg', 'Mag je in een tunnel parkeren?', 'Het is verboden te stoppen en parkeren in een tunnel. ', 'NEE', 'JA', ''),
	(1284, 'U', 2, 'ef_289.JPG', 'Mag je binnen een erf op de rijbaan parkeren?', 'Binnen een erf is het alleen toegestaan om in de parkeervakken te parkeren. ', 'NEE', 'JA', ''),
	(1292, 'U', 1, 'E01.png', 'Aan welke kant van de weg geldt dit bord?', 'Dit bord geldt voor de zijde waar het bord geplaatst is.', 'Aan de rechterkant', 'Aan de linkerkant', 'Beide kanten'),
	(1295, 'U', 2, '062.jpg', 'Een fietser wil over het zebrapad fietsen, moet de auto deze voor laten gaan?', 'Op een zebrapad hebben voetgangers en bestuurders van een gehandicaptenvoertuig voorrang.  Dus geen fietsers. ', 'NEE', 'JA', ''),
	(1301, 'U', 2, '079.jpg', 'Mag je hier iemand uit laten stappen?', 'De vluchthaven is alleen bestemd voor noodgevallen. Stoppen is hier dus verboden tenzij het een noodgeval is. ', 'NEE', 'JA', ''),
	(1307, 'V', 2, '221.jpg', 'Je wilt de afslag nemen. Moet je richting aangeven?', 'Om aan het overige verkeer duidelijk te maken dat je echt van plan bent om de afslag te nemen, geef je richting aan naar rechts. Zo weet het overige verkeer dat jij niet van plan bent om weer in te voegen op de doorgaande rijbaan.', 'JA', 'NEE', ''),
	(1310, 'V', 2, '134.jpg', 'Een begrafeniswagen rijdt zonder vlaggen. Moet je deze voorrang verlenen?\r\n', 'Een begrafeniswagen heeft alleen voorrang bij het rijden met vlaggen en alarmlichten. ', 'NEE', 'JA', ''),
	(1312, 'V', 1, '135.jpg', 'Waarom gebruikt het zwarte voertuig zijn waarschuwingslichten?', 'Om aan te geven dat je een file nadert en er flink hard geremd moet worden, kun je je waarschuwingslichten gebruiken.', 'Het voertuig waarschuwt voertuigen achter hem voor de file.', 'Omdat het voertuig motorproblemen heeft.', 'Omdat het voertuig in een konvooi rijdt.'),
	(1314, 'V', 1, 'C04-R.png', 'Je wilt de weg met verplichte rijrichting oprijden, moet je richting aangeven?', 'Je moet altijd richting aangeven als je afslaat, ook als je de andere kant niet op mag rijden.\n', 'Ja, je moet altijd richting aangeven', 'Nee, je kan maar een kant op', ''),
	(1315, 'V', 1, 's_012.png', 'Waar mag je, bij direct gevaar, met je lichten knipperen?', 'Als er direct gevaar is en je kan door je lichten te knipperen andere bestuurders hiermee waarschuwen, dan is dit altijd toegestaan. ', 'Beide', 'Binnen de bebouwde kom', 'Buiten de bebouwde kom'),
	(1317, 'V', 1, '342.jpg', 'Je hebt motorproblemen. Op hoeveel afstand plaats je de gevarendriehoek op z’n minst?', 'Een gevarendriehoek plaats je altijd 30 meter van je auto af. ', '30 meter', '1 meter', '10 meter '),
	(1319, 'V', 1, '133.jpg', 'Wie moet een signaal geven als er gevaar dreigt?', 'Alle bestuurders dienen een signaal te geven als er gevaar dreigt. ', 'Alle bestuurders', 'Auto’s en motorfietsen', 'Bestuurders van motorvoertuigen'),
	(1320, 'V', 2, 's_012.png', 'Mag je jouw vrienden begroeten met een lichtsignaal?', 'Dit mag alleen om aan te geven dat er gevaar dreigt. ', 'NEE', 'JA', ''),
	(1321, 'V', 1, '401c.jpg', 'Welk signaal geef je aan het overige verkeer als je geparkeerd staat met pech?\r\n', 'Je zet pas een gevarendriehoek neer als je je alarmlichten aan hebt, die zijn verplicht. ', 'Je zet de alarmlichten aan', 'Alleen de gevarendriehoek', 'Je zet de richtingaanwijzer aan naar links en rechts'),
	(1322, 'V', 2, '401b.jpg', 'Je staat met pech geparkeerd op de vluchtstrook. Je hebt jouw alarmlichten aanstaan. Moet je ook de gevarendriehoek plaatsen indien je die bij je hebt?', 'Een gevarendriehoek bij je hebben is niet verplicht in Nederland, maar als je er eentje bij je hebt is het wel verplicht deze te plaatsen.', 'JA', 'NEE', ''),
	(1324, 'V', 2, '128.jpg', 'Je gaat inhalen. Moet je richting aangeven?\r\n', 'Ook bij het inhalen manoeuvres moet je altijd je richting aangeven. Op deze plek (voor zebrapad) mag je echter niet inhalen. ', 'JA', 'NEE', ''),
	(1325, 'W', 2, '078.jpg', 'Ben je verplicht om verlichting te voeren bij het rijden in een tunnel?', 'Het is verplicht wanneer je in een tunnel rijdt dimlicht te voeren, zowel overdag als \'s nachts.', 'JA', 'NEE', ''),
	(1326, 'W', 2, '154.jpg', 'Het regent hard. Je zet dimlichten aan. Mag dit?', 'Dit is zelfs wenselijk. Door het gebruik van dimlichten zie je beter en ben je zelf ook beter zichtbaar.', 'JA', 'NEE', ''),
	(1327, 'W', 2, '171.jpg', 'Moeten ruiters ook voor- en achterlicht voeren?', 'Een lamp met een rood licht moet naar achteren stralen en een wit of geel licht naar voren stralen. ', 'JA', 'NEE', ''),
	(1331, 'V', 1, 'ef_054.JPG', 'Het regent hard en je kunt minder zien dan 50 meter. Wat doe je in deze situatie? ', 'Wanneer het regent, mag je jouw mistachterlicht niet gebruiken.', 'Je rijdt niet harder dan 50 km/u en je rijdt niet te dicht op de auto voor je.', 'Je zet jouw achtermistlicht aan en rijdt niet harder dan 50 km/u.', ''),
	(1332, 'W', 1, '178.jpg', 'Een tegemoetkomend voertuig verblind je met zijn groot licht, wat doe je? \n', 'Wanneer deze jou verblindt met zijn groot licht, kun je het beste jouw hoofd wegdraaien van het licht. Dit is de veiligste optie. Extra veilig is nog je gas los laten.', 'Je draait je hoofd weg van het licht ', 'Je gebruikt de claxon', 'Je zet jouw groot licht aan'),
	(1335, 'W', 2, '412.jpg', 'Mag je rijden zonder kentekenplaat verlichting?', 'Aan de voor- en achterkant van auto’s en motoren moet een rechthoekige kentekenplaat zijn bevestigd. De kentekenplaat aan de achterkant van de auto en/of motor moet worden verlicht als de verlichting van het voertuig brandt.', 'NEE', 'JA', ''),
	(1339, 'V', 2, 'ef_317.jpg', 'Moet een gekoppelde aanhangwagen verlichting voeren?', 'Gekoppelde aanhangwagens moeten bij dag, indien het zicht ernstig wordt belemmerd, en bij nacht, achterlicht en verlichting van de achterkentekenplaat hebben.', 'JA', 'NEE', ''),
	(1340, 'V', 2, 's_005a.png', 'Je rijdt in de mist. Het zicht is minder dan 50m. Je zet jouw achtermistlicht aan, is dat correct? \r\n', 'Bij mist met het zicht minder dan 50m moet je je mistlicht aanzetten. ', 'JA', 'NEE', ''),
	(1341, 'W', 1, '213.jpg', 'Welk licht zet je aan in een tunnel? ', 'In een tunnel zet je altijd je dimlicht aan. ', 'Dimlicht', 'Je zet jouw grootlicht aan', 'Geen licht'),
	(1342, 'W', 2, 's_005achter.png', 'Je rijdt in de mist. Het zicht is minder dan 100m. Je zet jouw achtermistlicht aan, is dit correct? \r\n', 'Je mag alleen je mistlicht aanzetten als het zicht 50 meter of minder is. ', 'NEE', 'JA', ''),
	(1343, 'W', 2, '037b.jpg', 'Het sneeuwt, je kan ongeveer 100 meter voor je uitkijken. Mag je nu jouw achtermistlicht aanzetten? ', 'Dit mag alleen als het zicht minder is dan 50 meter.', 'NEE', 'JA', ''),
	(1347, 'W', 2, '153.jpg', 'Wordt ‘dagrijlicht’ gebruikt in combinatie met andere lichten? ', 'Dit mag niet, tenzij het niet anders kan met nieuwe voertuigen die zo gemaakt zijn, maar dan heb je geen keus. ', 'NEE', 'JA', ''),
	(1349, 'W', 2, 's_26.png', 'Mag je parkeerlichten aanzetten buiten de bebouwde kom? \n', 'Dit is zelfs verplicht bij het parkeren. ', 'JA', 'NEE', ''),
	(1351, 'W', 2, '407.jpg', 'Je rijdt hier met grootlicht aan, mag dat?', 'Buiten de bebouwde kom mag je met grootlicht aan rijden. Let wel goed op tegenliggend verkeer en meerijdend verkeer voor je. ', 'JA', 'NEE', ''),
	(1352, 'W', 2, '410.jpg', 'Je rijdt hier met dimlicht aan, mag dat?', 'Binnen de bebouwde kom mag je met dimlicht aan rijden. ', 'JA', 'NEE', ''),
	(1358, 'W', 2, 'ef_302.jpg', 'Moet de kentekenplaat van een gekoppelde aanhangwagen verlicht zijn ‘s nachts?\r\n', 'Ook de kentekenplaat van een gekoppelde aanhangwagen is verplicht om zichtbaar te zijn, deze moet dus ook verlicht worden als de lampen van de aanhangwagen branden. ', 'JA', 'NEE', ''),
	(1359, 'W', 1, 's_23a.png', 'Je hebt de laagstaande zon in je rug, wat doe je om je zichtbaarheid te vergroten?\r\n', 'Om je zichtbaarheid te vergroten zet je je dimlichten aan.', 'Dimlicht aanzetten', 'Grootlicht aanzetten', 'Stadslicht aanzetten'),
	(1362, 'Y', 1, '192.jpg', 'Je rijdt 50 km/u en je hebt 50 meter om tot stilstand te komen. Het verkeerslicht springt op ‘geel’. Wat doe je nu?', 'Je hebt nog genoeg afstand om rustig te remmen en tot stilstand te komen. Heel hard remmen is niet aan te raden en veel gas erbij geven ook niet. \r\nAls je nagaat hoeveel meter er nodig is om tot stilstand te komen bij 50 km/u, ga je de stopafstand na. De stopafstand is de reactieafstand + de remafstand. De reactieafstand = (snelheid/4)+10% en de remafstand = (snelheid/10)²/2. Voor de stopafstand betekent dat 13,75 + 12,5 = 26,25 meter. ', 'Rustig remmen ', 'Heel hard remmen', 'Ik geef veel gas bij'),
	(1363, 'Y', 2, '107.jpg', 'Er rijdt een auto van Rijkswaterstaat voor je en er brandt ‘STOP’ vanuit de achterruit. Moet je nu stoppen?', 'Rijkswaterstaat is bevoegd om voertuigen tot stilstand te brengen. Als zij jou een stopteken geven, heb je je daar aan te houden.', 'JA', 'NEE', ''),
	(1364, 'Y', 2, '103.jpg', 'Er staat een klaar-over op de weg. Moet je stoppen?', 'Een klaar-over die op de weg staat, geeft aan dat er nu voetgangers mogen oversteken, daarom moet jij het teken om te stoppen van de klaar-over opvolgen.', 'JA', 'NEE', ''),
	(1365, 'Y', 1, '265e.png', 'Wie krijgt een stopteken? ', 'Het verkeer van achter de agent wordt als het ware tegengehouden door zijn opgestoken arm. Het verkeer van achter de agent moet dus stoppen. ', 'Verkeer dat de verkeersagent van achteren nadert', 'Verkeer dat de verkeersagent van voren nadert ', 'Verkeer dat de verkeersagent van links nadert'),
	(1366, 'Y', 2, '020.jpg', 'Een touringcar rijdt op een lijnbus strook. Mag dit?', 'Een ‘lijnbus’ strook is speciaal voor lijnbussen. Hier mogen geen touringcars rijden.', 'NEE', 'JA', ''),
	(1367, 'Y', 2, '019.jpg', 'Mag je op een busstrook stoppen?', 'Je mag niet stil staan op of naast weggedeelten die bedoeld zijn voor een ander soort weggebruikers, zoals bussen. Een busstrook is bedoeld voor alle bussen, inclusief lijnbussen.', 'NEE', 'JA', ''),
	(1371, 'Y', 2, '230b.jpg', 'De militaire colonne is de kruising aan het oversteken. Het eerste voertuig is al op de kruising, en de volgauto\'s rijden door rood. Moet je nu voorrang verlenen?', 'Wanneer een militaire colonne begonnen is met rijden door het groene licht en het wordt rood, mogen ze door rood rijden. Jij mag dat niet en moet dus voorrang verlenen \n', 'JA', 'NEE', ''),
	(1372, 'Y', 1, '422.jpg', 'Op de achterkant van dit voertuig is een geel knipperend licht bevestigd. Waar waarschuwt deze voor?', 'Een geel knipperend licht op een voertuig betekent: let op, dit is uitzonderlijk vervoer. ', 'Uitzonderlijk vervoer', 'De lading van de vrachtwagen is 3 meter breed', 'Deze vrachtwagen rijdt niet sneller dan 50 km/u'),
	(1373, 'Y', 1, '265f.png', 'Wie krijgt een stopteken? ', 'Het verkeer dat de verkeersregelaar van voren nadert, moet nu stoppen. Je rijdt aan de rechterkant, dus je rijdt als het ware tegen zijn hand aan. ', 'Verkeer dat de verkeersregelaar van voren nadert ', 'Verkeer dat de verkeersregelaar van achteren nadert', 'Verkeer dat de verkeersregelaar van links nadert'),
	(1375, 'Y', 1, '366.png', 'Bij welk stoplicht moet je extra op het tegemoetkomend verkeer letten? \r\n', 'Wanneer er geen pijl op het verkeerslicht zichtbaar is, geldt dit voor alle bestuurders die van voren naderen. Hier kun je wel tegemoetkomend verkeer verwachten, dus bestuurders die links of rechts willen afslaan of die rechtdoor willen. Let bij dergelijke kruisingen dus extra goed op. ', 'Stoplicht A', 'Stoplicht B', ''),
	(1376, 'Y', 1, '028.png', 'Voor wie moet je altijd stoppen als zij dit aangeven? ', 'Voor alle daartoe bevoegde en als zodanig kenbare ambtenaren moet je stoppen wanneer zij dit van jou eisen. ', 'Alle daartoe bevoegde en als zodanig kenbare ambtenaren', 'Personen die het verkeer regelen', 'Verkeerspolitie'),
	(1378, 'Y', 2, 'efvkr_022.jpg', 'Moet je altijd stoppen voor rood licht?\r\n', 'Voor rood licht moet je altijd stoppen. ', 'JA', 'NEE', ''),
	(1379, 'Y', 2, '034.png', 'Het verkeerslicht is groen, maar er kruisen jou nog steeds voertuigen. Volg je het groene licht op?', 'Voor de algehele veiligheid, stop je en wacht je totdat het kruispunt vrij is en je ongehinderd kunt verder rijden. Ook al heb je voorrang, je moet het alleen nemen als het hier veilig genoeg voor is. ', 'NEE', 'JA', ''),
	(1380, 'Y', 2, 'ef_344.jpg', 'Er brandt een verkeerslicht rood en het symbool is een fiets. Moet jij nu stoppen?', 'Wanneer het symbool van de fiets geprojecteerd is in het verkeerslicht dan geldt dit voor fietsers, snorfietsers en bromfietsers die het verplicht fiets-/ bromfietspad volgen. ', 'NEE', 'JA', ''),
	(1382, 'Z', 2, 'efvkr_051.jpg', 'Een doorgetrokken streep bevindt zich aan jouw kant, aan de andere kant is een onderbroken streep. Mag je nu inhalen?\r\n', 'Als de doorgetrokken streep zich aan jouw kant bevindt dan mag je daar niet overheen en mag je dus niet inhalen.', 'NEE', 'JA', ''),
	(1383, 'Z', 2, '068.jpg', 'De spitsstrook is open. Mag ik over de doorgetrokken streep heen rijden om van de spitsstrook gebruik te maken?\n', 'Normaal gesproken geldt de strook rechts van de doorgetrokken streep als vluchtstrook en mag je daar alleen gebruik van maken in noodgevallen. In het geval dat de spitsstrook open is, geldt die strook als gewone rijstrook en mag je dus over de doorgetrokken streep heen rijden.', 'JA', 'NEE', ''),
	(1384, 'Z', 1, '057.jpg', 'Op het wegdek bevinden zich gele en witte markeringen. Waar moet ik nu rijden?\r\n', 'Als je gele wegmarkeringen ziet, moet je je daar aan houden.', 'Tussen de gele strepen', 'Tussen de witte strepen', ''),
	(1385, 'Z', 2, '115.jpg', 'Mag je op een verdrijvingsvlak parkeren?', 'Het is verboden om je met een voertuig op een verdrijvingsvlak te bevinden.', 'NEE', 'JA', ''),
	(1386, 'Z', 2, 'efvkr_040.jpg', 'Moet je altijd stoppen voor haaientanden?', 'Zolang de haaientanden niet gepaard gaan met een stopgebod en er komt geen verkeer aan waaraan je voorrang moet verlenen, hoef je niet te stoppen.', 'NEE', 'JA', ''),
	(1388, 'Z', 2, '096.jpg', 'Ik sta op een voorsorteerstrook naar links, maar ik wil naar rechts. Mag ik nog veranderen van rijstrook?', 'Wanneer je voorgesorteerd staat mag je niet meer van richting veranderen. ', 'NEE', 'JA', ''),
	(1389, 'Z', 2, '343.jpg', 'Er is geen ‘STOP’ bord aanwezig.\r\nMoet ik stoppen voor deze streep?', 'Stopstrepen gaan altijd samen met een stopteken. Mocht dit om een bepaalde reden toch niet het geval zijn, dan ben je nog steeds verplicht voor de stopstreep te stoppen.  ', 'JA', 'NEE', ''),
	(1392, 'Z', 2, '413a.jpg', 'Je wilt de eerste afslag naar links, mag dat?', 'Deze voorsorteringspijl heeft een geblokkeerde afsplitsing. Dit betekent dat je niet de eerste afslag naar links mag nemen. De tweede afslag mag je wel naar links. ', 'NEE', 'JA', ''),
	(1393, 'Z', 2, '413b.jpg', 'Mag je voorsorteren op deze pijl op het wegdek?', 'Op deze pijl op het wegdek mag je voorsorteren. Deze voorsorteringspijl heeft een geblokkeerde afsplitsing. ', 'JA', 'NEE', ''),
	(1394, 'Z', 2, '016.jpg', 'Mag je op over een puntstuk rijden om nog van baan te verwisselen?', 'Van een puntstuk mag je nooit gebruik maken. Dit kan voor gevaarlijke situaties zorgen.', 'NEE', 'JA', ''),
	(1395, 'Z', 2, 'efvkr_203.jpg', 'Mag je op een verdrijvingsvlak rijden wanneer de spitsstrook gesloten is?', 'Je mag niet op een verdrijvingsvlak rijden. ', 'NEE', 'JA', ''),
	(1396, 'Z', 2, 'efvkr_234.jpg', 'Mag je op een verdrijvingsvlak rijden wanneer de spitsstrook open is?', 'In het algemeen mag je van een verdrijvingsvlak geen gebruik maken, tenzij je een spitsstrook volgt, waarbij er een samenvoeging van wegen is.', 'JA', 'NEE', ''),
	(1397, 'Z', 2, 'constructie_rechts.png', 'Er is een omleiding. Moet ik de richting van de gele pijl volgen?', 'Je kunt de richting van de gele pijl volgen, wanneer je de omleiding wilt volgen. Het is geen verplichte rijrichting. ', 'NEE', 'JA', ''),
	(1399, 'Z', 2, '039.jpg', 'Je rijdt op de rechterweghelft. Mag je je verplaatsen naar de linkerbaan?\r\n', 'Er bevindt zich een onderbroken streep aan jouw kant, daarom mag je je verplaatsen naar de linkerbaan. ', 'JA', 'NEE', ''),
	(1408, 'X', 2, '034.png', 'Je reed net door het groene licht zonder te stoppen. Mag dat?', 'Ja, dat mag. Een verkeerslicht gaat boven borden of tekens op de weg, daarom geldt het bord niet als het verkeerslicht werkt. Mocht het verkeerslicht echter kapot zijn of uit staan dan moet je je wel houden aan de regels die je opgelegd worden door middel van de aangegeven borden of tekens op de weg.\r\n', 'JA', 'NEE', ''),
	(1409, 'X', 1, 'c02_d05.png', 'Bij welk bord mag je naar links afslaan? ', 'Bij bord A bevind je je op een eenrichtingsweg, daar mag je wel links of rechts afslaan, je mag alleen niet 180º de andere kant op rijden. Bord B geeft een verplichte rijrichting aan, daar moet je dus rechtsaf en mag je niet links afslaan.', 'Bord A', 'Bord B', ''),
	(1412, 'X', 2, '281.jpg', 'Je wilt naar links, mag dat?', 'Er staat geen verbod om links af te slaan. Het bord D2 is van belang als je rechtdoor wilt, het geeft namelijk aan dat je dan de rechterrijstrook moet nemen. \r\n', 'JA', 'NEE', ''),
	(1413, 'X', 1, 'f06_f05.png', 'Bij welk bord moet je voorrang verlenen aan tegemoetkomende bestuurders?', 'Dit is te zien aan de kleuren van de pijlen. Als de pijl naar boven rood is dan heb je geen voorrang\r\n\r\nAls de pijl naar boven wit is, dan wel. ', 'Bord B', 'Bord A', ''),
	(1422, 'X', 1, '236a.jpg', 'Bij welk bord kun je verkeer van rechts verwachten?', 'Het linkerbord betekent dat er een omleiding is, hier kan nog steeds verkeer van rechts aankomen.\nHet rechterbord betekent dat het een eenrichtingsweg is, hier komt dus geen verkeer van rechts. ', 'Het linkerbord', 'Het rechterbord', 'Beide'),
	(1424, 'X', 1, '236c.jpg', 'Bij welk bord kun je verkeer van de linkerkant verwachten?', 'Allebei deze borden betekenen dat het een eenrichtingsweg is. \r\n\r\nBij het linkerbord is het eenrichtingsverkeer van links naar rechts dus alleen hier komt het verkeer van de linkerkant. ', 'Het linkerbord', 'Het rechterbord', 'Beide'),
	(1432, 'X', 1, 'K14.png', 'Wat betekent dit bord? ', 'Wanneer je gevaarlijke stoffen vervoert, dan moet je deze route volgen.', 'Vervoer van gevaarlijke stoffen', 'Uitvoegstrook autosnelweg', 'Omleidingsroute'),
	(1443, 'X', 2, 'J11.png', 'Mag je inhalen op een onbewaakte spoorwegovergang?', 'Voor of op een onbewaakte spoorwegovergang inhalen is al gevaarlijk op zich.', 'NEE', 'JA', ''),
	(1449, 'X', 2, 'C11.png', 'Je rijdt met de auto deze straat in, mag dat?', 'Dit bord betekend dat deze weg verboden is voor motorrijders. Als auto mag je dus wel deze straat in. ', 'JA', 'NEE', ''),
	(1450, 'X', 2, 'J20.png', 'Geldt deze waarschuwing alleen als de weg nat is?', 'Deze waarschuwing geldt altijd, tenzij er een onderbord onder zit dat iets anders aangeeft. ', 'NEE', 'JA', ''),
	(1453, 'X', 2, 'J05.png', 'Verwacht je een bocht naar rechts als je dit bord ziet?', 'Bij dit bord verwacht je een bocht naar links aangezien de pijl deze richting op wijst. ', 'NEE', 'JA', ''),
	(1458, 'X', 2, 'c02_ob11.png', 'Jouw auto en aanhangwagen zijn 7 meter lang. Mag je hier rechtdoor rijden?', 'Ja, deze weg is alleen verboden voor vrachtwagens. Hoe lang het ook is, een auto met een aanhangwagen is nog steeds geen vrachtwagen. ', 'JA', 'NEE', ''),
	(1459, 'X', 1, 'B01.png', 'Wat betekent dit bord?', 'Dit bord betekent dat je op een voorrangsweg rijdt. ', 'Voorrangsweg', 'Voorrangskruising', 'Voorrang verlenen'),
	(1470, 'X', 2, 'B07.png', 'Moet je bij dit bord stoppen?', 'Bij dit bord moet je altijd stoppen, ook als er geen verkeer aankomt. ', 'JA', 'NEE', ''),
	(1490, 'I', 1, '237b.jpg', 'Wat is hier de juiste volgorde? ', '', 'Auto, Fietser, Motorfiets', 'Motorfiets, Fietser, Auto', 'Fietser, Motorfiets, Auto'),
	(1491, 'I', 1, '238b.jpg', 'Wie gaat er als laatste? ', 'De fietser blijft verkeer van rechts houden nadat de motor als eerste is gegaan. Daarom gaat hij als laatste.', 'Fietser', 'Auto', 'Motor'),
	(1492, 'I', 1, '239b.jpg', 'Wat is hier de juiste volgorde?', 'De fietser en motor rijden op een voorrangsweg, hiervan gaat de fietser rechtdoor dus deze gaat eerst. Daarna de motor. Hierna gaat de oranje auto rechtdoor dus deze gaat nu en als laatste de blauwe auto. ', 'Fietser, Motor, Oranje Auto, Blauwe Auto', 'Motor, Fietser, Oranje Auto, Blauwe Auto', 'Fietser, Motor, Blauwe Auto, Oranje Auto '),
	(1493, 'I', 1, '240b.jpg', 'Wat is hier de juiste volgorde?', 'De fietser moet op de auto wachten, de motor moet dit ook doen omdat deze van links komt. De fietser komt van rechts voor de motor dus die gaat daarna en als laatste blijft de motor over.', 'Auto, Fietser, Motor', 'Auto &amp; Fietser, Motor', 'Motor, Fietser Auto'),
	(1494, 'I', 1, '248b.jpg', 'Wat is hier de juiste volgorde?', '', 'Oranje Auto, Fietser, Blauwe Auto', 'Blauwe Auto, Oranje Auto, Fietser', 'Fietser, Oranje Auto, Blauwe Auto'),
	(1495, 'I', 1, '248d.jpg', 'Wat is hier de juiste volgorde?', 'Het is een gelijkwaardig kruispunt dus de standaard voorrangsregels zijn van toepassing.', 'Voetganger, Oranje Auto, Fietser, Blauwe Auto', 'Blauwe Auto, Oranje Auto, Fietser, Voetganger', 'Voetganger, Fietser, Oranje Auto, Blauwe Auto'),
	(1496, 'I', 1, '249b.jpg', 'Wie mag hier als eerste?', 'De paarse fietser heeft geen verkeer van rechts, de andere twee wel dus daarom gaat hij als eerste.', 'Paarse Fietser', 'Groene Fietser', 'Auto'),
	(1497, 'I', 1, '250b.jpg', 'Wie mag hier als eerste?', 'De blauwe auto rijdt op een voorrangsweg en heeft hierdoor voorrang. Bovendien heeft de oranje auto ook haaientanden.', 'Blauwe Auto', 'Oranje Auto', ''),
	(1501, 'I', 1, '270b.jpg', 'De politie heeft geen sirene of lichten aan. Wat is hier de juiste volgorde?', 'De politieauto gaat rechtdoor en heeft geen verkeer dat van rechts komt dus deze gaat eerst. Hierna gaat de blauwe omdat deze geen hierna geen verkeer van rechts heeft. De oranje gaat als laatste.', 'Politie, Blauwe Auto, Oranje Auto', 'Blauwe Auto, Politie, Oranje Auto', 'Blauwe Auto, Oranje Auto, Politie'),
	(1502, 'I', 1, '296.jpg', 'De douane auto heeft geen sirene of lichten aan. Wie gaat hier als eerste?', '', 'De Douane auto', 'De zwarte auto. ', ''),
	(1503, 'I', 1, '297.jpg', 'Wie mag hier als eerste?', '', 'De Scooter', 'De auto ', ''),
	(1504, 'I', 1, '298.jpg', 'De voetganger loopt naast een fiets, wie mag hier als eerste?', 'De voetganger mag eerst omdat zij voor een zebrapad staat. ', 'De voetganger', 'De auto', ''),
	(1539, 'E', 2, '030.jpg', 'Moet je in nieuwere touringcars een gordel dragen als die aanwezig zijn?\r\n', 'Als de gordels aanwezig zijn, ben je verplicht die te dragen', 'JA', 'NEE', ''),
	(1636, 'A', 1, '171.jpg', 'Iemand loopt met een paard op het fietspad. Hoe wordt deze persoon gezien volgens de verkeerswet?', 'Een persoon die naast een paard loopt, moet deze alsnog besturen. Daarom is deze persoon een bestuurder. ', 'Een bestuurder.', 'Een voetganger. ', 'Een speciale weggebruiker'),
	(1952, 'I', 6, '238b.jpg', 'Wat is de juiste volgorde?', 'Dit is een gelijkwaardig kruispunt. De groene motor komt van rechts voor de blauwe auto, de blauwe auto komt van rechts voor de fietser en de fietser mag dus pas als laatste.', '69,30', '84,67', '17,72'),
	(1953, 'I', 6, '240b.jpg', 'Wat is de juiste volgorde?', 'De motor moet wachten op de blauwe auto en de fietser die van rechts komen. De fietser moet wachten op de blauwe auto omdat deze rechtdoor gaat.', '26,61', '53,59', '24,29'),
	(1954, 'I', 6, '248b.jpg', 'Wat is de juiste volgorde?', 'De blauwe auto moet wachten op de groene fietser die van rechts komt. De fietser moet weer wachten op oranje auto die de kleine bocht heeft.', '26,55', '58,34', '68,58'),
	(1955, 'I', 6, '250b.jpg', 'Wat is de juiste volgorde?', 'De blauwe auto rijdt op een voorrangsweg die meegaat met de bocht. De oranje auto moet achter de haaientanden wachten.', '70,62', '42,27', ''),
	(1956, 'I', 6, '249b.jpg', 'Wat is de juiste volgorde?', 'De blauwe auto moet wachten op de groene fietser die van rechts komt. De groene fietser moet weer wachten op de paarse fietser die voor hem van rechts komt.', '39,34', '67,35', '63,63'),
	(1957, 'I', 6, 'mtr_005.png', 'Wat is de juiste volgorde?', 'De voetganger steekt over waar geen oversteekplaats is en moet dus wachten op het andere verkeer.', '38,65', '90,5', ''),
	(1958, 'I', 6, 'mtr_015.png', 'Wat is de juiste volgorde?', 'De tram is al voorbij de haaientanden en moet nu het kruispunt oversteken voordat de motor mag rijden.', '61,37', '53,63', ''),
	(1959, 'I', 6, '214.jpg', 'Wat is de juiste volgorde?', 'De witte auto komt van een onverharde weg en moet op het andere verkeer wachten om de verharde weg te betreden.', '93,48', '50,51', ''),
	(1960, 'I', 6, '297.png', 'Wat is de juiste volgorde?', 'Rechtdoor op dezelfde weg gaat voor, daarom mag de auto eerst in deze situatie.', '60,61', '57,33', ''),
	(1961, 'I', 6, 'sleep331.jpg', 'Wat is de juiste volgorde?', 'Een tram heeft alleen voorrang op een gelijkwaardig kruispunt en moet zich hier dus houden aan de haaientanden.', '31,69', '59,49', ''),
	(1962, 'I', 6, '332.jpg', 'Wat is de juiste volgorde?', 'Op een gelijkwaardig kruispunt mag een tram altijd eerst, ook al komt er verkeer van rechts.', '63,50', '48,33', '');
